import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CSSTransition } from 'react-transition-group';
import './MainMenu.css';
import logoImage from './components/logo.png';

export default function MainMenu() {
  const navigate = useNavigate();
  const [theme, setTheme] = useState('dark');
  const [inProp, setInProp] = useState(false);

  useEffect(() => {
    setInProp(true);
  }, []);

  const toggleTheme = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  // ripple effect helper
  const handleRipple = e => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width = circle.style.height = `${d}px`;
    circle.style.left  = `${e.clientX - btn.offsetLeft - r}px`;
    circle.style.top   = `${e.clientY - btn.offsetTop  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);
  };

  const buttons = [
    { to: '/surveys',  label: '–û–ø—Ä–æ—Å—ã'   },
    { to: '/profile',  label: '–ü—Ä–æ—Ñ–∏–ª—å'   },
    { to: '/employee', label: '–°–æ—Ç—Ä—É–¥–Ω–∏–∫—É'},
  ];

  return (
    <div className={`mainmenu-container ${theme}`}>
      <div className="mainmenu-overlay"/>

      {/* theme toggle */}
      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'dark' ? 'üåû' : 'üåô'}
      </button>

      {/* logo */}
      <CSSTransition in={inProp} timeout={800} classNames="logo-fade" appear>
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" className="mainmenu-logo"/>
      </CSSTransition>

      {/* buttons */}
      <div className="mainmenu-buttons">
        {buttons.map(({ to, label }) => (
          <CSSTransition key={to} in={inProp} timeout={600} classNames="btn-fade" appear>
            <button
              className="mainmenu-btn"
              onClick={e => { handleRipple(e); navigate(to); }}
            >
              {/* —É–¥–∞–ª–∏–ª–∏ –∏–∫–æ–Ω–∫—É, –æ—Å—Ç–∞–≤–∏–ª–∏ —Ç–æ–ª—å–∫–æ —Ç–µ–∫—Å—Ç */}
              <span>{label}</span>
            </button>
          </CSSTransition>
        ))}
      </div>
    </div>
  );
}
