import React, { useEffect, useState } from 'react';
import './App.css';
import { useNavigate } from 'react-router-dom';  // Для перехода между страницами

const App = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Логика для получения данных о пользователе
    const getUserData = () => {
      if (window.Telegram && window.Telegram.WebApp) {
        const user = window.Telegram.WebApp.initDataUnsafe;
        if (user && user.first_name) {
          setUserName(user.first_name);
        } else {
          setUserName('Гость');
        }
      } else {
        setUserName('Гость');
      }
    };

    setTimeout(() => {
      setIsLoaded(true);
    }, 1500);

    getUserData();
  }, []);

  // Переход на страницу регистрации после 4 секунд
  useEffect(() => {
    setTimeout(() => {
      navigate('/register');
    }, 4000);
  }, [navigate]);

  return (
    <div className="App">
      <img src="/logo.png" alt="Логотип" className="logo" />
      <h1 className={`welcome-text ${isLoaded ? 'move-up' : ''}`}>
        Добро пожаловать, {userName}
      </h1>
    </div>
  );
};

export default App;
