*/_jsxs("div",{className:"form-group",children:[/*#__PURE__*/_jsx("label",{className:"form-label",children:"\u0415\u0441\u0442\u044C \u043B\u0438 \u0440\u043E\u0434\u0441\u0442\u0432\u0435\u043D\u043D\u0438\u043A\u0438, \u0442\u0440\u0435\u0431\u0443\u044E\u0449\u0438\u0435 \u043E\u0441\u043E\u0431\u043E\u0433\u043E \u0443\u0445\u043E\u0434\u0430?"}),/*#__PURE__*/_jsxs("div",{className:"insurance-options",children:[/*#__PURE__*/_jsx("button",{className:`insurance-btn ${specialCareRelatives==='yes'?'selected':''}`,onClick:()=>setSpecialCareRelatives('yes'),children:"\u0414\u0430"}),/*#__PURE__*/_jsx("button",{className:`insurance-btn ${specialCareRelatives==='no'?'selected':''}`,onClick:()=>setSpecialCareRelatives('no'),children:"\u041D\u0435\u0442"})]})]})]});}}return null;};return/*#__PURE__*/_jsxs("div",{className:"justincase-container",style:{backgroundImage:`url(${backgroundImage})`},children:[currentStep!==4&&/*#__PURE__*/_jsx("div",{className:"logo-container",children:/*#__PURE__*/_jsx("img",{src:logo,alt:"Logo",className:"logo-image"})}),currentStep!==4&&/*#__PURE__*/_jsx("h1",{className:"justincase-title",children:"\u0420\u0430\u0441\u0447\u0435\u0442 \u043F\u043E \u043F\u0440\u043E\u0433\u0440\u0430\u043C\u043C\u0435 \"\u041D\u0430 \u0432\u0441\u044F\u043A\u0438\u0439 \u0441\u043B\u0443\u0447\u0430\u0439\" \u0434\u043B\u044F \u0441\u043E\u0442\u0440\u0443\u0434\u043D\u0438\u043A\u0430"}),/*#__PURE__*/_jsx("div",{className:"justincase-form",children:renderStep()}),currentStep>1&&currentStep<4&&/*#__PURE__*/_jsx("div",{className:"back-btn-container",children:/*#__PURE__*/_jsx("button",{className:"back-btn",onClick:handlePrev,children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"none",children:/*#__PURE__*/_jsx("path",{d:"M12 20l-8-8 8-8"})})})}),currentStep<3&&/*#__PURE__*/_jsx("div",{className:"top-btn-container",children:/*#__PURE__*/_jsx("button",{className:"top-btn",onClick:handleNext,disabled:!canGoNext(),children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"none",children:/*#__PURE__*/_jsx("path",{d:"M12 4l8 8-8 8"})})})}),currentStep===3&&/*#__PURE__*/_jsx("div",{className:"top-btn-container",children:/*#__PURE__*/_jsx("button",{className:"top-btn",onClick:handleNext,disabled:!canGoNext(),children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"none",children:/*#__PURE__*/_jsx("path",{d:"M5 13l4 4L19 7"})})})}),currentStep===4&&/*#__PURE__*/_jsxs(_Fragment,{children:[/*#__PURE__*/_jsx("div",{className:"back-btn-container",children:/*#__PURE__*/_jsx("button",{className:"back-btn",onClick:goToMenu,children:"\u041C\u0435\u043D\u044E"})}),/*#__PURE__*/_jsx("div",{className:"top-btn-container",children:/*#__PURE__*/_jsx("button",{className:"top-btn",onClick:repeatCalculation,children:/*#__PURE__*/_jsx("svg",{xmlns:"http://www.w3.org/2000/svg",viewBox:"0 0 24 24",fill:"currentColor",children:/*#__PURE__*/_jsx("path",{d:"M17.65 6.35A7.95 7.95 0 0012 4a8 8 0 00-8 8h2.08a5.92 5.92 0 015.92-6 6 6 0 015.65 3.65L13 11h7V4l-2.35 2.35zM6.35 17.65A7.95 7.95 0 0012 20a8 8 0 008-8h-2.08a5.92 5.92 0 01-5.92 6 6 6 0 01-5.65-3.65L3.41 16.3 2 17.71l2.35-2.35z"})})})})]})]});};export default JustincasePage;

