import React, { useState, useEffect } from 'react';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import logo from './components/logo.png';
import './JustincasePage.css';

const JustincasePage = () => {
  // 1) –û–±—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  // 2) –°–æ–±–∏—Ä–∞–µ–º—ã–µ –¥–∞–Ω–Ω—ã–µ
  const [birthDate, setBirthDate] = useState(null);         // —Ñ–æ—Ä–º–∞—Ç "dd/mm/yyyy"
  const [gender, setGender] = useState(null);               // 'male' / 'female'
  const [insuranceInfo, setInsuranceInfo] = useState(null);   // 'yes' / 'no'
  const [insuranceTerm, setInsuranceTerm] = useState('1');    // —Å—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–ª–µ—Ç)
  const [insuranceSum, setInsuranceSum] = useState('');       // —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–Ω–∞—è —Å—É–º–º–∞
  const [insuranceFrequency, setInsuranceFrequency] = useState(''); // –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã

  // –í–µ—Ç–∫–∞ "–î–∞" ‚Äì –ø–∞–∫–µ—Ç—ã
  const [accidentPackage, setAccidentPackage] = useState(null); // 'yes' / 'no'
  const [criticalPackage, setCriticalPackage] = useState(null); // 'yes' / 'no'
  const [treatmentRegion, setTreatmentRegion] = useState(null);   // 'russia' / 'abroad'
  const [sportPackage, setSportPackage] = useState(null);         // 'yes' / 'no'

  // –í–µ—Ç–∫–∞ "–ù–µ—Ç" ‚Äì –≤–æ–ø—Ä–æ—Å—ã –æ —Ä–∞–±–æ—Ç–µ –∏ –¥–æ—Ö–æ–¥–∞—Ö
  const [hasJob, setHasJob] = useState(null);
  const [income2021, setIncome2021] = useState('');
  const [income2022, setIncome2022] = useState('');
  const [income2023, setIncome2023] = useState('');
  const [scholarship, setScholarship] = useState('');
  const [unsecuredLoans, setUnsecuredLoans] = useState('');

  // –ù–æ–≤—ã–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ–≥–æ —à–∞–≥–∞ (–ø—Ä–∏ insuranceInfo === 'no')
  const [breadwinnerStatus, setBreadwinnerStatus] = useState(null); // "yes", "no", "notMe"
  const [incomeShare, setIncomeShare] = useState('');
  const [childrenCount, setChildrenCount] = useState('0');
  const [specialCareRelatives, setSpecialCareRelatives] = useState(null); // 'yes' / 'no'

  // 3) –®–∞–≥–∏:
  // –®–∞–≥ 1 ‚Äì –¥–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è, –ø–æ–ª, –∑–Ω–∞–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ —Å—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –ø–æ–ª–∏—Å–∞.
  // –®–∞–≥ 2 ‚Äì –µ—Å–ª–∏ insuranceInfo === 'yes': –¥–µ—Ç–∞–ª–∏ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è, –∏–Ω–∞—á–µ: –≤–æ–ø—Ä–æ—Å—ã –æ —Ä–∞–±–æ—Ç–µ –∏ –¥–æ—Ö–æ–¥–∞—Ö.
  // –®–∞–≥ 3 ‚Äì –µ—Å–ª–∏ insuranceInfo === 'yes': –≤–æ–ø—Ä–æ—Å—ã –ø–æ –ø–∞–∫–µ—Ç–∞–º, –∏–Ω–∞—á–µ: –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –≤–æ–ø—Ä–æ—Å—ã –ø—Ä–æ —Å–µ–º–µ–π–Ω—ã–π –±—é–¥–∂–µ—Ç.
  // –®–∞–≥ 4 ‚Äì —Ä–µ–∑—É–ª—å—Ç–∞—Ç —Ä–∞—Å—á—ë—Ç–∞ (–ø–æ—Å–ª–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ Excel)
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  // –ù–æ–≤—ã–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –ø—Ä–æ—Ü–µ—Å—Å–∞ —Ä–∞—Å—á—ë—Ç–∞
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // ---------- –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ ----------
  const parseDate = (dateStr) => {
    const [day, month, year] = dateStr.split('/');
    return new Date(+year, +month - 1, +day);
  };

  const calculateAge = (dateObj) => {
    const today = new Date();
    let age = today.getFullYear() - dateObj.getFullYear();
    const m = today.getMonth() - dateObj.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < dateObj.getDate())) {
      age--;
    }
    return age;
  };

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å—É–º–º—ã (–Ω–∞–ø—Ä–∏–º–µ—Ä "1.000.000")
  const formatSum = (rawValue) => {
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    result = digits + result;
    return result;
  };

  const handleSumChange = (e) => {
    setInsuranceSum(formatSum(e.target.value));
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –¥–ª—è –¥–µ—Ç–∞–ª–µ–π —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è (–≤–µ—Ç–∫–∞ "–î–∞")
  const handleTermChange = (e) => {
    setInsuranceTerm(e.target.value);
  };
  const handleFrequencyChange = (e) => {
    setInsuranceFrequency(e.target.value);
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –¥–ª—è –≤–µ—Ç–∫–∏ "–ù–µ—Ç" (–¥–æ—Ö–æ–¥—ã)
  const handleIncome2021Change = (e) => {
    setIncome2021(formatSum(e.target.value));
  };
  const handleIncome2022Change = (e) => {
    setIncome2022(formatSum(e.target.value));
  };
  const handleIncome2023Change = (e) => {
    setIncome2023(formatSum(e.target.value));
  };
  const handleScholarshipChange = (e) => {
    setScholarship(formatSum(e.target.value));
  };
  const handleUnsecuredLoansChange = (e) => {
    setUnsecuredLoans(formatSum(e.target.value));
  };

  // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –∏–∑–º–µ–Ω–µ–Ω–∏—è treatmentRegion –¥–ª—è –æ—Ç–ª–∞–¥–∫–∏
  useEffect(() => {
    console.log("treatmentRegion –æ–±–Ω–æ–≤–ª—ë–Ω:", treatmentRegion);
  }, [treatmentRegion]);

  // –ó–¥–µ—Å—å –Ω–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –∑–Ω–∞—á–µ–Ω–∏–µ –¥–ª—è treatmentRegion,
  // —á—Ç–æ–±—ã –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –Ω–∏ –æ–¥–Ω–∞ –∏–∑ –∫–Ω–æ–ø–æ–∫ –Ω–µ –±—ã–ª–∞ –≤—ã–±—Ä–∞–Ω–∞.

  // ---------- –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏, –º–æ–∂–Ω–æ –ª–∏ –ø–µ—Ä–µ–π—Ç–∏ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É —à–∞–≥—É ----------
  const canGoNext = () => {
    if (currentStep === 1) {
      return birthDate && gender && insuranceInfo;
    } else if (currentStep === 2) {
      if (insuranceInfo === 'yes') {
        return insuranceTerm && insuranceSum && insuranceFrequency;
      } else {
        return hasJob && income2021 && income2022 && income2023;
      }
    } else if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        return accidentPackage && criticalPackage && (criticalPackage === 'no' || treatmentRegion) && sportPackage;
      } else {
        return breadwinnerStatus &&
               (breadwinnerStatus === 'yes' || incomeShare) &&
               childrenCount !== '' &&
               specialCareRelatives;
      }
    } else {
      return true;
    }
  };

  // ---------- –ü–µ—Ä–µ—Ö–æ–¥—ã –º–µ–∂–¥—É —à–∞–≥–∞–º–∏ ----------
  const handleNext = () => {
    if (!canGoNext()) {
      alert('–ù–µ –≤—ã–ø–æ–ª–Ω–µ–Ω—ã —É—Å–ª–æ–≤–∏—è –¥–ª—è –ø–µ—Ä–µ—Ö–æ–¥–∞ –∫ —Å–ª–µ–¥—É—é—â–µ–º—É —à–∞–≥—É');
      return;
    }
    if (currentStep === 1) {
      setCurrentStep(2);
    } else if (currentStep === 2) {
      setCurrentStep(3);
    } else if (currentStep === 3) {
      // –ó–¥–µ—Å—å –Ω–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ treatmentRegion ‚Äì –∂–¥—ë–º –≤—ã–±–æ—Ä–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      setIsProcessing(true);
      // –§–æ—Ä–º–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞
      const calculatorData = {
        birthDate,
        gender,
        insuranceTerm,
        insuranceSum,
        insuranceFrequency,
        accidentPackage,
        criticalPackage,
        treatmentRegion,
        sportPackage,
        // –¥–ª—è –≤–µ—Ç–∫–∏ "–ù–µ—Ç"
        hasJob,
        income2021,
        income2022,
        income2023,
        scholarship,
        unsecuredLoans,
        breadwinnerStatus,
        incomeShare,
        childrenCount,
        specialCareRelatives
      };
      console.log("–û—Ç–ø—Ä–∞–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞:", calculatorData);
      // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å —á–µ—Ä–µ–∑ –ø—Ä–æ–∫—Å–∏
      fetch('/api/proxy/calculator/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(calculatorData),
        credentials: 'include'
      })
      .then(response => response.json())
      .then(data => {
        setResultData(data);
        setIsProcessing(false);
        setCurrentStep(4); // –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —à–∞–≥—É —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
      })
      .catch(err => {
        console.error("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞:", err);
        setIsProcessing(false);
        alert("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞");
      });
    }
  };

  const handlePrev = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∫–Ω–æ–ø–∫–∏ "–ú–µ–Ω—é" ‚Äì –ø–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É –º–µ–Ω—é
  const goToMenu = () => {
    // –ï—Å–ª–∏ –≤—ã –∏—Å–ø–æ–ª—å–∑—É–µ—Ç–µ React Router, –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å history.push('/menu')
