import React, { useState, useEffect } from 'react';
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';
import { useNavigate } from 'react-router-dom';

const SNPPage = () => {
  const [userName, setUserName] = useState('');
  const [birthDate, setBirthDate] = useState(null);
  const [gender, setGender] = useState(null);
  const [calcType, setCalcType] = useState(null);
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');
  const [showResult, setShowResult] = useState(false);
  const [calculationResult, setCalculationResult] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || '–ì–æ—Å—Ç—å');
  }, []);

  const formatSum = (raw) => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = (e) => {
    const formatted = formatSum(e.target.value);
    setAmount(formatted);

    const numeric = parseInt(formatted.replace(/\./g, '')) || 0;

    if (calcType === 'premium') {
      setAmountError(numeric < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60.000 —Ä—É–±–ª–µ–π' : '');
    }

    if (calcType === 'sum') {
      setAmountError(numeric < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450.000 —Ä—É–±–ª–µ–π' : '');
    }
  };

  const canGoNext = () => {
    const birthDateObj =
      typeof birthDate === 'string'
        ? new Date(birthDate.split('.').reverse().join('-'))
        : birthDate;

    if (!birthDateObj || !(birthDateObj instanceof Date) || isNaN(birthDateObj)) return false;

    const today = new Date();
    let userAge = today.getFullYear() - birthDateObj.getFullYear();
    if (
      today.getMonth() < birthDateObj.getMonth() ||
      (today.getMonth() === birthDateObj.getMonth() && today.getDate() < birthDateObj.getDate())
    ) {
      userAge--;
    }

    return birthDate && gender && calcType && amount && isAmountValid() && userAge >= 18;
  };

  const isAmountValid = () => {
    const numeric = parseInt(amount.replace(/\./g, '')) || 0;
    return calcType === 'premium'
      ? numeric >= 60000
      : numeric >= 450000;
  };

  const handleNext = async () => {
    if (!canGoNext()) {
      alert('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è');
      return;
    }

    const birthDateObj =
      typeof birthDate === 'string'
        ? new Date(birthDate.split('.').reverse().join('-'))
        : birthDate;

    const d = birthDateObj.getDate().toString().padStart(2, '0');
    const m = (birthDateObj.getMonth() + 1).toString().padStart(2, '0');
    const y = birthDateObj.getFullYear().toString();
    const birthdate = `${d}.${m}.${y}`;

    const today = new Date();
    let age = today.getFullYear() - birthDateObj.getFullYear();
    if (
      today.getMonth() < birthDateObj.getMonth() ||
      (today.getMonth() === birthDateObj.getMonth() && today.getDate() < birthDateObj.getDate())
    ) {
      age--;
    }

    const sumClean = parseInt(amount.replace(/\./g, ''), 10);

    const payload = {
      birthdate,
      age,
      gender,
      payment: calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: sumClean
    };

    try {
      const response = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const result = await response.json();

      if (!response.ok) {
        alert("–û—à–∏–±–∫–∞ —Ä–∞—Å—á—ë—Ç–∞: " + (result.error || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞"));
        return;
      }

      setShowResult(true);
      setCalculationResult(result);

    } catch (err) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–∏ —Å —Å–µ—Ä–≤–µ—Ä–æ–º:", err);
      alert("–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è —Å —Å–µ—Ä–≤–µ—Ä–æ–º");
    }
  };

  const goToMenu = () => {
    navigate('/Main-Menu');
  };

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image" />
      </div>

      <h1 className="snp-title">
        –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"
      </h1>

      <div className="snp-form">
        <div className="form-group">
          <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
          <DateWheelPicker onChange={setBirthDate} />
        </div>

        <div className="form-group">
          <label className="form-label">–ü–æ–ª:</label>
          <div className="gender-selector">
            {['male', 'female'].map((g) => (
              <div
                key={g}
                className={`gender-option ${gender === g ? 'selected' : ''}`}
                onClick={() => setGender(g)}
              >
                <div className="gender-label">{g === 'male' ? '–ú—É–∂—Å–∫–æ–π' : '–ñ–µ–Ω—Å–∫–∏–π'}</div>
                <div className="gender-icon">{g === 'male' ? 'üë®' : 'üë©'}</div>
              </div>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
            </button>
          </div>
        </div>

        {calcType && (
          <div className="form-group">
            <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={amount}
              onChange={handleAmountChange}
              placeholder={calcType === 'premium' ? '–ú–∏–Ω–∏–º—É–º 60.000' : '–ú–∏–Ω–∏–º—É–º 450.000'}
            />
            {amountError && <div className="input-error-message">{amountError}</div>}
          </div>
        )}
      </div>

      {showResult && calculationResult && (
        <div className="result-container">
          <div className="result-block">
            <p><strong>–ü–æ–ª:</strong> {calculationResult.gender}</p>
            <p><strong>–í–æ–∑—Ä–∞—Å—Ç:</strong> {calculationResult.age}</p>
            <p><strong>–¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞:</strong> {calculationResult.payment}</p>
            <p><strong>–ë–∞–∑–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {calculationResult.basePremium}</p>
            <p><strong>–ò—Ç–æ–≥–æ–≤–∞—è –ø—Ä–µ–º–∏—è:</strong> {calculationResult.totalPremium}</p>
          </div>
          <div className="animated-arrow">üëâ</div>
        </div>
      )}

      <div className="back-btn-container">
        <button className="back-btn mainmenu-btn" onClick={goToMenu}>
          <img src={mainmenuIcon} alt="Menu" />
        </button>
      </div>
      <div className="top-btn-container">
        <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
          <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8" /></svg>
        </button>
      </div>
    </div>
  );
};

export default SNPPage;
