  // Состояния для опроса
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [shuffledAnswers, setShuffledAnswers] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);

  // Функция перемешивания ответов
  const shuffleAnswers = (answers) => {
    return answers.sort(() => Math.random() - 0.5);
  };

  // Обработчик выбора ответа
  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
    const updatedAnswers = [...userAnswers];
    updatedAnswers[currentQuestion] = answer;
    setUserAnswers(updatedAnswers);
  };

  // Функция для показа сообщения об ошибке на 4 секунды
  const showErrorMessage = () => {
    setErrorMessage("Выбери 1 из 3 вариантов, чтобы продолжить");
    setTimeout(() => {
      setErrorMessage('');
    }, 4000);
  };

  // Переход к следующему вопросу
  const handleNext = () => {
    if (!selectedAnswer) {
      showErrorMessage();
      return;
    }
    setSelectedAnswer('');
    setCurrentQuestion(currentQuestion + 1);
  };

  // === Функция отправки данных на сервер Flask ===
  const sendDataToServer = async (data) => {
    try {
      // Укажите правильный адрес вашего Flask-сервера, например http://localhost:5000
      const response = await fetch('http://localhost:5000/api/assessment/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        throw new Error(`Ошибка при сохранении: ${response.statusText}`);
      }
      console.log('Данные успешно сохранены в Excel (Flask)');
    } catch (error) {
      console.error('Ошибка при отправке данных на Flask:', error);
      alert('Не удалось сохранить результаты. Попробуйте ещё раз.');
    }
  };

  // Завершение опроса: вывод всех выбранных ответов и отправка на сервер
  const handleFinish = async () => {
    if (!selectedAnswer) {
      showErrorMessage();
      return;
    }

    // Формируем дату в нужном формате (DD.MM.YYYY HH:MM)
    const now = new Date();
    const day = String(now.getDate()).padStart(2, '0');
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const year = now.getFullYear();
    const hours = String(now.getHours()).padStart(2, '0');
    const minutes = String(now.getMinutes()).padStart(2, '0');
    const formattedDate = `${day}.${month}.${year} ${hours}:${minutes}`;

    // Отправляем на сервер объект с данными
    const dataToSave = {
      fullName,           // Полное ФИО
      dateTime: formattedDate,
      answers: userAnswers // Массив из 25 ответов
    };

    await sendDataToServer(dataToSave);

    alert(
      `Опрос завершён, ${fullName}!\nВаши ответы:\n` +
      userAnswers
        .map((ans, index) => `Вопрос ${index + 1}: ${ans || 'Не выбран'}`)
        .join('\n')
    );
    navigate('/');
  };

  // При каждом изменении currentQuestion перемешиваем варианты ответов
  useEffect(() => {
    if (questions[currentQuestion]) {
      const shuffled = shuffleAnswers([...questions[currentQuestion].answers]);
      setShuffledAnswers(shuffled);
    }
  }, [currentQuestion]);

  // Если опрос еще не начат, показываем форму для ввода ФИО
  if (!isStarted) {
    return (
      <div className="assessment-container">
        <h2>Введите ваши ФИО</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const full = `${surname} ${firstName} ${patronymic}`;
            setFullName(full);
            setIsStarted(true);
          }}
          className="name-form"
        >
          <div>
            <label>Фамилия: </label>
            <input
              type="text"
              value={surname}
              onChange={(e) => setSurname(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Имя: </label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Отчество: </label>
            <input
              type="text"
              value={patronymic}
              onChange={(e) => setPatronymic(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn red-btn">Начать опрос</button>
        </form>
      </div>
    );
  }

  return (
    <div className="assessment-container">
      <h2>{questions[currentQuestion]?.question}</h2>
      {shuffledAnswers.map((answer, index) => (
        <button
          key={index}
          className={`answer-btn ${selectedAnswer === answer ? 'selected' : ''}`}
          onClick={() => handleAnswerSelect(answer)}
        >
          {answer}
        </button>
      ))}
      <div>
        {currentQuestion === questions.length - 1 ? (
          <button className="btn red-btn" onClick={handleFinish}>
            Завершить опрос
          </button>
        ) : (
          <button className="btn red-btn" onClick={handleNext}>
            Далее
          </button>
        )}
      </div>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
    </div>
  );
};

