import React, { useState } from 'react';
import axios from 'axios';
import logo from './components/logo.png';
import './RegisterPage.css';  // Импортируем стили
import { useNavigate } from 'react-router-dom';

const RegisterPage = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleRegister = async () => {
    setStatus('Проверка email...');

    try {
      const response = await fetch('http://127.0.0.1:5000/check_email', {
        method: 'POST',
        body: JSON.stringify({ email }),
        headers: { 'Content-Type': 'application/json' },
      });

      const result = await response.json();

      if (result.isValid) {
        setStatus('Почта проверена, отправляем код...');

        // Отправка кода на почту
        await fetch('http://127.0.0.1:5000/send_code', {
          method: 'POST',
          body: JSON.stringify({ email }),
          headers: { 'Content-Type': 'application/json' },
        });

        // ✅ Переход на страницу подтверждения кода (НЕ на опросы!)
        navigate('/confirm-code', { state: { email } });
      } else {
        setStatus('Этот email не зарегистрирован в системе.');
      }
    } catch (error) {
      setStatus('Ошибка при проверке email. Попробуйте снова.');
      console.error(error);
    }
  };

  // ✅ Только эта кнопка ведёт на опросы
  const handleGuestLogin = () => {
    navigate('/polls'); // Переход на страницу с опросами
  };

  return (
    <div className="register-container">
      <div className="logo"/>
      <h2>Введите корпоративную почту</h2>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Введите email"
        className="input-field"
      />
      <button className="btn red-btn" onClick={handleRegister}>Зарегистрироваться</button>
      <button className="btn gray-btn" onClick={handleGuestLogin}>Войти как гость</button>
      <p>{status}</p>
    </div>
  );
};

export default RegisterPage;
