import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // —à–∞–ø–∫–∞, –∫–Ω–æ–ø–∫–∏, –∞–Ω–∏–º–∞—Ü–∏–∏
import './SNPPage.css';    // –≤–∞—à–∏ —Å—Ç–∏–ª–∏

const SNPPage = () => {
  const navigate = useNavigate();

  // === —Å–æ—Å—Ç–æ—è–Ω–∏—è ===
  const [step, setStep]           = useState(1);
  const [email, setEmail]         = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate] = useState(new Date());
  const [gender, setGender]       = useState(null); // 'male' –∏–ª–∏ 'female'
  const [calcType, setCalcType]   = useState(null); // 'premium' –∏–ª–∏ 'sum'
  const [amount, setAmount]       = useState('');
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Å—É–º–º—ã 123456 ‚Üí "123.456"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // –ø–æ–¥—Å—á—ë—Ç –≤–æ–∑—Ä–∞—Å—Ç–∞
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) age--;
    return age;
  };

  // –ø—Ä–æ–≤–µ—Ä–∫–∞ —Å—É–º–º—ã
  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && num >= 60000)
        || (calcType === 'sum'     && num >= 450000);
  };

  // –º–æ–∂–Ω–æ –ª–∏ –∞–∫—Ç–∏–≤–∏—Ä–æ–≤–∞—Ç—å –∫–Ω–æ–ø–∫—É "–î–∞–ª–µ–µ"
  const canGoNext = () => (
    birthDate instanceof Date &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amount) &&
    isAmountValid()
  );

  // === –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ ===
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('–ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—É—é –ø–æ—á—Ç—É –¥–æ–º–µ–Ω–∞ @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  const handleAmountChange = e => {
    const f = formatSum(e.target.value);
    setAmount(f);
    const num = parseInt(f.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium' && num < 60000) {
      setAmountError('–ú–∏–Ω–∏–º—É–º 60 000 ‚ÇΩ');
    } else if (calcType === 'sum' && num < 450000) {
      setAmountError('–ú–∏–Ω–∏–º—É–º 450 000 ‚ÇΩ');
    } else {
      setAmountError('');
    }
  };

  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium'
                    ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞'
                    : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum:        parseInt(amount.replace(/\./g, ''), 10)
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);
      if (res.ok) setResultData(data);
      else console.error('–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞:', data);
    } catch (err) {
      setIsProcessing(false);
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', err);
    }
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* —à–∞–ø–∫–∞ */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" />
        </div>
      </div>

      {/* ‚Äî‚Äî‚Äî –®–∞–≥ 1: –ø–æ—á—Ç–∞ ‚Äî‚Äî‚Äî */}
      {step === 1 && !isProcessing && !resultData && (
        <>
          <div className="email-step">
            <label className="email-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}

          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              –ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å
            </button>
          </div>
        </>
      )}

      {/* ‚Äî‚Äî‚Äî –®–∞–≥ 2: —Ä–∞—Å—á—ë—Ç ‚Äî‚Äî‚Äî */}
      {step === 2 && !isProcessing && !resultData && (
        <>
          <h1 className="polls-title animate-title">
            –†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ ¬´–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç¬ª
          </h1>

          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="date-section animate-btn btn1">
            <label className="section-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                // val –º–æ–∂–µ—Ç –±—ã—Ç—å Date –∏–ª–∏ { day, month, year }
                if (val instanceof Date) {
                  setBirthDate(val);
                } else if (val && val.day) {
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
                }
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="gender-section animate-btn btn2">
            <button
              className={`insurance-btn ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => setGender('male')}
            >
              üë® –ú—É–∂—Å–∫–æ–π
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender('female')}
            >
              üë© –ñ–µ–Ω—Å–∫–∏–π
            </button>
          </div>

          {/* –¢–∏–ø —Ä–∞—Å—á—ë—Ç–∞ */}
          <div className="insurance-options animate-btn btn3">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
            </button>
          </div>

          {/* –°—É–º–º–∞ */}
          {calcType && (
            <div className="sum-section animate-btn btn4">
              <label className="section-label">–°—É–º–º–∞ (‚ÇΩ):</label>
              <input
                type="text"
                placeholder={
                  calcType === 'premium'
                    ? '–ú–∏–Ω–∏–º—É–º 60 000'
                    : '–ú–∏–Ω–∏–º—É–º 450 000'
                }
                value={amount}
                onChange={handleAmountChange}
              />
              {amountError && <div className="input-error">{amountError}</div>}
            </div>
          )}

          {/* –ö–Ω–æ–ø–∫–∞ "–î–∞–ª–µ–µ ‚Üí" */}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn5"
              onClick={handleNext}
              disabled={!canGoNext()}
            >
              –î–∞–ª–µ–µ <span className="arrow-icon">‚Üí</span>
            </button>
          </div>
        </>
      )}

      {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç‚Ä¶</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}
      {resultData && (
        <div className="result-container">
          <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
          {/* ... */}
        </div>
      )}
    </div>
  );
};

export default SNPPage;
