import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';  // шапка, кнопки, анимации
import './SNPPage.css';    // ваши стили

const SNPPage = () => {
  const navigate = useNavigate();

  // === состояния ===
  const [step, setStep]           = useState(1);
  const [email, setEmail]         = useState('');
  const [emailError, setEmailError] = useState('');
  const [birthDate, setBirthDate] = useState(new Date());
  const [gender, setGender]       = useState(null); // 'male' или 'female'
  const [calcType, setCalcType]   = useState(null); // 'premium' или 'sum'
  const [amount, setAmount]       = useState('');
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // форматирование суммы 123456 → "123.456"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // подсчёт возраста
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    if (
      today.getMonth() < date.getMonth() ||
      (today.getMonth() === date.getMonth() && today.getDate() < date.getDate())
    ) age--;
    return age;
  };

  // проверка суммы
  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && num >= 60000)
        || (calcType === 'sum'     && num >= 450000);
  };

  // можно ли активировать кнопку "Далее"
  const canGoNext = () => (
    birthDate instanceof Date &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amount) &&
    isAmountValid()
  );

  // === Обработчики ===
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  const handleAmountChange = e => {
    const f = formatSum(e.target.value);
    setAmount(f);
    const num = parseInt(f.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium' && num < 60000) {
      setAmountError('Минимум 60 000 ₽');
    } else if (calcType === 'sum' && num < 450000) {
      setAmountError('Минимум 450 000 ₽');
    } else {
      setAmountError('');
    }
  };

  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium'
                    ? 'Страхового взноса'
                    : 'Страховой суммы',
      sum:        parseInt(amount.replace(/\./g, ''), 10)
    };

    try {
      setIsProcessing(true);
      const res = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      const data = await res.json();
      setIsProcessing(false);
      if (res.ok) setResultData(data);
      else console.error('Ошибка сервера:', data);
    } catch (err) {
      setIsProcessing(false);
      console.error('Ошибка запроса:', err);
    }
  };

  const goHome = () => navigate('/MainMenu');

  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* шапка */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="Домой" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
      </div>

      {/* ——— Шаг 1: почта ——— */}
      {step === 1 && !isProcessing && !resultData && (
        <>
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}

          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* ——— Шаг 2: расчёт ——— */}
      {step === 2 && !isProcessing && !resultData && (
        <>
          <h1 className="polls-title animate-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          {/* Дата рождения */}
          <div className="date-section animate-btn btn1">
            <label className="section-label">Дата рождения:</label>
            <DateWheelPicker
              value={birthDate}
              onChange={val => {
                // val может быть Date или { day, month, year }
                if (val instanceof Date) {
                  setBirthDate(val);
                } else if (val && val.day) {
                  setBirthDate(new Date(val.year, val.month - 1, val.day));
                }
              }}
            />
          </div>

          {/* Пол */}
          <div className="gender-section animate-btn btn2">
            <button
              className={`insurance-btn ${gender === 'male' ? 'selected' : ''}`}
              onClick={() => setGender('male')}
            >
              👨 Мужской
            </button>
            <button
              className={`insurance-btn ${gender === 'female' ? 'selected' : ''}`}
              onClick={() => setGender('female')}
            >
              👩 Женский
            </button>
          </div>

          {/* Тип расчёта */}
          <div className="insurance-options animate-btn btn3">
            <button
              className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('premium');
                setAmount('');
                setAmountError('');
              }}
            >
              Страхового взноса
            </button>
            <button
              className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
              onClick={() => {
                setCalcType('sum');
                setAmount('');
                setAmountError('');
              }}
            >
              Страховой суммы
            </button>
          </div>

          {/* Сумма */}
          {calcType && (
            <div className="sum-section animate-btn btn4">
              <label className="section-label">Сумма (₽):</label>
              <input
                type="text"
                placeholder={
                  calcType === 'premium'
                    ? 'Минимум 60 000'
                    : 'Минимум 450 000'
                }
                value={amount}
                onChange={handleAmountChange}
              />
              {amountError && <div className="input-error">{amountError}</div>}
            </div>
          )}

          {/* Кнопка "Далее →" */}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn5"
              onClick={handleNext}
              disabled={!canGoNext()}
            >
              Далее <span className="arrow-icon">→</span>
            </button>
          </div>
        </>
      )}

      {/* Индикатор обработки и результат без изменений */}
      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт…</p>
          <div className="arrow-animation">→</div>
        </div>
      )}
      {resultData && (
        <div className="result-container">
          <h2>Результаты расчёта</h2>
          {/* ... */}
