import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // === Состояния ===
  const [birthDateParts, setBirthDateParts] = useState({ day: null, month: null, year: null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);

  // === Параметры расчёта ===
  // Жёстко зашито 5 лет (т.к. «Стратегия на пять»)
  const term = 5;
  const periodicityLabel = 'Ежегодно';
  const currencyLabel    = 'рубли';

  // Собираем настоящий Date из парсинга колёс
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      // Проверяем валидность
      if (
        !isNaN(dt.getTime()) &&
        dt.getDate()     === d &&
        dt.getMonth()+1  === m &&
        dt.getFullYear() === y
      ) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // Формат суммы: "1234567" → "1.234.567"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(num < 60000 ? 'Введите сумму от 60 000 ₽' : '');
    } else if (calcType === 'sum') {
      setAmountError(num < 450000 ? 'Введите сумму от 450 000 ₽' : '');
    }
  };

  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (calcType === 'premium' && num >= 60000)
        || (calcType === 'sum'     && num >= 450000);
  };

  // Точный расчёт возраста
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // Можем ли показать кнопку «Далее»?
  const canGoNext = () => (
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18
  );

  // Отправляем расчёт на сервер
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const age = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType === 'premium'
        ? 'Страхового взноса'
        : 'Страховой суммы',
      sum: parseInt(amount.replace(/\./g, ''), 10)
    };

    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
      } else {
        console.error('Ошибка от сервера:', data);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
    }
    setIsProcessing(false);
  };

  // Дата нынешнего расчёта
  const calculationDate = new Date().toLocaleDateString('ru-RU', {
    day: '2-digit', month: '2-digit', year: 'numeric'
  });

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* Шапка */}
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>
      <h1 className="snp-title">Расчёт по программе "Стратегия на пять. Гарант"</h1>

      {/* Процессинг */}
      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {/* Результат */}
      {!isProcessing && resultData && (
        <div className="result-page-container">
          {/* Верхняя панель: Назад — Лого — Пересчитать */}
          <div className="result-header">
            <button className="header-btn" onClick={() => navigate('/Main-Menu')}>
              <img src={mainmenuIcon} alt="Назад"/>
            </button>
            <div className="header-logo">
              <img src={logoImage} alt="Логотип"/>
            </div>
            <button className="header-btn" onClick={handleNext}>
              <span className="refresh-unicode">⟳</span>
            </button>
          </div>

          <h2 className="result-main-title">ВАШ ПЕРСОНАЛЬНЫЙ ПРЕДВАРИТЕЛЬНЫЙ РАСЧЁТ</h2>

          {/* Данные расчёта в две колонки */}
          <div className="details-container">
            <div className="details-col">
              <p><span>Дата расчёта:</span> {calculationDate}</p>
              <p><span>Возраст Застрахованного:</span> {resultData.age} лет</p>
              <p><span>Срок страхования:</span> {term} лет</p>
              <p><span>Периодичность взносов:</span> {periodicityLabel}</p>
            </div>
            <div className="details-col">
              <p><span>Пол:</span> {resultData.gender}</p>
              <p><span>Валюта:</span> {currencyLabel}</p>
            </div>
          </div>

          {/* Блок с суммами */}
          <div className="calc-block">
            <h3 className="block-title">В ТЕЧЕНИЕ {term} ЛЕТ</h3>
            <div className="block-content">
              <div className="contribution">
                <p className="contribution-label">Вы вносите средства в программу</p>
                <p className="contribution-amount">
                  {formatSum(String(resultData.sum))} ₽
                </p>
                <p className="contribution-frequency">{periodicityLabel}</p>
              </div>
              <div className="total">
                <p className="total-amount">
                  {formatSum(String(resultData.sum * term))} ₽
                </p>
                <p className="total-label">Сумма ваших взносов</p>
              </div>
            </div>
          </div>

          <div className="next-arrow">→</div>
        </div>
      )}

      {/* Форма ввода */}
      {!isProcessing && !resultData && (
        <div className="snp-form">
          {/* ... ваш существующий блок формы (DateWheelPicker, пол, расчёт и т.д.) ... */}
          {/* Для краткости тут не дублирую форму, она осталась без изменений */}
        </div>
      )}

      {/* Кнопки навигации */}
      {!isProcessing && !resultData && (
        <>
          <div className="back-btn-container">
            <button className="back-btn mainmenu-btn" onClick={() => navigate('/Main-Menu')}>
              <img src={mainmenuIcon} alt="Menu"/>
            </button>
          </div>
          <div className="top-btn-container">
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </>
      )}
    </div>
  );
}
