      const age = calculateAge(bd);
      if (age < 18 || age > 70) {
        alert('–í–æ–∑—Ä–∞—Å—Ç –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 18 –¥–æ 70 –ª–µ—Ç');
        return;
      }
      if (!gender) {
        alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª');
        return;
      }
      if (!insuranceInfo) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –∑–Ω–∞–µ—Ç–µ –ª–∏ –í—ã –Ω–∞ –∫–∞–∫–æ–π —Å—Ä–æ–∫ –∏ —Å—É–º–º—É –æ—Ñ–æ—Ä–º–∏—Ç—å –ø–æ–ª–∏—Å');
        return;
      }
      // –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –≤—ã–±—Ä–∞–ª "–î–∞", –ø–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —à–∞–≥—É 2, –∏–Ω–∞—á–µ —Å—Ä–∞–∑—É –∫ —à–∞–≥—É 3 (–ø–∞–∫–µ—Ç—ã)
      if (insuranceInfo === 'yes') {
        setCurrentStep(2);
      } else {
        // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è –¥–ª—è —à–∞–≥–∞ 2 (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ)
        setInsuranceTerm('1');
        setInsuranceSum('');
        setInsuranceFrequency('');
        setCurrentStep(3);
      }
    } else if (currentStep === 2) {
      // –®–∞–≥ 2: –ü—Ä–æ–≤–µ—Ä–∫–∞ —Å—Ä–æ–∫–∞, —Å—É–º–º—ã –∏ –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç–∏
      const termNum = Number(insuranceTerm);
      if (termNum < 1 || termNum > 30) {
        alert('–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –æ—Ç 1 –¥–æ 30 –ª–µ—Ç');
        return;
      }
      const rawDigits = insuranceSum.replace(/\D/g, '');
      if (!rawDigits) {
        alert('–í–≤–µ–¥–∏—Ç–µ —Å—Ç—Ä–∞—Ö–æ–≤—É—é —Å—É–º–º—É');
        return;
      }
      const sumNumber = Number(rawDigits);
      if (sumNumber < 1000000) {
        alert('–°—Ç—Ä–∞—Ö–æ–≤–∞—è —Å—É–º–º–∞ –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—Ç 1 000 000 —Ä—É–±.');
        return;
      }
      if (!insuranceFrequency) {
        alert('–í—ã–±–µ—Ä–∏—Ç–µ –ø–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –æ–ø–ª–∞—Ç—ã —Å—Ç—Ä–∞—Ö–æ–≤–æ–π –ø—Ä–µ–º–∏–∏');
        return;
      }
      setCurrentStep(3);
    } else if (currentStep === 3) {
      // –®–∞–≥ 3: –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–∞–∫–µ—Ç–æ–≤
      if (!accidentPackage) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –ø–æ–¥–∫–ª—é—á–∞–µ–º –ª–∏ –í—ã –ø–∞–∫–µ—Ç "–ù–µ—Å—á–∞—Å—Ç–Ω—ã–π —Å–ª—É—á–∞–π"');
        return;
      }
      if (!criticalPackage) {
        alert('–£–∫–∞–∂–∏—Ç–µ, –ø–æ–¥–∫–ª—é—á–∞–µ–º –ª–∏ –í—ã –ø–∞–∫–µ—Ç "–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è"');
        return;
      }
      if (criticalPackage === 'yes' && !treatmentRegion) {
        alert('–í—ã–±–µ—Ä–∏—Ç–µ —Ä–µ–≥–∏–æ–Ω –ª–µ—á–µ–Ω–∏—è –¥–ª—è –ø–∞–∫–µ—Ç–∞ "–ö—Ä–∏—Ç–∏—á–µ—Å–∫–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è"');
        return;
      }
      // –ï—Å–ª–∏ –≤—Å–µ –ø—Ä–æ–≤–µ—Ä–∫–∏ –ø—Ä–æ–π–¥–µ–Ω—ã, –∑–∞–≤–µ—Ä—à–∞–µ–º –æ–ø—Ä–æ—Å
