// EmployeePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import mainMenuIcon from './components/icons/mainmenu.png';
import './EmployeePage.css';

const EmployeePage = () => {
  const navigate = useNavigate();

  const handleJustInCase = () => {
    navigate('/justincase');
  };

  const handleStrategy = () => {
    navigate('/snp'); // предполагается, что путь к SNPPage — '/snp'
  };

  const handleCareFuture = () => {
    navigate('/care-future');  // замените путь на нужный
  };

  const handleMainMenu = () => {
    navigate('/Main-Menu');  // при необходимости скорректируйте путь
  };

  return (
    <div
      className="employee-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Кнопка-иконка в левом верхнем углу */}
      <div className="back-btn-container">
        <button className="back-btn mainmenu-btn" onClick={handleMainMenu}>
          <img src={mainMenuIcon} alt="Main Menu" />
        </button>
      </div>

      <h1 className="employee-title">Страница для сотрудника</h1>

      <button className="mainmenu-btn" onClick={handleJustInCase}>
        На всякий случай
      </button>

      <button className="mainmenu-btn" onClick={handleStrategy}>
        Стратегия на пять. Гарант
      </button>

      <button className="mainmenu-btn" onClick={handleCareFuture}>
        Забота о будущем
      </button>
    </div>
  );
};

export default EmployeePage;
