import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable'; // Импортируем библиотеку для свайпов
import logo from './components/logo.png';
import backgroundImage from './components/background.jpg';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [textVisible, setTextVisible] = useState(true);
  const [isMovingUp, setIsMovingUp] = useState(false);
  const [greeting, setGreeting] = useState('Добро пожаловать!');
  const [offsetY, setOffsetY] = useState(0);

  // Обработчик свайпа
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => navigate('/register'), // Переход на страницу регистрации при свайпе влево
    onSwipedRight: () => navigate('/home'),   // Переход на домашнюю страницу при свайпе вправо
  });

  const handleScroll = () => {
    setOffsetY(window.pageYOffset);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    const now = new Date();
    const hour = now.getHours();
    if (hour >= 6 && hour < 11) {
      setGreeting("Доброе утро");
    } else if (hour >= 11 && hour < 17) {
      setGreeting("Добрый день!");
    } else if (hour >= 17 && hour < 24) {
      setGreeting("Добрый вечер!");
    } else {
      setGreeting("Доброй ночи!");
    }

    // Через 4 секунды запускаем анимацию
    const moveUpTimer = setTimeout(() => {
      setIsMovingUp(true);
      setTextVisible(false);
    }, 4000);

    // Через 5 секунд перенаправляем на страницу регистрации
    const redirectTimer = setTimeout(() => {
      navigate('/register');
    }, 5000);

    return () => {
      clearTimeout(moveUpTimer);
      clearTimeout(redirectTimer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, [navigate]);

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})`, backgroundPositionY: `${offsetY * 0.5}px` }} {...swipeHandlers}>
      <div className="logo">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>
        {greeting}
      </h1>
    </div>
  );
};

export default WelcomePage;
