// src/PollsPage.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate }           from 'react-router-dom';
import { useSwipeable }          from 'react-swipeable';

import './Styles/global.css';     // Montserrat + сбросы
import './Styles/background.css'; // фон-градиент, subtle-dot, π-анимация
import './Styles/logo.css';       // логотип + его CSS-анимации
import './Styles/Buttons.css';    // стили кнопок + ripple + entry/exit
import './Styles/HomeButton.css'; // стили кнопки «Домой»

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import piImage         from './components/pi.png';

export default function PollsPage() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);
  const [moveDuration, setMoveDuration]     = useState('70s');
  const [rotateDuration, setRotateDuration] = useState('6s');

  // Рефы на DOM-элементы:
  const logoRef      = useRef(null);
  const textTitleRef = useRef(null);
  const buttonRefs   = useRef([]);
  const piWrapperRef = useRef(null);
  const homeRef      = useRef(null);

  // При монтировании запускаем все entry-анимации:
  useEffect(() => {
    setAnimate(true);

    // 1) Кнопка «Домой» въезжает
    if (homeRef.current) {
      homeRef.current.classList.add('animate-home');
    }

    // 2) Логотип въезжает
    if (logoRef.current) {
      logoRef.current.classList.add('animate-logo');
    }

    // 3) Заголовок «Список опросов» въезжает
    if (textTitleRef.current) {
      textTitleRef.current.classList.add('animate-title');
    }

    // 4) Кнопки-опросы въезжают снизу с задержками
    buttonRefs.current.forEach((btn, idx) => {
      if (btn) {
        btn.classList.add('animate-btn', `btn${idx + 1}`);
      }
    });

    // 5) π-иконка начинает свой «летательный» + «tilt» цикл
    const rndMove = Math.random() * (90 - 50) + 50; // [50..90] с
    const rndRot  = Math.random() * (8 - 4)  + 4;   // [4..8] с
    setMoveDuration(`${rndMove.toFixed(2)}s`);
    setRotateDuration(`${rndRot.toFixed(2)}s`);
    if (piWrapperRef.current) {
      setTimeout(() => {
        piWrapperRef.current.classList.add('pi-active');
      }, 400);
    }
  }, []);

  // Обработчик нажатия на любую из «кнопок-опросов»:
  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    // 1) рисуем ripple-круг
    const circle = document.createElement('span');
    const d = Math.max(btn.clientWidth, btn.clientHeight);
    const r = d / 2;
    circle.style.width  = circle.style.height = `${d}px`;
    const rect = btn.getBoundingClientRect();
    circle.style.left = `${e.clientX - rect.left - r}px`;
    circle.style.top  = `${e.clientY - rect.top  - r}px`;
    circle.classList.add('ripple');
    const old = btn.getElementsByClassName('ripple')[0];
    if (old) old.remove();
    btn.appendChild(circle);

    // 2) Exit-анимация: кнопка «Домой»
    if (homeRef.current) {
      homeRef.current.classList.remove('animate-home');
      homeRef.current.classList.add('animate-home-exit');
    }
    // 3) Exit-анимация: логотип
    if (logoRef.current) {
      logoRef.current.classList.remove('animate-logo');
      logoRef.current.classList.add('animate-logo-exit');
    }
    // 4) Exit-анимация: заголовок
    if (textTitleRef.current) {
      textTitleRef.current.classList.remove('animate-title');
      textTitleRef.current.classList.add('animate-title-exit');
    }
    // 5) Exit-анимация: кнопки-опросы (реверсивно)
    buttonRefs.current
      .slice()
      .reverse()
      .forEach((btnEl, revIdx) => {
        if (!btnEl) return;
        btnEl.classList.remove(
          'animate-btn',
          `btn${buttonRefs.current.length - revIdx}`
        );
        btnEl.classList.add(
          'animate-btn-exit',
          `btn-exit${revIdx + 1}`
        );
      });
    // 6) Exit-анимация: π-иконка
    if (piWrapperRef.current) {
      piWrapperRef.current.classList.remove('pi-active');
      piWrapperRef.current.classList.add('pi-exit');
    }

    // После 0.8 c (когда завершатся exit-анимации) переходим на нужный маршрут
    setTimeout(() => {
      navigate(route);
    }, 800);
  };

  // Свайп-обработчик (остается «как было»)
  const swipeHandlers = useSwipeable({ preventDefault: true });

  // Массив «опросов»
  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь',    path: '/feedback'   },
    { label: 'Маржа продаж',      path: '/marzapoll'  },
  ];

  // Класс для логотипа: animate → 'logo-wrapper animate-logo'
  const logoClass = animate ? 'logo-wrapper animate-logo' : 'logo-wrapper';

  return (
    <div
      className="polls-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* === 10 subtle-dot «шариков» === */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/* === π-иконка («летает» + tilt) === */}
      <div
        ref={piWrapperRef}
        className="pi-wrapper"
        style={{ '--pi-move-duration': moveDuration }}
      >
        <img
          src={piImage}
          alt="Pi"
          className="pi-fly"
          style={{ '--pi-rotate-duration': rotateDuration }}
        />
      </div>

      {/* === Полупрозрачный «стеклянный» оверлей поверх фона === */}
      <div className="polls-overlay" />

      {/*
        === Шапка (.polls-header): Кнопка «Домой» + Логотип ===
        В CSS эти два элемента ждут контейнер .polls-header,
        чтобы отсюда задать правильный отступ сверху (padding-top:160) и
        абсолютное позиционирование логотипа.
      */}
      <div className="polls-header">
        {/* Кнопка «Домой» */}
        <button
          ref={homeRef}
          className="back-btn"
          onClick={() => {
            // 1) Exit-анимация лого/π, затем переход сразу
            if (logoRef.current) {
              logoRef.current.classList.remove('animate-logo');
              logoRef.current.classList.add('animate-logo-exit');
            }
            if (piWrapperRef.current) {
              piWrapperRef.current.classList.remove('pi-active');
              piWrapperRef.current.classList.add('pi-exit');
            }
            // 0.8s на exit, потом навигация
            setTimeout(() => navigate('/main-menu'), 800);
          }}
          aria-label="Домой"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 576 512"
            className="home-icon"
          >
            <path d="M280.37 148.26L96 300.11V464a16 16 0 0016 16l112-.29a16 16 0 0016-15.74V368a16 16 0 0116-16h64a16 16 0 0116 16v96a16 16 0 0016 15.74l112 .29a16 16 0 0016-16V300L295.67 148.26a12.19 12.19 0 00-15.3 0zM571.6 251.47L488 182.57V44a12 12 0 00-12-12h-72a12 12 0 00-12 12v72.61L318.47 43a48 48 0 00-61 0L4.34 251.47a12 12 0 00-1.6 16.9l25.5 31a12 12 0 0016.9 1.6L64 269.69V464a48 48 0 0048 48h112a48 48 0 0048-48V368a16 16 0 0116-16h64a16 16 0 0116 16v96a48 48 0 0048 48h112a48 48 0 0048-48V269.7l18.9 16.42a12 12 0 0016.9-1.6l25.5-31a12 12 0 00-1.7-16.95z" />
          </svg>
        </button>

        {/* Абсолютно позиционированный логотип */}
        <div ref={logoRef} className={logoClass}>
          <img
            src={logoImage}
            alt="Логотип РГС Жизнь"
            className="logo-image"
          />
        </div>
      </div>

      {/* === Заголовок «Список опросов» (появится ниже лого) === */}
      <h2 ref={textTitleRef} className="polls-title">
        Список опросов
      </h2>

      {/* === Блок кнопок-опросов === */}
      <div className="button-container">
        {polls.map((p, i) => (
          <button
            key={p.path}
            ref={(el) => (buttonRefs.current[i] = el)}
            className="btn-custom"
            onClick={(e) => handleClick(e, p.path)}
          >
            {p.label}
          </button>
        ))}
      </div>
    </div>
  );
}
