// src/SNPPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // части даты для пикера
  const [birthDateParts, setBirthDateParts] = useState({
    day:   null,
    month: null,
    year:  null,
  });
  // реальный объект Date
  const [birthDate, setBirthDate] = useState(null);

  const [gender, setGender]           = useState(null);   // 'male' или 'female'
  const [calcType, setCalcType]       = useState(null);   // 'premium' или 'sum'
  const [amount, setAmount]           = useState('');     // отформатированная сумма
  const [amountError, setAmountError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // собираем настоящий Date из частей
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (
        !isNaN(dt.getTime())
        && dt.getDate()    === d
        && dt.getMonth()+1 === m
        && dt.getFullYear()=== y
      ) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // формат суммы "1234567" → "1.234.567"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(num < 60000 ? 'Введите сумму от 60 000 ₽' : '');
    } else if (calcType === 'sum') {
      setAmountError(num < 450000 ? 'Введите сумму от 450 000 ₽' : '');
    }
  };

  const isAmountValid = () => {
    const num = parseInt(amount.replace(/\./g, ''), 10) || 0;
    return (
      (calcType === 'premium' && num >= 60000) ||
      (calcType === 'sum'     && num >= 450000)
    );
  };

  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) {
      age--;
    }
    return age;
  };

  const canGoNext = () => (
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18
  );

  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const age = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType === 'premium'
        ? 'Страхового взноса'
        : 'Страховой суммы',
      sum: parseInt(amount.replace(/\./g, ''), 10)
    };

    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) setResultData(data);
      else console.error('Сервер вернул ошибку:', data);
    } catch (err) {
      console.error('Ошибка запроса:', err);
    }
    setIsProcessing(false);
  };

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="logo-container">
        <img src={logoImage} alt="Logo" className="logo-image"/>
      </div>
      <h1 className="snp-title">
        Расчёт по программе "Стратегия на пять. Гарант"
      </h1>

      {isProcessing ? (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      ) : resultData ? (
        <div className="result-container">
          <h2>Результаты расчёта</h2>
          <p><strong>Возраст:</strong> {resultData.age} лет</p>
          <p><strong>Пол:</strong> {resultData.gender}</p>
          <p><strong>Тип расчёта:</strong> {resultData.payment}</p>
          <p><strong>Сумма:</strong> {formatSum(resultData.sum.toString())} ₽</p>
          <p><strong>Итоговая премия:</strong> {formatSum((resultData.totalPremium||0).toString())} ₽</p>
        </div>
      ) : (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Дата рождения:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val => {
                setBirthDateParts(prev => {
                  // если сразу Date
                  if (val instanceof Date) {
                    return {
                      day:   val.getDate(),
                      month: val.getMonth() + 1,
                      year:  val.getFullYear()
                    };
                  }
                  // если массив [д, м, г]
                  if (Array.isArray(val) && val.length === 3) {
                    const [d, m, y] = val.map(Number);
                    return { day: d, month: m, year: y };
                  }
                  // если объект с любыми полями day/month/year
                  if (val && typeof val === 'object') {
                    return {
                      ...prev,
                      ...(val.day   != null ? { day:   Number(val.day)   } : {}),
                      ...(val.month != null ? { month: Number(val.month) } : {}),
                      ...(val.year  != null ? { year:  Number(val.year)  } : {})
                    };
                  }
                  // если строка "DD.MM.YYYY" или "YYYY-MM-DD"
                  if (typeof val === 'string') {
                    if (val.includes('.')) {
                      const [d, m, y] = val.split('.').map(Number);
                      return { day: d, month: m, year: y };
                    }
                    if (val.includes('-')) {
                      const [y, m, d] = val.split('-').map(Number);
                      return { day: d, month: m, year: y };
                    }
                  }
                  return prev;
                });
              }}
            />
          </div>

          <div className="form-group">
            <label className="form-label">Пол:</label>
            <div className="gender-selector">
              {['male','female'].map(g => (
                <div
                  key={g}
                  className={`gender-option ${gender === g ? 'selected' : ''}`}
                  onClick={() => setGender(g)}
                >
                  <div className="gender-label">
                    {g === 'male' ? 'Мужской' : 'Женский'}
                  </div>
                  <div className="gender-icon">
                    {g === 'male' ? '👨' : '👩'}
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Расчёт от…</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => { setCalcType('premium'); setAmount(''); setAmountError(''); }}
              >
                Страхового взноса
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => { setCalcType('sum'); setAmount(''); setAmountError(''); }}
              >
                Страховой суммы
              </button>
            </div>
          </div>

          {calcType && (
            <div className="form-group">
              <label className="form-label">Введите сумму в рублях</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType === 'premium' ? 'Минимум 60 000' : 'Минимум 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}
        </div>
      )}

      <div className="back-btn-container">
        <button className="back-btn mainmenu-btn" onClick={() => navigate('/Main-Menu')}>
          <img src={mainmenuIcon} alt="Menu"/>
        </button>
      </div>

      {!isProcessing && !resultData && (
        <div className="top-btn-container">
          <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
            <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
          </button>
        </div>
      )}
    </div>
  );
}
