import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Autosuggest from 'react-autosuggest';
import partners from './components/autosuggest/partners.json';
import speakers from './components/autosuggest/speakers.json';
import './FeedbackPage.css';
import logo from './components/logo.png';

const FeedbackPage = () => {
  const navigate = useNavigate();
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [positiveQualities, setPositiveQualities] = useState([]);
  const [negativeQualities, setNegativeQualities] = useState([]);
  const [usefulness, setUsefulness] = useState('');
  const [brightThoughts, setBrightThoughts] = useState('');
  const [additionalItems, setAdditionalItems] = useState([]);
  const [impression, setImpression] = useState('');
  const [currentQuestion, setCurrentQuestion] = useState(0); // Для отслеживания текущего вопроса
  const [isFinished, setIsFinished] = useState(false);

  // Функция для фильтрации предложений по введенному значению
  const getPartnerSuggestions = (value) => {
    const trimmedValue = value && typeof value === 'string' ? value.trim().toLowerCase() : ''; // Проверка на строку
    if (!trimmedValue || trimmedValue.length < 2) {
      return [];
    }
    return partners.filter((partner) =>
      partner.toLowerCase().includes(trimmedValue)
    );
  };

  // Обработчик для поиска предложений
  const onSuggestionsFetchRequested = ({ value }) => {
    setPartnerSuggestions(getPartnerSuggestions(value));
  };

  // Очистка предложений
  const onSuggestionsClearRequested = () => {
    setPartnerSuggestions([]);
  };

  // Обработчик изменения значения ввода
  const onPartnerChange = (event, { newValue }) => {
    setPartner(newValue);
    // Очистить состояние предложений для спикеров при изменении партнера
    setSpeakersList([{ fullName: '', id: 1 }]);
  };

  // Рендеринг предложений для автозаполнения
  const renderSuggestion = (suggestion) => {
    return <div>{suggestion}</div>;
  };

  const partnerInputProps = {
    placeholder: 'Введите название партнера',
    value: partner,
    onChange: onPartnerChange,
  };

  // Функция для добавления новых спикеров
  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  // Функция для изменения фамилии спикера
  const handleSpeakerChange = (e, index) => {
    const updatedSpeakers = [...speakersList];
    updatedSpeakers[index].fullName = e.target.value || '';  // Гарантируем, что значение всегда строка
    setSpeakersList(updatedSpeakers);
  };

  // Функция для фильтрации спикеров
  const getSpeakerSuggestions = (value) => {
    const trimmedValue = value && typeof value === 'string' ? value.trim().toLowerCase() : ''; // Проверка на строку
    if (!trimmedValue || trimmedValue.length < 2) {
      return [];
    }
    return speakers.filter((speaker) =>
      speaker.toLowerCase().includes(trimmedValue)
    );
  };

  const handleSpeakerSuggestionsFetchRequested = (index, { value }) => {
    const updatedSpeakers = [...speakersList];
    updatedSpeakers[index].suggestions = getSpeakerSuggestions(value);
    setSpeakersList(updatedSpeakers);
  };

  const handleSpeakerSuggestionsClearRequested = (index) => {
    const updatedSpeakers = [...speakersList];
    updatedSpeakers[index].suggestions = [];
    setSpeakersList(updatedSpeakers);
  };

  // Оценка качеств спикера
  const handleQualityChange = (quality, isPositive) => {
    if (isPositive) {
      setPositiveQualities((prev) => [...prev, quality]);
      setNegativeQualities((prev) => prev.filter((item) => item !== quality));  // Убираем из отрицательных
    } else {
      setNegativeQualities((prev) => [...prev, quality]);
      setPositiveQualities((prev) => prev.filter((item) => item !== quality));  // Убираем из положительных
    }
  };

  const questions = [
    {
      type: 'input',
      label: 'Название партнера',
      renderInput: () => (
        <Autosuggest
          suggestions={partnerSuggestions}
          onSuggestionsFetchRequested={onSuggestionsFetchRequested}
          onSuggestionsClearRequested={onSuggestionsClearRequested}
          getSuggestionValue={(suggestion) => suggestion}
          renderSuggestion={renderSuggestion}
          inputProps={partnerInputProps}
        />
      ),
    },
    {
      type: 'speakers',
      label: 'Фамилия и имя спикера',
      renderSpeakers: () => (
        <div>
          {speakersList.map((speaker, index) => (
            <div key={index}>
              <label className="subquestion-name">Фамилия и имя спикера {index + 1}</label>
              <Autosuggest
                suggestions={speaker.suggestions || []}
                onSuggestionsFetchRequested={(params) => handleSpeakerSuggestionsFetchRequested(index, params)}
                onSuggestionsClearRequested={() => handleSpeakerSuggestionsClearRequested(index)}
                getSuggestionValue={(suggestion) => suggestion}
                renderSuggestion={(suggestion) => <div>{suggestion}</div>}
                inputProps={{
                  value: speaker.fullName, // Обновляем поле с выбранным значением
                  onChange: (e) => handleSpeakerChange(e, index), // При изменении обновляется значение
                  placeholder: 'Введите фамилию и имя',
                }}
              />
              {index === speakersList.length - 1 && (
                <button onClick={handleAddSpeaker}>Добавить еще спикера</button>
              )}
            </div>
          ))}
        </div>
      ),
    },
    {
      type: 'qualities',
      label: 'Оценка качеств спикера',
      renderQualities: () => (
        <div>
          <div className="quality-row">
            <button
              onClick={() => handleQualityChange('Экспертный', true)}
              className={positiveQualities.includes('Экспертный') ? 'green' : ''}
            >
              Экспертный
            </button>
            <button
              onClick={() => handleQualityChange('Некомпетентный', false)}
              className={negativeQualities.includes('Некомпетентный') ? 'red' : ''}
            >
              Некомпетентный
            </button>
          </div>

          <div className="quality-row">
            <button
              onClick={() => handleQualityChange('Энергичный', true)}
              className={positiveQualities.includes('Энергичный') ? 'green' : ''}
            >
              Энергичный
            </button>
            <button
              onClick={() => handleQualityChange('Пассивный', false)}
              className={negativeQualities.includes('Пассивный') ? 'red' : ''}
            >
              Пассивный
            </button>
          </div>

          <div className="quality-row">
            <button
              onClick={() => handleQualityChange('Мотивирующий', true)}
              className={positiveQualities.includes('Мотивирующий') ? 'green' : ''}
            >
              Мотивирующий
            </button>
            <button
              onClick={() => handleQualityChange('Невдохновляющий', false)}
              className={negativeQualities.includes('Невдохновляющий') ? 'red' : ''}
            >
              Невдохновляющий
            </button>
          </div>

          <div className="quality-row">
            <button
              onClick={() => handleQualityChange('Харизматичный', true)}
              className={positiveQualities.includes('Харизматичный') ? 'green' : ''}
            >
              Харизматичный
            </button>
            <button
              onClick={() => handleQualityChange('Душный', false)}
              className={negativeQualities.includes('Душный') ? 'red' : ''}
            >
              Душный
            </button>
          </div>

          <div className="quality-row">
            <button
              onClick={() => handleQualityChange('Доступно доносит информацию', true)}
              className={positiveQualities.includes('Доступно доносит информацию') ? 'green' : ''}
            >
              Доступно доносит информацию
            </button>
            <button
              onClick={() => handleQualityChange('Говорит сложными терминами', false)}
              className={negativeQualities.includes('Говорит сложными терминами') ? 'red' : ''}
            >
              Говорит сложными терминами
            </button>
          </div>
        </div>
      ),
    },
  ];

  // Функция для перехода к следующему вопросу
  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      handleFinish();
    }
  };

  // Функция для завершения опроса и отправки данных
  const handleFinish = async () => {
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      speakers: speakersList.map((speaker) => speaker.fullName).join(', '),
      positiveQualities,
      negativeQualities,
      usefulness,
      brightThoughts,
      additionalItems,
      impression,
    };

    try {
      const response = await fetch("https://yourserver/api/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        setIsFinished(true);
        alert('Спасибо за участие в опросе!');
      } else {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  const currentQuestionData = questions[currentQuestion];

  return (
    <div className="feedback-container">
      <div className="logo"></div> {/* Логотип */}
      <h1 className="name-vote">Оценка мероприятия</h1>

      {/* Вопросы */}
      {currentQuestionData.type === 'input' && currentQuestionData.renderInput()}
      {currentQuestionData.type === 'speakers' && currentQuestionData.renderSpeakers()}
      {currentQuestionData.type === 'qualities' && currentQuestionData.renderQualities()}

      {/* Кнопка "Далее" или "Завершить опрос" */}
      <button className="next-button" onClick={handleNext} disabled={isFinished}>
        {currentQuestion === questions.length - 1 ? 'Завершить опрос' : 'Далее'}
      </button>
    </div>
  );
};

export default FeedbackPage;
