import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Parallax } from 'react-parallax';
import logo from './components/logo.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [textVisible, setTextVisible] = useState(true);
  const [isMovingUp, setIsMovingUp] = useState(false);
  const [greeting, setGreeting] = useState('Добро пожаловать!');

  const WelcomePage = () => (
  <Parallax bgImage="/background.jpg" strength={300}>
    <div style={{ height: '100vh', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
      <img src={logo} alt="Логотип" style={{ width: '150px', height: '150px' }} />
      <h1 style={{ color: 'white' }}>Добро пожаловать!</h1>
    </div>
  </Parallax>
);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    const now = new Date();
    const hour = now.getHours();
    if (hour >= 6 && hour < 11) {
      setGreeting("Доброе утро");
    } else if (hour >= 11 && hour < 17) {
      setGreeting("Добрый день!");
    } else if (hour >= 17 && hour < 24) {
      setGreeting("Добрый вечер!");
    } else {
      setGreeting("Доброй ночи!");
    }

    // Через 4 секунды запускаем анимацию перемещения логотипа вверх
    // и исчезание текста (обновляем textVisible на false)
    const moveUpTimer = setTimeout(() => {
      setIsMovingUp(true);
      setTextVisible(false);
    }, 4000);

    // Через 5 секунд перенаправляем на страницу регистрации
    const redirectTimer = setTimeout(() => {
      navigate('/register');
    }, 5000);

    return () => {
      clearTimeout(moveUpTimer);
      clearTimeout(redirectTimer);
    };
  }, [navigate]);

  return (
    <div className="welcome-container" style={{ backgroundPositionY: `${offsetY * 0.5}px` }}>
      <div className={`logo ${isMovingUp ? 'move-up' : ''}`} />
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>
        {greeting}
      </h1>
    </div>
  );
};

export default WelcomePage;
