// src/MainMenu.js
import React, { useState, useEffect } from 'react';
import { useNavigate }           from 'react-router-dom';

import './Styles/global.css';     // Montserrat, сбросы
import './Styles/background.css'; // градиент, шум, subtle-dot и pi
import './Styles/logo.css';       // лого, анимации
import './Styles/Buttons.css';    // стили кнопок

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import piImage         from './components/pi.png';  // <-- импортируем pi.png

export default function MainMenu() {
  const navigate = useNavigate();
  const [animate, setAnimate] = useState(false);

  // Две случайные длительности:
  // • moveDuration — от 50 до 90 секунд
  // • rotateDuration — от 4 до 8 секунд
  const [moveDuration, setMoveDuration]   = useState('70s');
  const [rotateDuration, setRotateDuration] = useState('6s');

  useEffect(() => {
    // Запускаем анимацию логотипа
    setAnimate(true);

    // Генерируем рандомные длительности только один раз при монтировании
    const rndMove = Math.random() * (90 - 50) + 50;    // диапазон [50,90]
    const rndRot  = Math.random() * (8 - 4)  + 4;     // диапазон [4,8]

    setMoveDuration(`${rndMove.toFixed(2)}s`);
    setRotateDuration(`${rndRot.toFixed(2)}s`);
  }, []);

  const handleClick = (e, route) => {
    const btn = e.currentTarget;
    const circle = document.createElement('span');
    const diameter = Math.max(btn.clientWidth, btn.clientHeight);
    const radius = diameter / 2;
    circle.style.width  = circle.style.height = `${diameter}px`;
    circle.style.left   = `${e.clientX - btn.offsetLeft  - radius}px`;
    circle.style.top    = `${e.clientY - btn.offsetTop   - radius}px`;
    circle.classList.add('ripple');
    const oldRipple = btn.getElementsByClassName('ripple')[0];
    if (oldRipple) oldRipple.remove();
    btn.appendChild(circle);
    setTimeout(() => navigate(route), 300);
  };

  const buttons = [
    { to: '/polls',    label: 'Опросы'    },
    { to: '/employee', label: 'Сотруднику' },
  ];

  const logoClass = animate ? 'logo-wrapper animate-logo' : 'logo-wrapper';

  return (
    <div
      className="mainmenu-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/*
         10 «едва заметных» шариков — они задаются через background.css
      */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/*
         Здесь наша «pi-иконка»: плывёт и покачивается.
         Передаём в style две CSS-переменные:
           --pi-move-duration    (длительность анимации movePi)
           --pi-rotate-duration  (длительность анимации rotatePi)
      */}
      <div
        className="pi-wrapper"
        style={{
          '--pi-move-duration': moveDuration,
        }}
      >
        <img
          src={piImage}
          className="pi-fly"
          alt="Pi"
          style={{
            '--pi-rotate-duration': rotateDuration,
          }}
        />
      </div>

      <div className="mainmenu-overlay" />

      {/* Логотип */}
      <div className={logoClass}>
        <img
          src={logoImage}
          alt="Логотип РГС Жизнь"
          className="logo-image"
        />
      </div>

      {/* Кнопки */}
      <div className="button-container">
        {buttons.map((btn) => (
          <button
            key={btn.to}
            className="btn-custom"
            onClick={(e) => handleClick(e, btn.to)}
          >
            {btn.label}
          </button>
        ))}
      </div>
    </div>
  );
}
