import React, { useEffect } from 'react';
import './MainMenu.css';
import logoImage from './components/logo.png';
import backgroundImage from './components/background.png';

export default function MainMenu({ navigate }) {
  // Вариант использования react-router:
  // import { useNavigate } from 'react-router-dom';
  // const navigate = useNavigate();

  return (
    <div className="mainmenu-container">
      {/* опциональный glass-слой */}
      <div className="mainmenu-overlay" />

      {/* логотип */}
      <img
        src={logoImage}
        alt="Логотип РГС Жизнь"
        className="mainmenu-logo"
      />

      {/* кнопки */}
      <div className="mainmenu-buttons">
        <button onClick={() => navigate('/surveys')}    className="mainmenu-btn">Опросы</button>
        <button onClick={() => navigate('/profile')}    className="mainmenu-btn">Профиль</button>
        <button onClick={() => navigate('/employee')}   className="mainmenu-btn">Сотруднику</button>
      </div>
    </div>
);
}
