import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import mainmenuIcon    from './components/icons/mainmenu.png';
import './EmployeePage.css';

export default function EmployeePage() {
  const navigate = useNavigate();

  return (
    <div
      className="employee-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* полупрозрачный размытый слой */}
      <div className="employee-overlay" />

      {/* кнопка «Домой» */}
      <button className="back-btn" onClick={() => navigate('/main')}>
        <img src={mainmenuIcon} alt="Домой" />
      </button>

      {/* сам логотип */}
      <img src={logoImage} alt="Логотип" className="employee-logo" />

      {/* заголовок */}
      <h1 className="employee-title">Страница для сотрудника</h1>

      {/* три кнопки */}
      <div className="employee-buttons">
        <button
          className="employee-btn"
          onClick={() => navigate('/safety')}
        >
          На всякий случай
        </button>
        <button
          className="employee-btn"
          onClick={() => navigate('/snp')}
        >
          Стратегия на пять. Гарант
        </button>
        <button
          className="employee-btn"
          onClick={() => navigate('/carefuture')}
        >
          Забота о будущем
        </button>
      </div>
    </div>
  );
}
