// FirstDayAdaptation.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import './FirstDayAdaptation.css';

const FirstDayAdaptation = () => {
  const navigate = useNavigate();

  // Возвращаемся на страницу AdaptationPage
  const goBack = () => navigate('/adaptation'); // путь на AdaptationPage.js

  const handleSectionClick = (route) => {
    navigate(route);
  };

  return (
    <div className="first-day-adaptation-container">
      {/* Кнопка "Назад" */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={goBack}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
            <path d="M12 20l-8-8 8-8" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
          </svg>
        </button>
      </div>

      {/* Заголовок */}
      <h1 className="first-day-adaptation-title">Первый день адаптации</h1>

      {/* Список разделов первого дня */}
      <div className="first-day-adaptation-buttons">
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/znakomstvo')}
        >
          Знакомство с РГСЖ
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/standarty-vneshnego-vida')}
        >
          Стандарты внешнего вида
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/standarty-raboty')}
        >
          Стандарты работы
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/printsipy-raboty')}
        >
          Принципы работы
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/instrumenty-kontrolya')}
        >
          Инструменты и виды контроля
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/miceeling')}
        >
          Мисселинг
        </button>
        <button
          className="first-day-adaptation-btn"
          onClick={() => handleSectionClick('/otchetnost-soprovozhdenie')}
        >
          Отчетность и сопровождение продаж
        </button>
      </div>
    </div>
  );
};

export default FirstDayAdaptation;
