import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import mainMenuIcon    from './components/icons/mainmenu.png';
import './EmployeePage.css';

export default function EmployeePage() {
  const navigate = useNavigate();

  const handleJustInCase = () => navigate('/justincase');
  const handleStrategy   = () => navigate('/snp');
  const handleCareFuture = () => navigate('/carefuture');
  const handleMainMenu   = () => navigate('/main-menu');

  return (
    <div
      className="employee-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Кнопка «Домой» */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={handleMainMenu}>
          <img src={mainMenuIcon} alt="Главное меню" />
        </button>
      </div>

      {/* Логотип */}
      <div className="logo-container">
        <img src={require('./components/logo.png')} alt="Логотип" />
      </div>

      <h1 className="employee-title">Страница для сотрудника</h1>

      <div className="btn-group">
        <button className="employee-btn" onClick={handleJustInCase}>
          На всякий случай
        </button>
        <button className="employee-btn" onClick={handleStrategy}>
          Стратегия на пять. Гарант
        </button>
        <button className="employee-btn" onClick={handleCareFuture}>
          Забота о будущем
        </button>
      </div>
    </div>
  );
}
