import React from 'react';
import logo from './components/logo.png';
import backgroundImage from './components/background.jpg';
import pollIcon from './components/icons/опросы.png'; // Импорт иконки
import { useNavigate } from 'react-router-dom'; // Для навигации
import './PollsPage.css';

const PollsPage = () => {
  const navigate = useNavigate();

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <div className="logo">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <h2>Список опросов</h2>

      {/* Кнопка "Оценка кандидата" с иконкой */}
      <button className="poll-btn" onClick={() => navigate('/assessment')}>
        <img src={pollIcon} alt="Опросы" className="button-icon" />
        Оценка кандидата
      </button>

      {/* Кнопка "Обратная связь" с иконкой */}
      <button className="poll-btn" onClick={() => navigate('/feedback')}>
        <img src={pollIcon} alt="Опросы" className="button-icon" />
        Обратная связь
      </button>
    </div>
  );
};

export default PollsPage;
