import React, { useEffect, useRef, useState } from 'react';
import { useNavigate }               from 'react-router-dom';
import { useSwipeable }              from 'react-swipeable';
import { motion, AnimatePresence }   from 'framer-motion';

// Подключаем все нужные стили
import './Styles/global.css';      // Montserrat, сбросы и т.п.
import './Styles/background.css';  // фон-градиент, шум, subtle-dot, pi-иконка
import './Styles/logo.css';        // ТОТ САМЫЙ logo.css, который вы разместили
import './WelcomePage.css';        // Позиционирование и базовые стили для текста

import backgroundImage from './components/background.png';
import logoImage       from './components/logo.png';
import piImage         from './components/pi.png';

const WelcomePage = () => {
  const navigate = useNavigate();

  // Флаг, что текст-приветствие должен начаться exit-анимацией
  const [textExiting, setTextExiting] = useState(false);

  // Рефы на DOM-элементы логотипа и текста
  const logoRef = useRef(null);
  const textRef = useRef(null);

  // Отдельное состояние для приветствия «Доброе утро/день/вечер»
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    // 1) Формируем текст приветствия в зависимости от времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11)       text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else                              text = 'Доброй ночи';
    setGreeting(text);

    // 2) Запускаем “entry”–анимацию логотипа:
    //    Добавляем класс `animate-logo`, чтобы сработал slideDownWrapper (см. logo.css)
    if (logoRef.current) {
      logoRef.current.classList.add('animate-logo');
    }

    // 3) Запускаем Framer Motion-анимацию текста с задержкой 500ms
    const textEntryTimer = setTimeout(() => {
      if (textRef.current) {
        // Просто для синхронизации: Framer Motion сам берёт вариативы из JSX,
        // но мы можем пометить, что текст готов к анимации
        textRef.current.classList.add('ready-for-motion');
      }
    }, 500);

    // 4) Планируем exit-анимации через 2.5 секунды:
    //    — для логотипа: удаляем animate-logo, добавляем animate-logo-exit
    //    — для текста: переключаем флаг textExiting → Framer Motion запустит exit
    const exitTimer = setTimeout(() => {
      if (logoRef.current) {
        logoRef.current.classList.remove('animate-logo');
        logoRef.current.classList.add('animate-logo-exit');
      }
      setTextExiting(true);
    }, 2500);

    // 5) Навигация на /main-menu через 3 секунды (ждём 0.5s, пока exit-анимация логотипа завершится)
    const navTimer = setTimeout(() => {
      navigate('/main-menu');
    }, 3000);

    // Очистка таймеров при размонтировании
    return () => {
      clearTimeout(textEntryTimer);
      clearTimeout(exitTimer);
      clearTimeout(navTimer);
    };
  }, [navigate]);

  // Обработчик свайпа (если нужно)
  const swipeHandlers = useSwipeable({ preventDefault: true });

  // Framer Motion-variants для текста (entry/exit)
  const textVariants = {
    initial:  { x: '-100%', opacity: 0 },
    animate:  { x: '0%', opacity: 1, transition: { duration: 0.8, ease: 'easeOut', delay: 0 } },
    exit:     { x: '100%', opacity: 0, transition: { duration: 0.5, ease: 'easeIn' } },
  };

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* 10 «subtle-dot» шариков (описаны в background.css) */}
      <div className="subtle-dot dot-1"  />
      <div className="subtle-dot dot-2"  />
      <div className="subtle-dot dot-3"  />
      <div className="subtle-dot dot-4"  />
      <div className="subtle-dot dot-5"  />
      <div className="subtle-dot dot-6"  />
      <div className="subtle-dot dot-7"  />
      <div className="subtle-dot dot-8"  />
      <div className="subtle-dot dot-9"  />
      <div className="subtle-dot dot-10" />

      {/* pi-иконка (если нужно) */}
      <div className="pi-wrapper" style={{ '--pi-move-duration': '70s' }}>
        <img
          src={piImage}
          alt="Pi"
          className="pi-fly"
          style={{ '--pi-rotate-duration': '6s' }}
        />
      </div>

      {/* Полупрозрачный оверлей (при необходимости) */}
      <div className="mainmenu-overlay" />

      {/* === ЛОГОТИП === */}
      <div
        ref={logoRef}
        className="logo-wrapper"
      >
        <img
          src={logoImage}
          alt="Логотип РГС Жизнь"
          className="logo-image"
        />
      </div>

      {/* === ТЕКСТ-ПРИВЕТСТВИЕ === */}
      <AnimatePresence>
        {!textExiting && (
          <motion.div
            ref={textRef}
            className="text-wrapper"
            variants={textVariants}
            initial="initial"
            animate="animate"
            exit="exit"
          >
            <h1 className="welcome-title">{greeting}</h1>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default WelcomePage;
