import React from 'react';
import logo from './components/logo.png';
import backgroundImage from './components/background.jpg';
import { useNavigate } from 'react-router-dom'; // Для навигации
import './PollsPage.css'

const PollsPage = () => {
  const navigate = useNavigate();

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="logo"/>
       <h2>Список опросов</h2>
      <button className="poll-btn" onClick={() => navigate('/assessment')}>Оценка кандидата</button>
      <button className="poll-btn" onClick={() => navigate('/feedback')}>Обратная связь</button>
    </div>
  );
};

export default PollsPage;
