import React, { useState } from 'react';
import IOSPicker from 'react-ios-picker'; // убедитесь, что установили пакет

const DateWheelPicker = () => {
  const [selectedDate, setSelectedDate] = useState({
    day: '01',
    month: '01',
    year: '2000'
  });

  const days = Array.from({ length: 31 }, (_, i) => (i + 1).toString().padStart(2, '0'));
  const months = Array.from({ length: 12 }, (_, i) => (i + 1).toString().padStart(2, '0'));
  const years = Array.from({ length: 100 }, (_, i) => (2025 - i).toString());

  return (
    <div>
      <IOSPicker
        items={days}
        selectedItem={selectedDate.day}
        onChange={(day) => setSelectedDate({...selectedDate, day})}
      />
      <IOSPicker
        items={months}
        selectedItem={selectedDate.month}
        onChange={(month) => setSelectedDate({...selectedDate, month})}
      />
      <IOSPicker
        items={years}
        selectedItem={selectedDate.year}
        onChange={(year) => setSelectedDate({...selectedDate, year})}
      />
    </div>
  );
};

export default DateWheelPicker;
