import React, { useState } from 'react';
import axios from 'axios';
import './RegisterPage.css';  // Импортируем стили
import { useNavigate } from 'react-router-dom';

const RegisterPage = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('');
  const navigate = useNavigate();

  // Функция обработки ввода email
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  // Функция обработки регистрации
  const handleRegister = async () => {
    setStatus('Проверка email...');

    try {
      // Отправляем email на сервер для проверки
      const response = await axios.post('http://127.0.0.1:5000/check_email', { email });

      if (response.data.isValid) {
        setStatus(`Почта проверена! Уровень доступа: ${response.data.level}`);

        // Отправляем код на email
        const sendCodeResponse = await axios.post('http://127.0.0.1:5000/send_code', { email });

        if (sendCodeResponse.data.success) {
          setStatus('Код отправлен на ваш email!');

          // Перенаправляем пользователя на страницу подтверждения кода
          setTimeout(() => {
            navigate('/verify_code', { state: { email } });
          }, 2000);
        } else {
          setStatus('Ошибка отправки кода. Попробуйте снова.');
        }
      } else {
        setStatus('Этот email не зарегистрирован в системе.');
      }
    } catch (error) {
      console.error('Ошибка при проверке email:', error);
      setStatus('Ошибка проверки email. Попробуйте снова.');
    }
  };

  // Функция обработки входа гостя
  const handleGuestLogin = () => {
    navigate('/polls'); // Переход на страницу опросов
  };

  return (
    <div className="register-container">
      <h2>Введите корпоративную почту</h2>
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Введите email"
        className="input-field"
      />
      <button className="btn" onClick={handleRegister}>Зарегистрироваться</button>
      <button className="btn gray-btn" onClick={handleGuestLogin}>Войти как гость</button>
      <p>{status}</p>
    </div>
  );
};

export default RegisterPage;
