// src/SNPPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // состояния
  const [step, setStep]                   = useState(1);
  const [email, setEmail]                 = useState('');
  const [emailError, setEmailError]       = useState('');
  const [birthDate, setBirthDate]         = useState(new Date());
  const [gender, setGender]               = useState(null);
  const [calcType, setCalcType]           = useState(null);
  const [amountRaw, setAmountRaw]         = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError]     = useState('');
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);

  // подсчет возраста
  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) age--;
    return age;
  };

  // проверка суммы для выбранного типа
  const isAmountValid = () => {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  };

  // можно ли нажать «Далее →»
  const canGoNext = () =>
    step === 2 &&
    getAge(birthDate) >= 18 &&
    Boolean(gender) &&
    Boolean(calcType) &&
    Boolean(amountRaw) &&
    isAmountValid();

  // Шаг 1: проверка почты
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    setStep(2);
  };

  // ввод суммы: raw + визуальный display
  const onAmountChange = e => {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if (calcType === 'premium' && n < 60000)      setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum' && n < 450000)    setAmountError('Мин. 450 000 ₽');
    else                                          setAmountError('');
  };

  // Шаг 2: «Далее →» — теперь GET с query string
  const handleNext = async () => {
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });

    const params = new URLSearchParams({
      email,
      birthdate: dateStr,
      age:        getAge(birthDate).toString(),
      gender,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        amountRaw
    });

    try {
      const res = await fetch(`/api/proxy/snp/save?${params.toString()}`, {
        method: 'GET'
      });

      if (!res.ok) {
        const text = await res.text();
        console.error('Ошибка сервера:', res.status, text);
        alert(`Сервер вернул ошибку ${res.status}`);
      } else if (res.headers.get('content-type')?.includes('application/json')) {
        const data = await res.json();
        setResultData(data);
      } else {
        const text = await res.text();
        console.error('Ожидали JSON, получили:', text);
        alert('Неправильный ответ сервера');
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось отправить запрос');
    } finally {
      setIsProcessing(false);
    }
  };

  const goHome = () => navigate('/MainMenu');

  // экран «Выполняется расчёт…»
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <p>Выполняется расчёт...</p>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // экран результатов
  if (resultData) {
    return (
      <div className="result-container">
        <h2>Результаты расчёта</h2>
        <p><strong>Возраст:</strong> {resultData.age} лет</p>
        <p><strong>Пол:</strong> {resultData.gender}</p>
        <p><strong>Тип расчёта:</strong> {resultData.payment}</p>
        <p><strong>Сумма:</strong> {amountDisplay} ₽</p>
        <p><strong>Итоговая премия:</strong> {resultData.totalPremium} ₽</p>
      </div>
    );
  }

  // основной UI
  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* HEADER */}
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <img src={mainmenuIcon} alt="Домой" />
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        {step === 2 && (
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      {/* Step 1 */}
      {step === 1 && (
        <>
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              placeholder="example@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          <div className="polls-buttons">
            <button
              className="polls-btn animate-btn btn1"
              onClick={handleEmailNext}
            >
              Продолжить
            </button>
          </div>
        </>
      )}

      {/* Step 2 */}
      {step === 2 && (
        <>
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>

          <div className="date-section">
            <label className="section-label">Дата рождения:</label>
            <div className="date-wheels">
              <DateWheelPicker
                value={birthDate}
                onChange={val => {
                  if (val instanceof Date) setBirthDate(val);
                  else if (val?.day)
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                }}
              />
            </div>
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">Выберите пол:</div>
          <div className="gender-section">
            <button
              className={`insurance-btn ${gender==='male'?'selected':''}`}
              onClick={()=>setGender(prev=>prev==='male'?null:'male')}
            >
              <div>Мужской</div><div className="emoji">👨</div>
            </button>
            <button
              className={`insurance-btn ${gender==='female'?'selected':''}`}
              onClick={()=>setGender(prev=>prev==='female'?null:'female')}
            >
              <div>Женский</div><div className="emoji">👩</div>
            </button>
          </div>

          <div className="section-gap" />
          <div className="section-subtitle">Расчёт от…</div>
          <div className="insurance-options">
            <button
              className={`insurance-btn ${calcType==='premium'?'selected':''}`}
              onClick={()=>setCalcType(prev=>prev==='premium'?null:'premium')}
            >
              <div>Страхового</div><div>взноса</div>
            </button>
            <button
              className={`insurance-btn ${calcType==='sum'?'selected':''}`}
              onClick={()=>setCalcType(prev=>prev==='sum'?null:'sum')}
            >
              <div>Страховой</div><div>суммы</div>
            </button>
          </div>

          {calcType && (
            <>
              <div className="section-gap" />
              <div className="section-subtitle">Сумма (₽):</div>
              <div className="sum-section">
                <input
                  type="text"
                  placeholder={calcType==='premium'?'Мин. 60 000':'Мин. 450 000'}
                  value={amountDisplay}
                  onChange={onAmountChange}
                />
              </div>
              {amountError && <div className="input-error">{amountError}</div>}
            </>
          )}
        </>
      )}
    </div>
  );
}
