import React, { useState, useEffect } from 'react';
// Импорт иконок – проверьте пути согласно структуре вашего проекта
import mainmenuIcon from './components/icons/mainmenu.png';
import logoImage from './components/icons/Logo.png';

import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './JustincasePage.css';

const JustincasePage = () => {
  // 1) Общее состояние пользователя
  const [userName, setUserName] = useState('');
  useEffect(() => {
    const storedName = localStorage.getItem('userName');
    setUserName(storedName || 'Гость');
  }, []);

  // 2) Собираемые данные
  const [birthDate, setBirthDate] = useState(null);         // формат "dd/mm/yyyy"
  const [gender, setGender] = useState(null);               // 'male' / 'female'
  const [insuranceInfo, setInsuranceInfo] = useState(null);   // 'yes' / 'no'
  const [insuranceTerm, setInsuranceTerm] = useState('1');    // срок страхования (лет)
  const [insuranceSum, setInsuranceSum] = useState('');       // форматированная сумма
  const [insuranceFrequency, setInsuranceFrequency] = useState(''); // периодичность оплаты

  // Ветка "Да" – пакеты
  const [accidentPackage, setAccidentPackage] = useState(null); // 'yes' / 'no'
  const [criticalPackage, setCriticalPackage] = useState(null); // 'yes' / 'no'
  const [treatmentRegion, setTreatmentRegion] = useState(null);   // 'russia' / 'abroad'
  const [sportPackage, setSportPackage] = useState(null);         // 'yes' / 'no'

  // Ветка "Нет" – вопросы о работе и доходах
  const [hasJob, setHasJob] = useState(null);
  const [income2021, setIncome2021] = useState('');
  const [income2022, setIncome2022] = useState('');
  const [income2023, setIncome2023] = useState('');
  const [scholarship, setScholarship] = useState('');
  const [unsecuredLoans, setUnsecuredLoans] = useState('');

  // Новые состояния для дополнительного шага (при insuranceInfo === 'no')
  const [breadwinnerStatus, setBreadwinnerStatus] = useState(null); // "yes", "no", "notMe"
  const [incomeShare, setIncomeShare] = useState('');
  const [childrenCount, setChildrenCount] = useState('0');
  const [specialCareRelatives, setSpecialCareRelatives] = useState(null); // 'yes' / 'no'

  // 3) Шаги:
  // Шаг 1 – дата рождения, пол, знание параметров страхового полиса.
  // Шаг 2 – если insuranceInfo === 'yes': детали страхования, иначе: вопросы о работе и доходах.
  // Шаг 3 – если insuranceInfo === 'yes': вопросы по пакетам, иначе: дополнительные вопросы про семейный бюджет.
  // Шаг 4 – результат расчёта (после сохранения в Excel)
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  // Новые состояния для процесса расчёта
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData] = useState(null);

  // ---------- Вспомогательные функции ----------
  const parseDate = (dateStr) => {
    const [day, month, year] = dateStr.split('/');
    return new Date(+year, +month - 1, +day);
  };

  const calculateAge = (dateObj) => {
    const today = new Date();
    let age = today.getFullYear() - dateObj.getFullYear();
    const m = today.getMonth() - dateObj.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < dateObj.getDate())) {
      age--;
    }
    return age;
  };

  // Форматирование суммы (например "1.000.000")
  const formatSum = (rawValue) => {
    let digits = rawValue.replace(/\D/g, '');
    if (!digits) return '';
    let result = '';
    while (digits.length > 3) {
      const chunk = digits.slice(-3);
      digits = digits.slice(0, -3);
      result = '.' + chunk + result;
    }
    result = digits + result;
    return result;
  };

  const handleSumChange = (e) => {
    setInsuranceSum(formatSum(e.target.value));
  };

  // Обработчики для деталей страхования (ветка "Да")
  const handleTermChange = (e) => {
    setInsuranceTerm(e.target.value);
  };
  const handleFrequencyChange = (e) => {
    setInsuranceFrequency(e.target.value);
  };

  // Обработчики для ветки "Нет" (доходы)
  const handleIncome2021Change = (e) => {
    setIncome2021(formatSum(e.target.value));
  };
  const handleIncome2022Change = (e) => {
    setIncome2022(formatSum(e.target.value));
  };
  const handleIncome2023Change = (e) => {
    setIncome2023(formatSum(e.target.value));
  };
  const handleScholarshipChange = (e) => {
    setScholarship(formatSum(e.target.value));
  };
  const handleUnsecuredLoansChange = (e) => {
    setUnsecuredLoans(formatSum(e.target.value));
  };

  // Логирование изменения treatmentRegion для отладки
  useEffect(() => {
    console.log("treatmentRegion обновлён:", treatmentRegion);
  }, [treatmentRegion]);

  // Здесь не устанавливаем автоматически значение для treatmentRegion,
  // чтобы по умолчанию ни одна из кнопок не была выбрана.

  // ---------- Функция для проверки, можно ли перейти к следующему шагу ----------
  const canGoNext = () => {
    if (currentStep === 1) {
      return birthDate && gender && insuranceInfo;
    } else if (currentStep === 2) {
      if (insuranceInfo === 'yes') {
        return insuranceTerm && insuranceSum && insuranceFrequency;
      } else {
        return hasJob && income2021 && income2022 && income2023;
      }
    } else if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        return accidentPackage && criticalPackage && (criticalPackage === 'no' || treatmentRegion) && sportPackage;
      } else {
        return breadwinnerStatus &&
               (breadwinnerStatus === 'yes' || incomeShare) &&
               childrenCount !== '' &&
               specialCareRelatives;
      }
    } else {
      return true;
    }
  };

  // ---------- Переходы между шагами ----------
  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    if (currentStep === 1) {
      setCurrentStep(2);
    } else if (currentStep === 2) {
      setCurrentStep(3);
    } else if (currentStep === 3) {
      // Здесь не устанавливаем автоматически treatmentRegion – ждём выбора пользователя
      setIsProcessing(true);
      const calculatorData = {
        birthDate,
        gender,
        insuranceTerm,
        insuranceSum,
        insuranceFrequency,
        accidentPackage,
        criticalPackage,
        treatmentRegion,
        sportPackage,
        // для ветки "Нет"
        hasJob,
        income2021,
        income2022,
        income2023,
        scholarship,
        unsecuredLoans,
        breadwinnerStatus,
        incomeShare,
        childrenCount,
        specialCareRelatives
      };
      console.log("Отправляем данные калькулятора:", calculatorData);
      fetch('/api/proxy/calculator/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(calculatorData),
        credentials: 'include'
      })
        .then(response => response.json())
        .then(data => {
          setResultData(data);
          setIsProcessing(false);
          setCurrentStep(4);
        })
        .catch(err => {
          console.error("Ошибка сохранения данных калькулятора:", err);
          setIsProcessing(false);
          alert("Ошибка сохранения данных калькулятора");
        });
    }
  };

  const handlePrev = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  // Переход в главное меню
  const goToMenu = () => {
    window.location.href = '/menu';
  };

  // Повторить расчёт
  const repeatCalculation = () => {
    setBirthDate(null);
    setGender(null);
    setInsuranceInfo(null);
    setInsuranceTerm('1');
    setInsuranceSum('');
    setInsuranceFrequency('');
    setAccidentPackage(null);
    setCriticalPackage(null);
    setTreatmentRegion(null);
    setSportPackage(null);
    setHasJob(null);
    setIncome2021('');
    setIncome2022('');
    setIncome2023('');
    setScholarship('');
    setUnsecuredLoans('');
    setBreadwinnerStatus(null);
    setIncomeShare('');
    setChildrenCount('0');
    setSpecialCareRelatives(null);
    setResultData(null);
    setIsProcessing(false);
    setCurrentStep(1);
  };

  // ---------- Рендер шагов ----------
  const renderStep = () => {
    if (isProcessing) {
      return (
        <div className="processing-container">
          <h2>{userName}, производим расчет...</h2>
          <div className="loading-spinner">
            <svg width="50" height="50" viewBox="0 0 50 50">
              <circle cx="25" cy="25" r="20" stroke="#f00" strokeWidth="5" fill="none" strokeLinecap="round">
                <animateTransform attributeName="transform" type="rotate" dur="1s" from="0 25 25" to="360 25 25" repeatCount="indefinite" />
              </circle>
            </svg>
          </div>
        </div>
      );
    }
    if (currentStep === 4 && resultData) {
      return (
        <div className="result-container">
          <h2>Ваша программа «На всякий случай» (расчет от {resultData.calculationDate})</h2>
          <p>Возраст клиента: {resultData.clientAge} лет</p>
          <p>Пол клиента: {resultData.clientGender}</p>
          <p>Срок страхования: {resultData.insuranceTerm} лет</p>
          <hr />
          <h3>Основная программа (страхование на случай ухода из жизни и инвалидности I и II группы):</h3>
          <p>Страховая сумма: {resultData.baseInsuranceSum}</p>
          <p>Страховая премия: {resultData.basePremium} руб.</p>
          {resultData.accidentPackageIncluded && (
            <>
              <hr />
              <h3>Пакет «Несчастный случай»:</h3>
              <p>Страховая сумма: {resultData.accidentInsuranceSum}</p>
              <p>Страховая премия: {resultData.accidentPremium} руб.</p>
            </>
          )}
          {resultData.criticalPackageIncluded && (
            <>
              <hr />
              <h3>Пакет «Критические заболевания»:</h3>
              <p>• Максимальная страховая сумма 60 000 000 рублей, дополнительно по реабилитации – 100 000 рублей.</p>
              <p>Страховая премия: {resultData.criticalPremium} руб.</p>
            </>
          )}
          {resultData.sportOptionIncluded && (
            <>
              <hr />
              <h3>Опция «Любительский спорт»:</h3>
              <p>Включено</p>
            </>
          )}
          <hr />
          <p><strong>Итого страховая премия:</strong> {resultData.totalPremium} руб.</p>
          <p><strong>Порядок оплаты премии:</strong> {resultData.paymentMethod}</p>
          <hr />
          <p>
            Для дополнительной консультации и оформления программы свяжитесь с нашим менеджером:&nbsp;
            <a href="/contact">Наш менеджер</a>
          </p>
        </div>
      );
    }
    if (currentStep === 1) {
      return (
        <>
          <div className="form-group">
            <label className="form-label">Дата рождения:</label>
            <DateWheelPicker onChange={(dateString) => setBirthDate(dateString)} />
          </div>
          <div className="form-group">
            <label className="form-label">Пол:</label>
            <div className="gender-selector">
              <div className={`gender-option ${gender === 'male' ? 'selected' : ''}`} onClick={() => setGender('male')}>
                <div className="gender-label">Мужской</div>
                <div className="gender-icon">👨</div>
              </div>
              <div className={`gender-option ${gender === 'female' ? 'selected' : ''}`} onClick={() => setGender('female')}>
                <div className="gender-label">Женский</div>
                <div className="gender-icon">👩</div>
              </div>
            </div>
          </div>
          <div className="form-group">
            <label className="form-label">
              Знаете ли Вы на какой срок и на какую сумму оформить страховой полис?
            </label>
            <div className="insurance-options">
              <button className={`insurance-btn ${insuranceInfo === 'yes' ? 'selected' : ''}`} onClick={() => setInsuranceInfo('yes')}>
                Да
              </button>
              <button className={`insurance-btn ${insuranceInfo === 'no' ? 'selected' : ''}`} onClick={() => setInsuranceInfo('no')}>
                Нет
              </button>
            </div>
          </div>
        </>
      );
    } else if (currentStep === 2) {
      if (insuranceInfo === 'yes') {
        return (
          <>
            <div className="form-group">
              <label className="form-label">Выберите срок страхования (лет):</label>
              <select className="insurance-term-select" value={insuranceTerm} onChange={handleTermChange}>
                {Array.from({ length: 30 }, (_, i) => i + 1).map((year) => (
                  <option key={year} value={year}>
                    {year}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label className="form-label">Введите страховую сумму (от 1 млн руб):</label>
              <input type="text" className="insurance-sum-input" placeholder="1.000.000" value={insuranceSum} onChange={handleSumChange} />
            </div>
            <div className="form-group">
              <label className="form-label">Периодичность оплаты страховой премии:</label>
              <select className="insurance-frequency-select" value={insuranceFrequency} onChange={handleFrequencyChange}>
                <option value="">Выберите период</option>
                {["Ежемесячно", "Ежегодно", "раз в пол года", "ежеквартально"].map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))}
              </select>
            </div>
          </>
        );
      } else {
        return (
          <>
            <div className="form-group">
              <label className="form-label">Есть работа?</label>
              <div className="insurance-options">
                <button className={`insurance-btn ${hasJob === 'yes' ? 'selected' : ''}`} onClick={() => setHasJob('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${hasJob === 'no' ? 'selected' : ''}`} onClick={() => setHasJob('no')}>
                  Нет
                </button>
              </div>
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">Доход 2021, руб.:</label>
              <input type="text" className="inline-input" placeholder="Введите сумму" value={income2021} onChange={handleIncome2021Change} />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">Доход 2022, руб.:</label>
              <input type="text" className="inline-input" placeholder="Введите сумму" value={income2022} onChange={handleIncome2022Change} />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">Доход 2023, руб.:</label>
              <input type="text" className="inline-input" placeholder="Введите сумму" value={income2023} onChange={handleIncome2023Change} />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">Стипендия 2023, руб.:</label>
              <input type="text" className="inline-input" placeholder="Введите сумму" value={scholarship} onChange={handleScholarshipChange} />
            </div>
            <div className="form-group inline-group">
              <label className="inline-label">Кредиты, руб.:</label>
              <input type="text" className="inline-input" placeholder="Введите сумму" value={unsecuredLoans} onChange={handleUnsecuredLoansChange} />
            </div>
          </>
        );
      }
    } else if (currentStep === 3) {
      if (insuranceInfo === 'yes') {
        return (
          <>
            <div className="form-group">
              <label className="form-label">
                Подключаем пакет «Несчастный случай»?
              </label>
              <div className="insurance-options">
                <button className={`insurance-btn ${accidentPackage === 'yes' ? 'selected' : ''}`} onClick={() => setAccidentPackage('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${accidentPackage === 'no' ? 'selected' : ''}`} onClick={() => setAccidentPackage('no')}>
                  Нет
                </button>
              </div>
            </div>
            <div className="form-group">
              <label className="form-label">
                Подключаем пакет «Критические заболевания»?
              </label>
              <div className="insurance-options">
                <button className={`insurance-btn ${criticalPackage === 'yes' ? 'selected' : ''}`} onClick={() => setCriticalPackage('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${criticalPackage === 'no' ? 'selected' : ''}`} onClick={() => setCriticalPackage('no')}>
                  Нет
                </button>
              </div>
            </div>
            {criticalPackage === 'yes' && (
              <div className="form-group">
                <label className="form-label">
                  Какой регион лечения подключаем?
                </label>
                <div className="insurance-options">
                  <button className={`insurance-btn ${treatmentRegion === 'russia' ? 'selected' : ''}`} onClick={() => setTreatmentRegion('russia')}>
                    Лечение в РФ
                  </button>
                  <button className={`insurance-btn ${treatmentRegion === 'abroad' ? 'selected' : ''}`} onClick={() => setTreatmentRegion('abroad')}>
                    Лечение за рубежом
                  </button>
                </div>
              </div>
            )}
            <div className="form-group">
              <label className="form-label">
                Подключаем опцию «Любительский спорт»?
              </label>
              <div className="insurance-options">
                <button className={`insurance-btn ${sportPackage === 'yes' ? 'selected' : ''}`} onClick={() => setSportPackage('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${sportPackage === 'no' ? 'selected' : ''}`} onClick={() => setSportPackage('no')}>
                  Нет
                </button>
              </div>
            </div>
          </>
        );
      } else {
        return (
          <>
            <div className="form-group">
              <label className="form-label">Вы единственный кормилец в семье?</label>
              <div className="insurance-options">
                <button className={`insurance-btn ${breadwinnerStatus === 'yes' ? 'selected' : ''}`} onClick={() => setBreadwinnerStatus('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${breadwinnerStatus === 'no' ? 'selected' : ''}`} onClick={() => setBreadwinnerStatus('no')}>
                  Нет
                </button>
                <button className={`insurance-btn ${breadwinnerStatus === 'notMe' ? 'selected' : ''}`} onClick={() => setBreadwinnerStatus('notMe')}>
                  Не являюсь кормилцем
                </button>
              </div>
            </div>
            {breadwinnerStatus !== 'yes' && (
              <div className="form-group">
                <label className="form-label">Доля вашего дохода в семейном бюджете:</label>
                <select className="insurance-term-select" value={incomeShare} onChange={(e) => setIncomeShare(e.target.value)}>
                  <option value="">Выберите долю</option>
                  <option value="до 10%">до 10%</option>
                  <option value="10-24%">10–24%</option>
                  <option value="25-49%">25–49%</option>
                  <option value="50-74%">50–74%</option>
                  <option value="75-89%">75–89%</option>
                  <option value="Более 90%">Более 90%</option>
                </select>
              </div>
            )}
            <div className="form-group">
              <label className="form-label">Количество детей на иждивении?</label>
              <select className="insurance-term-select" value={childrenCount} onChange={(e) => setChildrenCount(e.target.value)}>
                <option value="0">0</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3 и более">3 и более</option>
              </select>
            </div>
            <div className="form-group">
              <label className="form-label">Есть ли родственники, требующие особого ухода?</label>
              <div className="insurance-options">
                <button className={`insurance-btn ${specialCareRelatives === 'yes' ? 'selected' : ''}`} onClick={() => setSpecialCareRelatives('yes')}>
                  Да
                </button>
                <button className={`insurance-btn ${specialCareRelatives === 'no' ? 'selected' : ''}`} onClick={() => setSpecialCareRelatives('no')}>
                  Нет
                </button>
              </div>
            </div>
          </>
        );
      }
    }
    return null;
  };

  return (
    <div className="justincase-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* На шагах, отличных от 4, отображаем логотип в верхнем центре */}
      {currentStep !== 4 && (
        <div className="logo-container">
          <img src={logoImage} alt="Logo" className="logo-image" />
        </div>
      )}
      {/* На шаге 4, логотип по центру вверху в стиле Feedback */}
      {currentStep === 4 && (
        <div className="logo-container logo-container-final">
          <img src={logoImage} alt="Logo" className="logo-image" />
        </div>
      )}
      {currentStep !== 4 && (
        <h1 className="justincase-title">
          Расчет по программе "На всякий случай" для сотрудника
        </h1>
      )}
      <div className="justincase-form">{renderStep()}</div>

      {/* Кнопка "Меню" на первом шаге (слева вверху) */}
      {currentStep === 1 && (
        <div className="back-btn-container">
          <button className="back-btn mainmenu-btn" onClick={goToMenu}>
            <img src={mainmenuIcon} alt="Menu" style={{ width: '24px', height: '24px' }} />
          </button>
        </div>
      )}

      {/* Стандартная кнопка "Назад" для шагов 2-3 */}
      {currentStep > 1 && currentStep < 4 && (
        <div className="back-btn-container">
          <button className="back-btn" onClick={handlePrev}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
              <path d="M12 20l-8-8 8-8" />
            </svg>
          </button>
        </div>
      )}

      {/* Кнопка "Далее" или "Завершить" для шагов 1-3 */}
      {currentStep < 3 && (
        <div className="top-btn-container">
          <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
              <path d="M12 4l8 8-8 8" />
            </svg>
          </button>
        </div>
      )}
      {currentStep === 3 && (
        <div className="top-btn-container">
          <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
              <path d="M5 13l4 4L19 7" />
            </svg>
          </button>
        </div>
      )}

      {/* Кнопки на странице результата (шаг 4) */}
      {currentStep === 4 && (
        <>
          <div className="back-btn-container">
            <button className="back-btn mainmenu-btn" onClick={goToMenu}>
              <img src={mainmenuIcon} alt="Menu" style={{ width: '24px', height: '24px' }} />
            </button>
          </div>
          <div className="top-btn-container">
            <button className="top-btn" onClick={repeatCalculation}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                <path d="M17.65 6.35A7.95 7.95 0 0012 4a8 8 0 00-8 8h2.08a5.92 5.92 0 015.92-6 6 6 0 015.65 3.65L13 11h7V4l-2.35 2.35zM6.35 17.65A7.95 7.95 0 0012 20a8 8 0 008-8h-2.08a5.92 5.92 0 01-5.92 6 6 6 0 01-5.65-3.65L3.41 16.3 2 17.71l2.35-2.35z" />
              </svg>
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default JustincasePage;
