// src/SNPPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // === —ç—Ç–∞–ø—ã: email ‚Üí form ‚Üí result ‚Üí manager
  const [step, setStep] = useState('email');

  // === –æ–±—â–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è ===
  const [email,        setEmail]        = useState('');
  const [emailError,   setEmailError]   = useState('');

  const [birthDateParts, setBirthDateParts] = useState({ day: null, month: null, year: null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [slideIndex,     setSlideIndex]     = useState(0);

  // —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –º–µ–Ω–µ–¥–∂–µ—Ä–∞
  const [mgrSurname,    setMgrSurname]    = useState('');
  const [mgrName,       setMgrName]       = useState('');
  const [mgrCity,       setMgrCity]       = useState('');
  const [mgrError,      setMgrError]      = useState('');

  // –∫–æ–Ω—Å—Ç–∞–Ω—Ç—ã
  const term        = 5;
  const periodicity = '–µ–∂–µ–≥–æ–¥–Ω–æ';
  const currency    = '—Ä—É–±–ª–µ–π';

  // —Å–±–æ—Ä–∫–∞ Date –∏–∑ birthDateParts
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m-1, d);
      if (!isNaN(dt) && dt.getDate()===d && dt.getMonth()+1===m && dt.getFullYear()===y) {
        setBirthDate(dt);
      } else setBirthDate(null);
    } else setBirthDate(null);
  }, [birthDateParts]);

  // —Ñ–æ—Ä–º–∞—Ç —Å—É–º–º—ã
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // –≤–≤–æ–¥ —Å—É–º–º—ã
  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10) || 0;
    if (calcType==='premium')    setAmountError(num<60000?'–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ':'');
    else if (calcType==='sum')   setAmountError(num<450000?'–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ':'' );
  };

  // –≤–æ–∑—Ä–∞—Å—Ç
  const getAge = d => {
    if (!(d instanceof Date)) return 0;
    const t = new Date();
    let age = t.getFullYear() - d.getFullYear();
    const m = t.getMonth() - d.getMonth();
    if (m<0 || (m===0 && t.getDate()<d.getDate())) age--;
    return age;
  };

  // –≤–∞–ª–∏–¥–∞—Ü–∏—è –ø–µ—Ä–µ–¥ —Ä–∞—Å—á—ë—Ç–æ–º
  const canGoNext = () =>
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender && calcType && amount && !amountError &&
    getAge(birthDate)>=18;

  // –æ—Ç–ø—Ä–∞–≤–∫–∞ —Ä–∞—Å—á—ë—Ç–∞
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{
      day:'2-digit', month:'2-digit', year:'numeric'
    });
    const age = getAge(birthDate);
    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType==='premium'?'–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞':'–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res  = await fetch('/api/snp/save',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      } else {
        console.error(data);
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // —Ç–µ–∫—É—â–∞—è –¥–∞—Ç–∞
  const todayStr = new Date().toLocaleDateString('ru-RU',{
    day:'2-digit', month:'2-digit', year:'numeric'
  });

  // —Å–ª–∞–π–¥—ã
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">–í –¢–ï–ß–ï–ù–ò–ï {term} –õ–ï–¢</div>
      <div className="slide-title">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞ –≤ –ø—Ä–æ–≥—Ä–∞–º–º—É</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ‚ÇΩ</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">–ù–ê–ß–ò–°–õ–Ø–ï–ú –î–û–ü–û–õ–ù–ò–¢–ï–õ–¨–ù–û</div>
      <div className="slide-title">{resultData.accrualPercent}% –æ—Ç –∫–∞–∂–¥–æ–≥–æ –≤–∑–Ω–æ—Å–∞</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–û–±—â–∞—è —Å—É–º–º–∞ –Ω–∞—á–∏—Å–ª–µ–Ω–∏–π</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ‚ÇΩ</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í–´ –ü–û–õ–£–ß–ê–ï–¢–ï:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {term} –ª–µ—Ç</div>
    </div>
  ] : [];

  // –ø—Ä–æ–≤–µ—Ä–∫–∞ email –∏ –ø–µ—Ä–µ—Ö–æ–¥ –∫ —Ñ–æ—Ä–º–µ
  const handleEmailNext = () => {
    if (!email.includes('@vtb.ru')) {
      setEmailError('–î–æ–º–µ–Ω –≤–∞—à–µ–π –ø–æ—á—Ç—ã –Ω–µ —è–≤–ª—è–µ—Ç—Å—è –∫–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω—ã–º. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  // –æ—Ç–ø—Ä–∞–≤–∫–∞ –º–µ–Ω–µ–¥–∂–µ—Ä—É (–º–æ–∂–µ—Ç–µ —Ä–∞—Å—à–∏—Ä–∏—Ç—å –ª–æ–≥–∏–∫—É)
  const handleManagerSubmit = () => {
    if (!mgrSurname || !mgrName || !mgrCity) {
      setMgrError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è.');
    } else {
      setMgrError('');
      // –∑–¥–µ—Å—å –º–æ–∂–Ω–æ –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –Ω–∞ —Å–µ—Ä–≤–µ—Ä –≤–º–µ—Å—Ç–µ —Å email
      console.log({ email, mgrSurname, mgrName, mgrCity });
      // –≤–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –≤ –º–µ–Ω—é
      navigate('/Main-Menu');
    }
  };

  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>

      {/* –î–æ–º–æ–π –∏ –õ–æ–≥–æ—Ç–∏–ø */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/Main-Menu')}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
        </button>
      </div>
      <div className="logo-container">
        <img src={logoImage} alt="–õ–æ–≥–æ" className="logo-image"/>
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"</h1>

      {/* === –®–ê–ì 1: Email === */}
      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–ö–æ—Ä–ø–æ—Ä–∞—Ç–∏–≤–Ω–∞—è –ø–æ—á—Ç–∞ –í–¢–ë:</label>
            <input
              type="email"
              className="insurance-sum-input"
              value={email}
              onChange={e=>setEmail(e.target.value)}
              placeholder="name@vtb.ru"
            />
            {emailError && <div className="input-error-message">{emailError}</div>}
          </div>
          <button
            className="insurance-btn selected"
            onClick={handleEmailNext}
          >
            –ü–µ—Ä–µ–π—Ç–∏ –∫ —Ä–∞—Å—á–µ—Ç—É
          </button>
        </div>
      )}

      {/* === –®–ê–ì 2: –§–æ—Ä–º–∞ —Ä–∞—Å—á—ë—Ç–∞ === */}
      {step==='form' && !isProcessing && !resultData && (
        <div className="snp-form">
          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val => {
                setBirthDateParts(prev => {
                  if (val instanceof Date) {
                    return {
                      day:   val.getDate().toString().padStart(2,'0'),
                      month: (val.getMonth()+1).toString().padStart(2,'0'),
                      year:  val.getFullYear().toString()
                    };
                  }
                  if (Array.isArray(val) && val.length===3) {
                    const [d,m,y] = val;
                    return { day:d, month:m, year:y };
                  }
                  if (val && typeof val==='object') {
                    return {
                      ...prev,
                      ...(val.day   != null ? { day:   String(val.day).padStart(2,'0')   } : {}),
                      ...(val.month != null ? { month: String(val.month).padStart(2,'0') } : {}),
                      ...(val.year  != null ? { year:  String(val.year)                } : {})
                    };
                  }
                  if (typeof val==='string') {
                    if (val.includes('.')) {
                      const [d,m,y] = val.split('.');
                      return { day:d.padStart(2,'0'), month:m.padStart(2,'0'), year:y };
                    }
                    if (val.includes('-')) {
                      const [y,m,d] = val.split('-');
                      return { day:d.padStart(2,'0'), month:m.padStart(2,'0'), year:y };
                    }
                  }
                  return prev;
                });
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              {['–º—É–∂—Å–∫–æ–π','–∂–µ–Ω—Å–∫–∏–π'].map(v=>(
                <div
                  key={v}
                  className={`gender-option ${gender===v?'selected':''}`}
                  onClick={()=>setGender(v)}
                >
                  <div className="gender-label">{v==='–º—É–∂—Å–∫–æ–π'?'–ú—É–∂—Å–∫–æ–π':'–ñ–µ–Ω—Å–∫–∏–π'}</div>
                  <div className="gender-icon">{v==='–º—É–∂—Å–∫–æ–π'?'üë®':'üë©'}</div>
                </div>
              ))}
            </div>
          </div>

          {/* –†–∞—Å—á–µ—Ç –æ—Ç */}
          <div className="form-group">
            <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                onClick={()=>{ setCalcType('premium'); setAmount(''); setAmountError(''); }}
              >–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞</button>
              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={()=>{ setCalcType('sum'); setAmount(''); setAmountError(''); }}
              >–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã</button>
            </div>
          </div>

          {/* –°—É–º–º–∞ */}
          {calcType && (
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType==='premium'?'–ú–∏–Ω–∏–º—É–º 60 000':'–ú–∏–Ω–∏–º—É–º 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}
        </div>
      )}

      {/* === –®–ê–ì: PROCESSING === */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* === –®–ê–ì 3: –†–ï–ó–£–õ–¨–¢–ê–¢ –∏ –°–õ–ê–ô–î–ï–† === */}
      {step==='result' && !isProcessing && resultData && (
        <>
          <div className="result-container">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
            <p><strong>–î–∞—Ç–∞ —Ä–∞—Å—á—ë—Ç–∞:</strong> {todayStr}</p>
            <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
            <p><strong>–í–æ–∑—Ä–∞—Å—Ç –∑–∞—Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–Ω–æ–≥–æ:</strong> {resultData.age} –ª–µ—Ç</p>
            <p><strong>–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è:</strong> {term} –ª–µ—Ç</p>
            <p><strong>–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –≤–∑–Ω–æ—Å–æ–≤:</strong> {periodicity}</p>
            <p><strong>–í–∞–ª—é—Ç–∞:</strong> {currency}</p>
          </div>

          <div className="detail-slider-container">
            {/* –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä—ã */}
            <div className="slider-indicators">
              {[0,1,2].map(i=>(
                <div key={i} className={`slider-dot ${slideIndex===i?'active':''}`}/>
              ))}
            </div>
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 && <button className="nav-btn left"  onClick={()=>setSlideIndex(si=>si-1)}>‚Üê</button>}
              {slideIndex<slides.length-1 && <button className="nav-btn right" onClick={()=>setSlideIndex(si=>si+1)}>‚Üí</button>}
            </div>
          </div>

          {/* –ö–Ω–æ–ø–∫–∞ —Å–≤—è–∑–∞—Ç—å—Å—è —Å –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º */}
          <button className="insurance-btn selected" onClick={()=>setStep('manager')}>
            –°–≤—è–∑–∞—Ç—å—Å—è —Å –º–µ–Ω–µ–¥–∂–µ—Ä–æ–º
          </button>
        </>
      )}

      {/* === –®–ê–ì 4: –§–æ—Ä–º–∞ –º–µ–Ω–µ–¥–∂–µ—Ä–∞ === */}
      {step==='manager' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">–§–∞–º–∏–ª–∏—è:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrSurname}
              onChange={e=>setMgrSurname(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">–ò–º—è:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrName}
              onChange={e=>setMgrName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">–ì–æ—Ä–æ–¥ —Ä–∞–±–æ—Ç—ã:</label>
            <input
              type="text"
              className="insurance-sum-input"
              value={mgrCity}
              onChange={e=>setMgrCity(e.target.value)}
            />
          </div>
          {mgrError && <div className="input-error-message">{mgrError}</div>}
          <button className="insurance-btn selected" onClick={handleManagerSubmit}>
            –û—Ç–ø—Ä–∞–≤–∏—Ç—å –º–µ–Ω–µ–¥–∂–µ—Ä—É
          </button>
        </div>
      )}

    </div>
  );
}
