import React, { useEffect, useState, useLayoutEffect } from 'react';
import { useSwipeable } from 'react-swipeable';
import { useNavigate } from 'react-router-dom';
import backgroundImage from './components/background.png';
import assessmentQR from './components/QR/assessment.png';
import './PollsPage.css';

const PollsPage = () => {
  const navigate = useNavigate();
  const [qrModal, setQrModal] = useState(null);
  const [qrTitle, setQrTitle] = useState('');
  const [isQRLoading, setIsQRLoading] = useState(true);

  useLayoutEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    setTimeout(() => window.scrollTo(0, 0), 100);
  }, []);

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => navigate('/register'),
  });

  const user = {
    isRegistered: true,
    accessLevel: 1,
  };

  const getQRCodeUrl = (targetUrl) => {
    return `https://quickchart.io/qr?text=${encodeURIComponent(targetUrl)}&size=200`;
  };

  const polls = [
    { label: 'Оценка кандидата', path: '/assessment' },
    { label: 'Обратная связь', path: '/feedback' },
    { label: 'Зачем продавать маржа?', path: '/marzapoll' }
  ];

  const handleQRClick = (poll) => {
    const fullUrl = window.location.origin + poll.path;
    setQrTitle(poll.label);
    setIsQRLoading(true);
    setTimeout(() => setQrModal(getQRCodeUrl(fullUrl)), 100);
  };

  return (
    <div className="container-polls" style={{ backgroundImage: `url(${backgroundImage})` }} {...swipeHandlers}>
      <h2>Список опросов</h2>

      {polls.map((poll, index) => (
        <div key={index} className="poll-container">
          <button className="poll-btn" onClick={() => navigate(poll.path)}>
            <span className="button-text">{poll.label}</span>
          </button>
          {user.isRegistered && user.accessLevel === 1 && (
            <button className="qr-btn" onClick={() => handleQRClick(poll)}>
              <img src={assessmentQR} alt="QR-код" className="qr-code-img" />
            </button>
          )}
        </div>
      ))}

      {qrModal && (
        <div className="qr-modal-overlay" onClick={() => setQrModal(null)}>
          <div className="qr-modal-content">
            <h3 className="qr-title">{qrTitle}</h3>
            <div className="qr-image-wrapper">
              {isQRLoading && (
                <svg className="spinner" viewBox="0 0 50 50">
                  <circle className="path" cx="25" cy="25" r="20" fill="none" strokeWidth="5"/>
                </svg>
              )}
              <img src={qrModal} alt="QR Code" className="qr-image" onLoad={() => setIsQRLoading(false)} style={isQRLoading ? { display: 'none' } : {}}/>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PollsPage;
