// src/WelcomePage.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable';

import backgroundImage from './components/background.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [animateLogo, setAnimateLogo] = useState(false);
  const [animateText, setAnimateText] = useState(false);
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    // 1) Выбираем приветствие по времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11)       text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else                               text = 'Доброй ночи';
    setGreeting(text);

    // 2) Через 100ms добавляем класс animate-logo
    const logoTimer = setTimeout(() => setAnimateLogo(true), 100);

    // 3) Через 500ms добавляем класс animate-text
    const textTimer = setTimeout(() => setAnimateText(true), 500);

    // 4) Через 3000ms переходим на /main-menu
    const navTimer = setTimeout(() => navigate('/main-menu'), 1000000);

    return () => {
      clearTimeout(logoTimer);
      clearTimeout(textTimer);
      clearTimeout(navTimer);
    };
  }, [navigate]);

  const swipeHandlers = useSwipeable({ preventDefault: true });

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/*
        1) Логотип «из CSS» теперь содержит в себе
           рамку (полупрозрачный фон + тень + border-radius)
           и тег <img>, который поверх рамки отображает всю картинку целиком.
        2) Когда `animateLogo === true`, добавляется класс `.animate-logo`,
           и рамка + <img> одновременно «выезжают» сверху (через slideDownLogo).
      */}
      <div className={`logo-wrapper ${animateLogo ? 'animate-logo' : ''}`}>
        <img
          className="logo-image"
          src={require('./components/logo.png')}
          alt="Логотип РГС Жизнь"
        />
      </div>

      {/* Текст-приветствие «выплывает» снизу */}
      <div className={`text-wrapper ${animateText ? 'animate-text' : ''}`}>
        <h1 className="welcome-title">{greeting}</h1>
      </div>
    </div>
  );
};

export default WelcomePage;
