// src/CareFuturePage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

// Логотип и фон — те же, что в SNPPage
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './CareFuturePage.css';

export default function CareFuturePage() {
  const navigate = useNavigate();

  // ===== Состояния =====
  const [step,           setStep]           = useState('email');   // email → form → processing → result → manager
  const [email,          setEmail]          = useState('');
  const [emailError,     setEmailError]     = useState('');
  const [birthDateParts, setBirthDateParts] = useState({ day:null, month:null, year:null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [programTerm,    setProgramTerm]    = useState(5);
  const [termError,      setTermError]      = useState('');
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [mgrSurname,     setMgrSurname]     = useState('');
  const [mgrName,        setMgrName]        = useState('');
  const [mgrCity,        setMgrCity]        = useState('');
  const [mgrError,       setMgrError]       = useState('');
  const [slideIndex,     setSlideIndex]     = useState(0);

  // === Константы ===
  const periodicity = 'ежегодно';
  const minAmount   = 100000; // минимальная сумма

  // === Эффект: собираем Date из birthDateParts ===
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (
        !isNaN(dt.getTime()) &&
        dt.getDate()     === d &&
        dt.getMonth()+1  === m &&
        dt.getFullYear() === y
      ) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // === Хелпер: формат “100000” → “100.000” ===
  const formatSum = raw => {
    let digits = raw.replace(/\D/g,'');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // === Хелпер: вычисляем возраст ===
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // === Шаг 1 → Валидируем e-mail и переходим к форме ===
  const handleEmailNext = () => {
    if (!email.toLowerCase().endsWith('@vtb.ru')) {
      setEmailError('Домен должен быть @vtb.ru');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  // === Обработчик изменения суммы ===
  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10) || 0;
    if (num < minAmount) {
      setAmountError(`Введите сумму от ${minAmount.toLocaleString('ru-RU')} ₽`);
    } else {
      setAmountError('');
    }
  };

  // === Проверка, можно ли нажать «Далее» на шаге форма ===
  const canGoNext = () => {
    if (
      !(birthDate instanceof Date) ||
      !gender ||
      !calcType ||
      !amount ||
      amountError
    ) {
      return false;
    }
    const age = getAge(birthDate);
    if (age < 18) return false;                     // минимум 18 лет
    const endAge = age + programTerm;
    if (endAge > 70) return false;                   // не старше 70 к концу срока
    return true;
  };

  // === Эффект: проверка срока программы на выход за границы возраста ===
  useEffect(() => {
    if (birthDate) {
      const age = getAge(birthDate);
      const endAge = age + programTerm;
      if (age >= 18 && endAge > 70) {
        if (programTerm === 5) {
          setTermError('Эта программа вам не подходит');
        } else {
          setTermError('Возраст на конец программы > 70. Уменьшите срок.');
        }
      } else {
        setTermError('');
      }
    }
  }, [programTerm, birthDate]);

  // === Шаг 2 → Отправляем на сервер /api/carefuture/save ===
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{ day:'2-digit', month:'2-digit', year:'numeric' });
    const payload = {
      email,
      birthdate: dateRu,
      age:        getAge(birthDate),
      gender,
      term:       programTerm,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res = await fetch('/api/carefuture/save', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (res.ok) {
        const data = await res.json();
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // === Рендер элементов карусели результата ===
  const slides = resultData ? [
    <div key={0} className="carousel-page">
      <div className="first-line">В течение {programTerm} лет</div>
      <div className="unit">Вы вносите средства</div>
      <div className="value">{formatSum(String(resultData.contribution))} ₽</div>
      <div className="unit">{periodicity.charAt(0).toUpperCase() + periodicity.slice(1)}</div>
      <div className="value">{formatSum(String(resultData.totalContribution))} ₽</div>
      <div className="footer-label">Сумма ваших взносов</div>
    </div>,
    <div key={1} className="carousel-page">
      <div className="first-line">Начисляем дополнительно</div>
      <div className="unit">{`${resultData.accrualPercent}% от каждого взноса`}</div>
      <div className="value">{formatSum(String(resultData.accrualAmount))} ₽</div>
      <div className="unit">{periodicity.charAt(0).toUpperCase() + periodicity.slice(1)}</div>
      <div className="value">{formatSum(String(resultData.totalAccrual))} ₽</div>
      <div className="footer-label">Общая сумма начислений</div>
    </div>,
    <div key={2} className="carousel-page">
      <div className="first-line">Итоговая годовая выплата</div>
      <div className="unit">Сумма с начислениями</div>
      <div className="value">{formatSum(String(resultData.finalAnnual))} ₽</div>
      <div className="unit">Сумма за {programTerm} лет</div>
      <div className="value">{formatSum(String(resultData.finalTotal))} ₽</div>
      <div className="footer-label">Ваш капитал через {programTerm} лет</div>
    </div>
  ] : [];

  // === Шаг «Связаться с менеджером» ===
  const handleManagerSubmit = async () => {
    if (!mgrSurname.trim() || !mgrName.trim() || !mgrCity.trim()) {
      setMgrError('Заполните все поля');
      return;
    }
    const payload = {
      surname:      mgrSurname.trim(),
      name:         mgrName.trim(),
      city:         mgrCity.trim(),
      email,
      gender,
      birthdate:    birthDate.toLocaleDateString('ru-RU',{ day:'2-digit', month:'2-digit', year:'numeric' }),
      age:          getAge(birthDate),
      term:         programTerm,
      contribution: resultData.contribution || 0
    };
    try {
      const res = await fetch('/api/carefuture/send_manager', {
        method:  'POST',
        headers: { 'Content-Type': 'application/json' },
        body:    JSON.stringify(payload)
      });
      if (res.ok) {
        navigate('/main-menu');
      } else {
        setMgrError('Ошибка отправки');
      }
    } catch {
      setMgrError('Сетевая ошибка');
    }
  };

  // === Рендер страницы ===
  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>

      {/* === Шапка: “Домой” + лого + (кнопка «Далее» для email или формы) === */}
      <div className="polls-header">
        {/* Кнопка “Домой” */}
        <button className="back-btn" onClick={()=>navigate('/main-menu')}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/>
          </svg>
        </button>
        {/* Логотип с анимацией */}
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        {/* Кнопка «Далее» – отображается и на шаге email, и на шаге form */}
        {(step === 'email' || step === 'form') && (
          <button
            className="forward-btn"
            onClick={step === 'email' ? handleEmailNext : handleNext}
            disabled={
              step === 'email'
                ? !email.toLowerCase().endsWith('@vtb.ru')
                : !canGoNext()
            }
          >
            <span className="arrow-icon">→</span>
          </button>
        )}
      </div>

      {/* === Заголовок «Забота о будущем» (анимация как в SNPPage) === */}
      {(step === 'email' || step === 'form') && (
        <h1 className="polls-title">Забота о будущему</h1>
      )}

      {/* ===== Шаг 1: EMAIL ===== */}
      {step === 'email' && (
        <div className="content-wrapper">
          <div className="email-step">
            <label className="email-label">Корпоративная почта ВТБ:</label>
            <input
              type="email"
              inputMode="email"
              autoComplete="email"
              autoCapitalize="none"
              spellCheck="false"
              placeholder="name@vtb.ru"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          {emailError && <div className="input-error">{emailError}</div>}
          {/* Кнопка «Продолжить» (дублирована под input, но основная навигация — стрелка вперед) */}
          <button
            className="polls-btn"
            onClick={handleEmailNext}
            disabled={!email.toLowerCase().endsWith('@vtb.ru')}
          >
            Продолжить
          </button>
        </div>
      )}

      {/* ===== Шаг 2: ФОРМА ===== */}
      {step === 'form' && !isProcessing && !resultData && (
        <>
          <div className="content-wrapper">
            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDateParts}
                onChange={val => {
                  if (val instanceof Date) {
                    setBirthDateParts({
                      day: String(val.getDate()).padStart(2, '0'),
                      month: String(val.getMonth() + 1).padStart(2, '0'),
                      year: String(val.getFullYear())
                    });
                  } else if (typeof val === 'object') {
                    setBirthDateParts(prev => ({
                      ...prev,
                      ...(val.day   != null ? { day: String(val.day).padStart(2,'0') } : {}),
                      ...(val.month != null ? { month: String(val.month).padStart(2,'0') } : {}),
                      ...(val.year  != null ? { year: String(val.year) } : {})
                    }));
                  } else if (typeof val === 'string') {
                    const parts = val.includes('.') ? val.split('.') :
                                  val.includes('-') ? val.split('-').reverse() : [];
                    if (parts.length === 3) {
                      setBirthDateParts({
                        day: parts[0].padStart(2,'0'),
                        month: parts[1].padStart(2,'0'),
                        year: parts[2]
                      });
                    }
                  }
                }}
              />
              {termError && <div className="input-error">{termError}</div>}
            </div>

            <div className="date-section">
              <label className="section-label">Срок программы (лет):</label>
              <select
                className="sum-section"
                value={programTerm}
                onChange={e => setProgramTerm(Number(e.target.value))}
              >
                {Array.from({ length: 16 }, (_, i) => 5 + i).map(v => (
                  <option key={v} value={v}>{v} лет</option>
                ))}
              </select>
            </div>

            <div className="section-subtitle">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-subtitle">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => { setCalcType('premium'); setAmount(''); setAmountError(''); }}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => { setCalcType('sum'); setAmount(''); setAmountError(''); }}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-subtitle">Введите сумму в рублях:</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={`Мин. ${minAmount.toLocaleString('ru-RU')} ₽`}
                    value={amount}
                    onChange={handleAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </div>
          {/* Кнопка «Далее» (стрелка вправо) */}
          <button
            className="forward-btn"
            onClick={handleNext}
            disabled={!canGoNext()}
          >
            <span className="arrow-icon">→</span>
          </button>
        </>
      )}

      {/* ===== Экраны “Processing” ===== */}
      {isProcessing && (
        <div className="processing-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
          <p className="processing-title">Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {/* ===== Шаг “Результат” + карусель + кнопка менеджера ===== */}
      {step === 'result' && resultData && !isProcessing && (
        <>
          <div className="content-wrapper">
            <div className="params-container">
              <h2>Параметры</h2>
              <p><strong>Возраст:</strong> {resultData.age} лет</p>
              <p><strong>Пол:</strong> {resultData.gender}</p>
              <p><strong>Срок:</strong> {resultData.term} лет</p>
              <p><strong>Сумма:</strong> {formatSum(String(resultData.sum))} ₽</p>
            </div>

            {/* ==== КАРУСЕЛЬ ==== */}
            <div className="carousel-container">
              {/* Индикаторы */}
              <div className="carousel-indicators">
                <span className={slideIndex === 0 ? 'active' : ''}></span>
                <span className={slideIndex === 1 ? 'active' : ''}></span>
                <span className={slideIndex === 2 ? 'active' : ''}></span>
              </div>

              {/* «Окно» карусели */}
              <div className="carousel-clipper">
                <div
                  className="carousel-wrapper"
                  style={{ transform: `translateX(-${slideIndex * 100}%)` }}
                >
                  {slides}
                </div>
              </div>

              {/* Кнопки навигации */}
              <button
                className="carousel-control prev"
                onClick={() => setSlideIndex(i => i - 1)}
                disabled={slideIndex === 0}
              >
                ‹
              </button>
              <button
                className="carousel-control next"
                onClick={() => setSlideIndex(i => i + 1)}
                disabled={slideIndex === slides.length - 1}
              >
                ›
              </button>
            </div>
            {/* ==== конец карусели ==== */}

            <button
              className="contact-btn"
              onClick={() => setStep('manager')}
            >
              Связаться с менеджером
            </button>
          </div>
        </>
      )}

      {/* ===== Шаг “Менеджер” ===== */}
      {step === 'manager' && (
        <div className="content-wrapper">
          <h2 className="contact-title">Введите Ваши данные:</h2>
          <div className="contact-form">
            <label>Фамилия:</label>
            <input
              type="text"
              placeholder="Введите фамилию"
              value={mgrSurname}
              onChange={e => setMgrSurname(e.target.value)}
            />

            <label>Имя:</label>
            <input
              type="text"
              placeholder="Введите имя"
              value={mgrName}
              onChange={e => setMgrName(e.target.value)}
            />

            <label>Город работы:</label>
            <input
              type="text"
              placeholder="Введите город"
              value={mgrCity}
              onChange={e => setMgrCity(e.target.value)}
            />

            {mgrError && <div className="input-error">{mgrError}</div>}
          </div>

          <button
            className="contact-btn"
            onClick={handleManagerSubmit}
          >
            Отправить запрос
          </button>
        </div>
      )}
    </div>
  );
}
