// src/WelcomePage.js

import React, { useEffect, useState } from 'react';
import { useNavigate }              from 'react-router-dom';
import { useSwipeable }             from 'react-swipeable';
import { motion, AnimatePresence }  from 'framer-motion';

import backgroundImage from './components/background.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();

  const [animateLogo, setAnimateLogo] = useState(false);
  const [showText, setShowText]       = useState(false);
  const [greeting, setGreeting]       = useState('');

  useEffect(() => {
    // Определяем приветствие по времени суток
    const hour = new Date().getHours();
    let text = '';
    if (hour >= 6 && hour < 11)       text = 'Доброе утро';
    else if (hour >= 11 && hour < 17) text = 'Добрый день';
    else if (hour >= 17 && hour < 24) text = 'Добрый вечер';
    else                               text = 'Доброй ночи';
    setGreeting(text);

    // 1) Через 100ms «выезжает» логотип (CSS-анимация)
    const logoTimer = setTimeout(() => setAnimateLogo(true), 100);

    // 2) Через 900ms показываем текст (Framer Motion: slide-in)
    const textInTimer = setTimeout(() => setShowText(true), 900);

    // 3) Через 3500ms скрываем текст (Framer Motion: slide-out)
    const textOutTimer = setTimeout(() => setShowText(false), 3500);

    // 4) Через 4000ms переходим на /main-menu
    const navTimer = setTimeout(() => navigate('/main-menu'), 4000);

    return () => {
      clearTimeout(logoTimer);
      clearTimeout(textInTimer);
      clearTimeout(textOutTimer);
      clearTimeout(navTimer);
    };
  }, [navigate]);

  const swipeHandlers = useSwipeable({ preventDefault: true });

  return (
    <div
      className="welcome-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
      {...swipeHandlers}
    >
      {/* Логотип (рамка + <img>), выезжает сверху через CSS */}
      <div className={`logo-wrapper ${animateLogo ? 'animate-logo' : ''}`}>
        <img
          className="logo-image"
          src={require('./components/logo.png')}
          alt="Логотип РГС Жизнь"
        />
      </div>

      {/* Текст-приветствие с Framer Motion */}
      <div className="text-wrapper">
        <AnimatePresence>
          {showText && (
            <motion.h1
              className="welcome-title"
              initial={{ x: '-100%', opacity: 0 }}
              animate={{ x: 0,       opacity: 1 }}
              exit={{ x: '100%',     opacity: 0 }}
              transition={{ duration: 0.8, ease: 'easeOut' }}
            >
              {greeting}
            </motion.h1>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default WelcomePage;
