import React from 'react';
import './DateWheelPicker.css'; // Создадим отдельный CSS для стилизации

const DateWheelPicker = ({ onChange }) => {
  const days = Array.from({ length: 31 }, (_, i) => (i + 1).toString().padStart(2, '0'));
  const months = Array.from({ length: 12 }, (_, i) => (i + 1).toString().padStart(2, '0'));
  const years = Array.from({ length: 100 }, (_, i) => (2025 - i).toString());

  // Функция, которая собирает выбранные значения и вызывает onChange
  const handleChange = () => {
    const day = document.getElementById('wheel-day').value;
    const month = document.getElementById('wheel-month').value;
    const year = document.getElementById('wheel-year').value;
    onChange(`${day}/${month}/${year}`);
  };

  return (
    <div className="date-wheel-picker">
      <select id="wheel-day" onChange={handleChange} defaultValue="01">
        {days.map((d) => (
          <option key={d} value={d}>
            {d}
          </option>
        ))}
      </select>
      <select id="wheel-month" onChange={handleChange} defaultValue="01">
        {months.map((m) => (
          <option key={m} value={m}>
            {m}
          </option>
        ))}
      </select>
      <select id="wheel-year" onChange={handleChange} defaultValue="2000">
        {years.map((y) => (
          <option key={y} value={y}>
            {y}
          </option>
        ))}
      </select>
    </div>
  );
};

export default DateWheelPicker;
