    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í–´ –ü–û–õ–£–ß–ê–ï–¢–ï:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {term} –ª–µ—Ç</div>
    </div>
  ] : [];

  const handleManagerSubmit = () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∑–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è.');
    } else {
      console.log({ email, mgrSurname, mgrName, mgrCity });
      navigate('/Main-Menu');
    }
  };

  return (
    <div className="snp-container" style={{backgroundImage:`url(${backgroundImage})`}}>

      {/* –î–æ–º–æ–π */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/Main-Menu')}>
          <img src={mainmenuIcon} alt="–î–æ–º–æ–π"/>
