// CareFuturePage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './CareFuturePage.css';

export default function CareFuturePage() {
  const navigate = useNavigate();

  // === состояния ===
  const [step,           setStep]           = useState('email'); // email → form → processing → result → manager
  const [email,          setEmail]          = useState('');
  const [emailError,     setEmailError]     = useState('');
  const [birthDateParts, setBirthDateParts] = useState({ day:null, month:null, year:null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);
  const [programTerm,    setProgramTerm]    = useState(5);       // срок программы в годах
  const [termError,      setTermError]      = useState('');
  const [calcType,       setCalcType]       = useState(null);
  const [amount,         setAmount]         = useState('');
  const [amountError,    setAmountError]    = useState('');
  const [isProcessing,   setIsProcessing]   = useState(false);
  const [resultData,     setResultData]     = useState(null);
  const [mgrSurname,     setMgrSurname]     = useState('');
  const [mgrName,        setMgrName]        = useState('');
  const [mgrCity,        setMgrCity]        = useState('');
  const [mgrError,       setMgrError]       = useState('');
  const [slideIndex,     setSlideIndex]     = useState(0);

  // === константы ===
  const periodicity = 'ежегодно';
  const minAmount   = 100000; // новая минималка

  // === эффект: собираем Date из birthDateParts ===
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (!isNaN(dt.getTime())
          && dt.getDate()     === d
          && dt.getMonth()+1  === m
          && dt.getFullYear() === y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // === утилиты ===
  const formatSum = raw => {
    let digits = raw.replace(/\D/g,'');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // === валидации ===
  const handleEmailNext = () => {
    if (!email.endsWith('@vtb.ru')) {
      setEmailError('Домен вашей почты не является корпоративным. Попробуйте ещё раз.');
    } else {
      setEmailError('');
      setStep('form');
    }
  };

  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g,''),10) || 0;
    setAmountError(num < minAmount
      ? `Введите сумму от ${minAmount.toLocaleString('ru-RU')} ₽`
      : '');
  };

  // проверяем условия перед кнопкой "Далее"
  const canGoNext = () => {
    if (!(birthDate instanceof Date) || !gender || !calcType || !amount || amountError) return false;
    const age = getAge(birthDate);
    if (age < 18) return false;           // минимум 18 полных лет
    const endAge = age + programTerm;
    if (endAge > 70) return false;        // не старше 70 полных на конец
    return true;
  };

  // валидация срока программы
  useEffect(() => {
    if (birthDate) {
      const age = getAge(birthDate);
      const endAge = age + programTerm;
      if (age >= 18 && endAge > 70) {
        if (programTerm === 5) {
          setTermError('К сожалению, данная программа Вам не подойдёт');
        } else {
          setTermError('Возраст на дату окончания не должен превышать 70 лет. Попробуйте уменьшить срок.');
        }
      } else {
        setTermError('');
      }
    }
  }, [programTerm, birthDate]);

  // === переход к расчёту ===
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'});
    const payload = {
      birthdate: dateRu,
      age:        getAge(birthDate),
      gender,
      term:       programTerm,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amount.replace(/\./g,''),10)
    };
    setIsProcessing(true);
    try {
      const res = await fetch('/api/carefuture/save', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
        setStep('result');
      }
    } catch(err) {
      console.error(err);
    }
    setIsProcessing(false);
  };

  // === слайды результата ===
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">В ТЕЧЕНИЕ {programTerm} ЛЕТ</div>
      <div className="slide-title">Вы вносите средства</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ₽</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">Сумма ваших взносов</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ₽</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">НАЧИСЛЯЕМ ДОПОЛНИТЕЛЬНО</div>
      <div className="slide-title">{`${resultData.accrualPercent}% от каждого взноса`}</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ₽</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">Общая сумма начислений</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ₽</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">ВЫ ПОЛУЧАЕТЕ:</div>
      <div className="slide-icon">💰</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ₽</div>
      <div className="slide-period">Ежегодные накопления</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ₽</div>
      <div className="slide-subtitle">Ваш капитал через {programTerm} лет</div>
    </div>
  ] : [];

  // === отправка менеджеру ===
  const handleManagerSubmit = async () => {
    if (!mgrSurname||!mgrName||!mgrCity) {
      setMgrError('Заполните все поля');
      return;
    }
    const payload = {
      surname:      mgrSurname.trim(),
      name:         mgrName.trim(),
      city:         mgrCity.trim(),
      email,
      gender,
      birthdate:    birthDate.toLocaleDateString('ru-RU',{day:'2-digit',month:'2-digit',year:'numeric'}),
      age:          getAge(birthDate),
      term:         programTerm,
      contribution: resultData.contribution
    };
    try {
      const res = await fetch('/api/carefuture/send_manager',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(payload)
      });
      if (res.ok) navigate('/main-menu');
      else       setMgrError('Ошибка отправки');
    } catch {
      setMgrError('Сетевая ошибка');
    }
  };

  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>

      {/* кнопка Домой */}
      <div className="back-btn-container">
        <button className="back-btn" onClick={()=>navigate('/main-menu')}>
          <img src={mainmenuIcon} alt="Домой"/>
        </button>
      </div>
      {/* логотип */}
      <div className="logo-container">
        <img src={logoImage} alt="Логотип" className="logo-image"/>
      </div>
      {/* заголовок */}
      <h1 className="snp-title">Забота о будущем</h1>

      {/* Шаг 1 — EMAIL */}
      {step==='email' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Корпоративная почта ВТБ:</label>
            <input type="email" className="insurance-sum-input"
                   value={email} onChange={e=>setEmail(e.target.value)}
                   placeholder="name@vtb.ru"/>
          </div>
          <button className="insurance-btn selected" onClick={handleEmailNext}>
            Перейти к расчёту
          </button>
          {emailError && <div className="input-error-message">{emailError}</div>}
        </div>
      )}

      {/* Шаг 2 — ФОРМА */}
      {step==='form' && !isProcessing && !resultData && (
        <>
          <div className="snp-form">
            <div className="form-group">
              <label className="form-label">Дата рождения:</label>
              <DateWheelPicker value={birthDateParts}
                onChange={val=>{
                  if (val instanceof Date) {
                    setBirthDateParts({
                      day: String(val.getDate()).padStart(2,'0'),
                      month: String(val.getMonth()+1).padStart(2,'0'),
                      year: String(val.getFullYear())
                    });
                  } else if (typeof val==='object') {
                    setBirthDateParts(prev=>({
                      ...prev,
                      ...(val.day   != null ? {day:String(val.day).padStart(2,'0')}   :{}),
                      ...(val.month != null ? {month:String(val.month).padStart(2,'0')}:{}),
                      ...(val.year  != null ? {year:String(val.year)}                  :{})
                    }));
                  } else if (typeof val==='string') {
                    const parts = val.includes('.') ? val.split('.') :
                                  val.includes('-') ? val.split('-').reverse() : [];
                    if (parts.length===3) {
                      setBirthDateParts({
                        day:   parts[0].padStart(2,'0'),
                        month: parts[1].padStart(2,'0'),
                        year:  parts[2]
                      });
                    }
                  }
                }}/>
            </div>

            <div className="form-group">
              <label className="form-label">Срок программы (лет):</label>
              <select className="insurance-sum-input"
                      value={programTerm}
                      onChange={e=>setProgramTerm(Number(e.target.value))}>
                {Array.from({length:16},(_,i)=>5+i).map(v=>(
                  <option key={v} value={v}>{v}</option>
                ))}
              </select>
              {termError && <div className="input-error-message">{termError}</div>}
            </div>

            <div className="form-group">
              <label className="form-label">Пол:</label>
              <div className="gender-selector">
                {['мужской','женский'].map(v=>(
                  <div key={v}
                    className={`gender-option ${gender===v?'selected':''}`}
                    onClick={()=>setGender(v)}>
                    <div className="gender-label">{v==='мужской'?'Мужской':'Женский'}</div>
                    <div className="gender-icon">{v==='мужской'?'👨':'👩'}</div>
                  </div>
                ))}
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">Расчёт от…</label>
              <div className="insurance-options">
                <button className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                        onClick={()=>{ setCalcType('premium'); setAmount(''); setAmountError(''); }}>
                  Страхового взноса
                </button>
                <button className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                        onClick={()=>{ setCalcType('sum'); setAmount(''); setAmountError(''); }}>
                  Страховой суммы
                </button>
              </div>
            </div>

            {calcType && (
              <div className="form-group">
                <label className="form-label">Введите сумму в рублях</label>
                <input type="text" className="insurance-sum-input"
                       value={amount}
                       onChange={handleAmountChange}
                       placeholder={`Минимум ${minAmount.toLocaleString('ru-RU')}`}/>
                {amountError && <div className="input-error-message">{amountError}</div>}
              </div>
            )}
          </div>

          <div className="top-btn-container">
            <button className="top-btn"
                    onClick={handleNext}
                    disabled={!canGoNext()}>
              <svg viewBox="0 0 24 24"><path d="M12 4l8 8-8 8"/></svg>
            </button>
          </div>
        </>
      )}

      {/* Processing */}
      {isProcessing && (
        <div className="processing-container">
          <p>Выполняется расчёт...</p>
          <div className="arrow-animation">→</div>
        </div>
      )}

      {/* Результат + slider + менеджер */}
      {step==='result' && resultData && !isProcessing && (
        <>
          <div className="result-container">
            <h2>Результаты расчёта</h2>
            <p><strong>Возраст:</strong> {resultData.age} лет</p>
            <p><strong>Пол:</strong> {resultData.gender}</p>
            <p><strong>Срок:</strong> {resultData.term} лет</p>
            <p><strong>Сумма:</strong> {formatSum(String(resultData.sum))} ₽</p>
            <p><strong>Итоговая премия:</strong> {formatSum(String(resultData.totalPremium||0))} ₽</p>
          </div>
          <div className="detail-slider-container">
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 &&
                <button onClick={()=>setSlideIndex(i=>i-1)} className="nav-btn left">←</button>}
              {slideIndex<slides.length-1 &&
                <button onClick={()=>setSlideIndex(i=>i+1)} className="nav-btn right">→</button>}
            </div>
          </div>
          <button className="insurance-btn selected"
                  onClick={()=>setStep('manager')}>
            Связаться с менеджером
          </button>
        </>
      )}

      {/* Форма менеджера */}
      {step==='manager' && (
        <div className="snp-form">
          <div className="form-group">
            <label className="form-label">Фамилия:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrSurname}
                   onChange={e=>setMgrSurname(e.target.value)}/>
          </div>
          <div className="form-group">
            <label className="form-label">Имя:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrName}
                   onChange={e=>setMgrName(e.target.value)}/>
          </div>
          <div className="form-group">
            <label className="form-label">Город работы:</label>
            <input type="text" className="insurance-sum-input"
                   value={mgrCity}
                   onChange={e=>setMgrCity(e.target.value)}/>
          </div>
          {mgrError && <div className="input-error-message">{mgrError}</div>}
          <div className="top-btn-container">
            <button className="top-btn"
                    onClick={handleManagerSubmit}>
              Отправить менеджеру
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
