import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // шаги и свайпы
  const [step, setStep]                   = useState(1);
  const [carouselIndex, setCarouselIndex] = useState(0);

  // ввод почты
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  // ввод персональных данных
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null);    // 'мужской' | 'женский'
  const [calcType, setCalcType]     = useState(null);    // 'premium' | 'sum'
  const [amountRaw, setAmountRaw]   = useState('');      // цифры без форматирования
  const [amountDisplay, setAmountDisplay] = useState(''); // для показа с точками
  const [amountError, setAmountError]     = useState('');

  // запрос и результат
  const [isProcessing, setIsProcessing] = useState(false);
  const [resultData, setResultData]     = useState(null);

  // утилиты валидации
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) ||
           (calcType === 'sum'     && n >= 450000);
  }

  function canGoNext() {
    return step === 2 &&
           getAge(birthDate) >= 18 &&
           gender &&
           calcType &&
           amountRaw &&
           isAmountValid();
  }

  // Шаг 1 → Шаг 2
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // сбросим предыдущие
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  // ввод суммы с авто-форматированием
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    // визуальное форматирование с точками
    let d = raw, o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    // валидация минимальной суммы
    const n = parseInt(raw, 10) || 0;
    if      (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum'     && n < 450000) setAmountError('Мин. 450 000 ₽');
    else                                           setAmountError('');
  }

  // отправка на прокси и основной сервер
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const payload = {
      email,
      birthdate: dateStr,
      age:        getAge(birthDate),
      gender,
      payment:    calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum:        parseInt(amountRaw, 10)
    };

    try {
      // сначала на ваш прокси
      const proxyRes = await fetch('/api/proxy/snp/save', {
        method:  'POST',
        headers: {'Content-Type':'application/json'},
        body:    JSON.stringify(payload)
      });
      if (!proxyRes.ok) {
        console.error('Прокси-ошибка', proxyRes.status, await proxyRes.text());
        alert(`Ошибка ${proxyRes.status}. Подробности в консоли.`);
      } else {
        // дальше вы можете дергать основной эндпоинт, если нужно
        const data = await proxyRes.json();
        setResultData(data);
        setCarouselIndex(0);
      }
    } catch (err) {
      console.error('Ошибка запроса:', err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  function goHome() {
    navigate(-1);
  }

  // --- экран «Выполняется расчёт…» ---
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <h2 className="processing-title">
          Выполняется расчёт по программе<br/>
          «Стратегия на пять. Гарант»
        </h2>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // --- экран результата и первая страница карусели ---
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day:'2-digit', month:'2-digit', year:'numeric'
    });

    // читаем C13 и C16 из результата и парсим цифры
    const rawC13 = (resultData.c13 || '0').toString().replace(/\s/g, '').replace(',', '.');
    const rawC16 = (resultData.c16 || '0').toString().replace(/\s/g, '').replace(',', '.');
    const c13 = parseFloat(rawC13) || 0;
    const c16 = parseFloat(rawC16) || 0;
    const fmt = new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });
    const c13f = fmt.format(c13);
    const c16f = fmt.format(c16);

    return (
      <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>
          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>
          <button
            className="repeat-btn"
            onClick={() => {
              setResultData(null);
              setStep(2);
              setCarouselIndex(0);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="currentColor"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          {/* Параметры расчёта */}
          <h1 className="polls-title">
            Расчёт по программе «Стратегия на пять. Гарант»
          </h1>
          <div className="params-container">
            <h2>Параметры</h2>
            <p>Дата расчёта: {todayStr}</p>
            <p>Возраст: {getAge(birthDate)} лет</p>
            <p>Пол: {gender}</p>
            <p>Вариант расчёта от: {calcType==='premium'?'Страхового взноса':'Страховой суммы'}</p>
          </div>

          {/* Карусель с 3 страницами */}
          <div className="carousel-container">
            {/* индикаторы */}
            <div className="carousel-indicators">
              {[0,1,2].map(i => (
                <span key={i} className={carouselIndex===i?'active':''} />
              ))}
            </div>

            {/* wrapper */}
            <div
              className="carousel-wrapper"
              style={{ transform:`translateX(-${carouselIndex*100}%)` }}
            >
              {/* страница 1 */}
              <div className="carousel-page">
                <p className="first-line">В течение 5 лет</p>
                <p className="unit">Вы вносите средства в программу</p>
                <p className="value">{c13f} ₽</p>
                <p className="unit">Ежегодно</p>
                <p className="value">{c16f} ₽</p>
                <p className="unit">Сумма ваших взносов</p>
              </div>
              {/* страницы 2 и 3 пока пустые */}
              <div className="carousel-page"></div>
              <div className="carousel-page"></div>
            </div>

            {/* стрелки по бокам */}
            {carouselIndex > 0 && (
              <button
                className="carousel-control prev"
                onClick={() => setCarouselIndex(i => i - 1)}
              >‹</button>
            )}
            {carouselIndex < 2 && (
              <button
                className="carousel-control next"
                onClick={() => setCarouselIndex(i => i + 1)}
              >›</button>
            )}
          </div>
        </div>
      </div>
    );
  }

  // ——— Шаг 1 и Шаг 2 (ввод) ———
  return (
    <div className="snp-container" style={{ backgroundImage:`url(${backgroundImage})` }}>
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24"><path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z"/></svg>
        </button>
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <button
          className="forward-btn"
          onClick={step===1 ? handleEmailNext : handleNext}
          disabled={step===1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      <div className="content-wrapper">
        {/* Шаг 1: почта */}
        {step===1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={e=>setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button
              className="polls-btn"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {/* Шаг 2: данные */}
        {step===2 && (
          <>
            <h1 className="polls-title">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={val=>{
                  if (val instanceof Date) setBirthDate(val);
                  else if(val?.day)
                    setBirthDate(new Date(val.year,val.month-1,val.day));
                }}
              />
            </div>

            <div className="section-label">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender==='мужской'?'selected':''}`}
                onClick={()=>setGender('мужской')}
              >
                <div>Мужской</div><div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender==='женский'?'selected':''}`}
                onClick={()=>setGender('женский')}
              >
                <div>Женский</div><div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-label">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                onClick={()=>setCalcType('premium')}
              >
                <div>Страхового</div><div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={()=>setCalcType('sum')}
              >
                <div>Страховой</div><div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-label">Сумма (₽):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType==='premium'? 'Мин. 60 000': 'Мин. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
