import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable'; // Импортируем библиотеку для свайпов
import logo from './components/logo.png';
import backgroundImage from './components/background.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [textVisible, setTextVisible] = useState(true);
  const [greeting, setGreeting] = useState('Добро пожаловать!');
  const [userName, setUserName] = useState('');
  const [offsetY, setOffsetY] = useState(0);

  // Обработчик свайпа
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => navigate('/register'),
    onSwipedRight: () => navigate('/home'),
  });

  const handleScroll = () => {
    setOffsetY(window.pageYOffset);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    // Получаем имя пользователя из параметров URL
    const userNameFromURL = searchParams.get('user_first_name');
    if (userNameFromURL) {
      setUserName(userNameFromURL);
      localStorage.setItem('userName', userNameFromURL);
    } else {
      // Если имя не передано в URL, пытаемся получить его из localStorage
      const storedUserName = localStorage.getItem('userName');
      if (storedUserName) {
        setUserName(storedUserName);
      } else {
        // Если имя не найдено, устанавливаем "Гость"
        setUserName('Гость');
      }
    }

    const now = new Date();
    const hour = now.getHours();
    if (hour >= 6 && hour < 11) {
      setGreeting("Доброе утро");
    } else if (hour >= 11 && hour < 17) {
      setGreeting("Добрый день");
    } else if (hour >= 17 && hour < 24) {
      setGreeting("Добрый вечер");
    } else {
      setGreeting("Доброй ночи");
    }

    // Проверяем, зарегистрирован ли пользователь (например, наличие токена)
    const userToken = localStorage.getItem('userToken');

    // Через 4 секунды запускаем анимацию
    const moveUpTimer = setTimeout(() => {
      setTextVisible(false);
    }, 4000);

    // Через 5 секунд перенаправляем: если зарегистрирован, на главную, иначе на регистрацию
    const redirectTimer = setTimeout(() => {
      if (userToken) {
        navigate('/main-menu'); // Если токен есть, считаем, что пользователь зарегистрирован
      } else {
        navigate('/register');
      }
    }, 5000);

    return () => {
      clearTimeout(moveUpTimer);
      clearTimeout(redirectTimer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, [navigate, searchParams]);

  return (
    <div
      className="container"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundPositionY: `${offsetY * 0.5}px`
      }}
      {...swipeHandlers}
    >
      <div className="logo">
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>
        {greeting}, {userName}!
      </h1>
    </div>
  );
};

export default WelcomePage;
