import React, { useEffect, useState } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable';
import logo from './components/logo.png';
import backgroundImage from './components/background.png';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [textVisible, setTextVisible] = useState(true);
  const [greeting, setGreeting] = useState('Добро пожаловать!');
  const [userName, setUserName] = useState('');
  const [offsetY, setOffsetY] = useState(0);
  const [swipeClass, setSwipeClass] = useState(''); // класс для анимации свайпа

  const handleScroll = () => {
    setOffsetY(window.pageYOffset);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    const userNameFromURL = searchParams.get('user_first_name');
    if (userNameFromURL) {
      setUserName(userNameFromURL);
      localStorage.setItem('userName', userNameFromURL);
    } else {
      const storedUserName = localStorage.getItem('userName');
      if (storedUserName) {
        setUserName(storedUserName);
      } else {
        setUserName('Гость');
      }
    }

    const now = new Date();
    const hour = now.getHours();
    if (hour >= 6 && hour < 11) {
      setGreeting("Доброе утро");
    } else if (hour >= 11 && hour < 17) {
      setGreeting("Добрый день");
    } else if (hour >= 17 && hour < 24) {
      setGreeting("Добрый вечер");
    } else {
      setGreeting("Доброй ночи");
    }

    const userToken = localStorage.getItem('userToken');

    const exitTimer = setTimeout(() => {
      setTextVisible(false);
    }, 4000);

    const redirectTimer = setTimeout(() => {
      if (userToken) {
        navigate('/main-menu');
      } else {
        navigate('/register');
      }
    }, 5000);

    return () => {
      clearTimeout(exitTimer);
      clearTimeout(redirectTimer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, [navigate, searchParams]);

  // Обработчики свайпов с эффектом
  const handleSwipeLeft = () => {
    setSwipeClass('swipe-left');
    setTimeout(() => {
      const userToken = localStorage.getItem('userToken');
      if (userToken) {
        navigate('/main-menu');
      } else {
        navigate('/register');
      }
    }, 500);
  };

  const handleSwipeRight = () => {
    setSwipeClass('swipe-right');
    setTimeout(() => {
      const userToken = localStorage.getItem('userToken');
      if (userToken) {
        navigate('/main-menu');
      } else {
        navigate('/home');
      }
    }, 500);
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: handleSwipeLeft,
    onSwipedRight: handleSwipeRight,
  });

  return (
    <div
      className={`container ${swipeClass}`}
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundPositionY: `${offsetY * 0.5}px`
      }}
      {...swipeHandlers}
    >
      <div className={`logo-assessment ${textVisible ? '' : 'disappear'}`}>
        <img src={logo} alt="Logo" className="logo-image" />
      </div>
      <h1 className={`welcome-text ${textVisible ? '' : 'disappear'}`}>
        {greeting}, {userName}!
      </h1>
    </div>
  );
};

export default WelcomePage;
