import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import logo from './components/logo.png';
import backgroundImage from './components/background.jpg';
import partners from './components/autosuggest/partners.json';
import speakers from './components/autosuggest/speakers.json';
import './FeedbackPage.css';

const FeedbackPage = () => {
  const [keyboardVisible, setKeyboardVisible] = useState(false);
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  const getPartnerSuggestions = (value) => {
    const inputValue = value ? value.trim().toLowerCase() : '';
    return inputValue.length < 2
      ? []
      : partners.filter(p => typeof p === 'string' && p.toLowerCase().includes(inputValue));
  };

  const handlePartnerChange = (e) => {
    setPartner(e.target.value); // Обновляем partner
  };

  const partnerInputProps = {
    placeholder: 'Введите название партнёра',
    value: partner,
    onChange: handlePartnerChange,
    onFocus: () => setKeyboardVisible(true),  // Когда фокус на поле ввода
    onBlur: () => setKeyboardVisible(false),  // Когда фокус покидает поле ввода
    autoComplete: 'off'
  };

  useEffect(() => {
    // Отслеживание изменения высоты экрана при появлении клавиатуры
    const handleResize = () => {
      const isKeyboardVisible = window.innerHeight < 500; // Пороговая высота для определения появления клавиатуры
      setKeyboardVisible(isKeyboardVisible);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Этап выбора спикеров (обязательно хотя бы один)
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };
  const handleSpeakerChange = (e, index) => {
    const updated = [...speakersList];
    updated[index].fullName = e.target.value;
    setSpeakersList(updated);
  };

  // Этап оценки качеств спикера для каждого спикера отдельно
  const qualityRows = [
    { positive: "Экспертный", negative: "Компетентный" },
    { positive: "Энергичный", negative: "Пассивный" },
    { positive: "Мотивирующий", negative: "Невдохновляющий" },
    { positive: "Харизматичный", negative: "Душный" },
    { positive: "Доступно доносит информацию", negative: "Говорит сложными терминами" },
  ];
  // Для каждого спикера хранится массив оценок (5 строк)
  const [speakerQualities, setSpeakerQualities] = useState([]);
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);
  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  // Функция для обновления оценок конкретного спикера (объявлена один раз)
  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    updated[currentSpeakerIndex][rowIndex] = type;
    setSpeakerQualities(updated);
  };

  // Этап 4: Фактическая ценность информации
  const [usefulness, setUsefulness] = useState('');
  const handleUsefulness = (value) => {
    setUsefulness(value);
  };

  // Этап 5: Самые яркие мысли с мероприятия
  const [brightThoughts, setBrightThoughts] = useState('');
  const handleBrightThoughts = (e) => {
    setBrightThoughts(e.target.value);
  };

  // Этап 6: Что добавить в тренинг (множественный выбор)
  const additionalOptions = [
    "Практики",
    "Клиентских историй",
    "Статистических данных",
    "Фишек продаж",
    "Перерывов / 'Кофе'-пауз",
    "Разбор продуктовой линейки",
    "Детальный разбор параметров продукта",
    "Ящик Prosecco",
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);
  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter(item => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  // Этап 7: Общее впечатление от тренинга (3 смайлика)
  const [impression, setImpression] = useState('');
  const handleImpression = (value) => {
    setImpression(value);
  };

  // Управление этапами опроса:
  // 1 - выбор партнёра, 2 - выбор спикеров, 3 - оценка качеств спикера (для каждого спикера),
  // 4 - фактическая ценность, 5 - яркие мысли, 6 - добавить в тренинг, 7 - общее впечатление
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);
  const [isFinishClicked, setIsFinishClicked] = useState(false);

  const handleNext = () => {
    if (currentStep === 1 && !partner) {
      alert("Введите название партнёра");
      return;
    }
    if (currentStep === 2 && speakersList[0].fullName.trim() === "") {
      alert("Введите хотя бы одного спикера");
      return;
    }
    if (currentStep === 3) {
      if (speakerQualities[currentSpeakerIndex][0] === null) {
        alert("Выберите оценку качеств для спикера");
        return;
      }
      if (currentSpeakerIndex < speakersList.length - 1) {
        setCurrentSpeakerIndex(currentSpeakerIndex + 1);
        return; // остаёмся на этапе 3 для следующего спикера
      } else {
        setCurrentStep(currentStep + 1);
      }
      return;
    }
    if (currentStep === 4 && !usefulness) {
      alert("Выберите ответ на вопрос 'Фактическая ценность информации'");
      return;
    }
    if (currentStep === 5 && brightThoughts.trim().length < 5) {
      alert("Ответ на вопрос 'Самые яркие мысли с мероприятия' должен содержать минимум 5 символов");
      return;
    }
    if (currentStep === 7 && !impression) {
      alert("Выберите общее впечатление от тренинга");
      return;
    }
    setCurrentStep(currentStep + 1);
  };

const handleFinish = async () => {
  if (isFinishClicked) return;
  setIsFinishClicked(true);
  setIsFinished(true); // Немедленно переключаем на страницу благодарности

  // Формируем списки положительных и отрицательных качеств с ФИО спикеров
  const positiveQualitiesList = [];
  const negativeQualitiesList = [];
  speakersList.forEach((speaker, idx) => {
    qualityRows.forEach((row, j) => {
      const selection = speakerQualities[idx][j];
      if (selection === 'positive') {
        positiveQualitiesList.push(`${row.positive} (${speaker.fullName})`);
      } else if (selection === 'negative') {
        negativeQualitiesList.push(`${row.negative} (${speaker.fullName})`);
      }
    });
  });
  const positiveQualitiesStr = positiveQualitiesList.join(', ');
  const negativeQualitiesStr = negativeQualitiesList.join(', ');

  const data = {
    partner,
    speakers: speakersList.map(s => s.fullName),
    answers: {
      speakerQualities, // Массив оценок для каждого спикера (5 оценок на спикера)
      usefulness,
      brightThoughts,
      additionalSuggestions: additionalSelections,
      impression,
    },
    dateTime: new Date().toISOString(),
    positiveQualities: positiveQualitiesStr,   // добавлено
    negativeQualities: negativeQualitiesStr,   // добавлено
  };

  try {
    const response = await fetch('https://rgszh-miniapp.org/api/feedback/save', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
      credentials: 'include',
    });
    if (!response.ok) {
      alert("Ошибка при сохранении данных");
    }
  } catch (error) {
    console.error("Ошибка при отправке данных:", error);
    alert("Ошибка соединения с сервером");
  }
};


  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div>
            <h2>Введите название партнёра</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={({ value }) => setPartnerSuggestions(getPartnerSuggestions(value))}
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={suggestion => suggestion}
              renderSuggestion={suggestion => <div>{suggestion}</div>}
              inputProps={partnerInputProps}
            />
          </div>
        );
      case 2:
        return (
          <div>
            <h2>Введите ФИО спикера(ов)</h2>
            {speakersList.map((speaker, index) => (
              <div key={index}>
                <input
                  type="text"
                  value={speaker.fullName}
                  onChange={(e) => handleSpeakerChange(e, index)}
                  placeholder="Введите ФИО спикера"
                />
                {index === speakersList.length - 1 && (
                  <button onClick={handleAddSpeaker}>Добавить спикера</button>
                )}
              </div>
            ))}
          </div>
        );
      case 3:
        return (
          <div>
            <h2>Оцените качества спикера</h2>
            <p>Оцените спикера: <strong>{speakersList[currentSpeakerIndex].fullName}</strong></p>
            {qualityRows.map((row, idx) => (
              <div key={idx} className="quality-row">
                <button
                  onClick={() => handleQualitySelectForSpeaker(idx, 'positive')}
                  className={speakerQualities[currentSpeakerIndex] && speakerQualities[currentSpeakerIndex][idx] === 'positive' ? 'green' : ''}
                >
                  {row.positive}
                </button>
                <button
                  onClick={() => handleQualitySelectForSpeaker(idx, 'negative')}
                  className={speakerQualities[currentSpeakerIndex] && speakerQualities[currentSpeakerIndex][idx] === 'negative' ? 'red' : ''}
                >
                  {row.negative}
                </button>
              </div>
            ))}
            {speakersList.length > 1 && currentSpeakerIndex < speakersList.length - 1 && (
              <button className="btn next-btn" onClick={() => setCurrentSpeakerIndex(currentSpeakerIndex + 1)}>
                Далее для следующего спикера
              </button>
            )}
          </div>
        );
      case 4:
        return (
          <div>
            <h2>Фактическая ценность информации</h2>
            <div className="usefulness-container">
              <button
                onClick={() => handleUsefulness("Полезно в работе (буду применять на практике)")}
                className={usefulness === "Полезно в работе (буду применять на практике)" ? 'green' : ''}
              >
                Полезно в работе (буду применять на практике)
              </button>
              <button
                onClick={() => handleUsefulness("Бесполезно в работе (с моими клиентами это не работает)")}
                className={usefulness === "Бесполезно в работе (с моими клиентами это не работает)" ? 'red' : ''}
              >
                Бесполезно в работе (с моими клиентами это не работает)
              </button>
            </div>
          </div>
        );
      case 5:
        return (
          <div>
            <h2>Самые яркие мысли с мероприятия</h2>
            <input
              type="text"
              value={brightThoughts}
              onChange={handleBrightThoughts}
              placeholder="Введите текст (минимум 5 символов)"
            />
          </div>
        );
      case 6:
        return (
          <div>
            <h2>Что стоило бы добавить в тренинг?</h2>
            <div className="additional-container">
              {additionalOptions.map((opt, idx) => (
                <button
                  key={idx}
                  onClick={() => toggleAdditionalSelection(opt)}
                  className={additionalSelections.includes(opt) ? 'selected' : ''}
                >
                  {opt}
                </button>
              ))}
            </div>
          </div>
        );
      case 7:
        return (
          <div>
            <h2>Общее впечатление от тренинга</h2>
            <div className="impression-container">
              <button onClick={() => handleImpression('sad')}>
                <img src="/sad_smiley.png" alt="С грустным смайлом" />
              </button>
              <button onClick={() => handleImpression('neutral')}>
                <img src="/neutral_smiley.png" alt="Нейтральный смайл" />
              </button>
              <button onClick={() => handleImpression('happy')}>
                <img src="/happy_smiley.png" alt="Счастливый смайл" />
              </button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  if (isFinished) {
    return (
      <div className="thank-you-message">
        <h2>Благодарим за прохождение опроса!</h2>
      </div>
    );
  }

  return (
    <div
      className={`container ${keyboardVisible ? 'keyboard-visible' : ''}`}
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="logo">
        <img src={logo} alt="Логотип" />
      </div>

      {/* Форма ввода названия партнера */}
      <div className="input-label">Введите название партнера</div>
      <input
        type="text"
        className="partner-input"
        value={partner}
        onChange={handlePartnerChange}
        placeholder="Например, ВТБ"
        onFocus={handleFocus}
        onBlur={handleBlur}
      />

      {/* Кнопка "Далее" */}
      <button className="full-width-btn">Далее</button>
    </div>
  );
};

export default FeedbackPage;
