import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './WelcomePage.css';

const WelcomePage = () => {
  const navigate = useNavigate();
  const logoRef = useRef(null);
  const [redirected, setRedirected] = useState(false);

  useEffect(() => {
    // Выводим информацию о логотипе в консоль для диагностики
    console.log("Элемент logo:", logoRef.current);
    if (logoRef.current) {
      const rect = logoRef.current.getBoundingClientRect();
      console.log("Размеры logo:", rect.width, rect.height);
    }
    // Переход через 5 секунд
    if (!redirected) {
      setTimeout(() => {
        setRedirected(true);
        navigate('/register');
      }, 5000);
    }
  }, [navigate, redirected]);

  return (
    <div className="welcome-container">
      <div className="logo" ref={logoRef}></div>
      <h1 className="welcome-text">Добро пожаловать!</h1>
    </div>
  );
};

export default WelcomePage;
