import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';

// Данные для автодополнения
import famData from './components/autosuggest/surname.json';
import nameData from './components/autosuggest/firstname.json';
import patrData from './components/autosuggest/lastname.json';

// Фон и логотип
import backgroundImage from './components/background.png';
import logo from './components/logo.png';

// Стили
import './AssessmentPage.css';

function NewAssessmentPage() {
  // Поля ФИО
  const [famValue, setFamValue] = useState('');
  const [nameValue, setNameValue] = useState('');
  const [patrValue, setPatrValue] = useState('');

  // Подсказки для ФИО
  const [famHints, setFamHints] = useState([]);
  const [nameHints, setNameHints] = useState([]);
  const [patrHints, setPatrHints] = useState([]);

  // Списки из JSON
  const famList = famData.map(obj => obj.male || obj.female);
  const nameList = nameData.map(obj =>
    typeof obj === 'string' ? obj : (obj.firstName || obj.name)
  );
  const patrList = patrData.map(obj =>
    typeof obj === 'string' ? obj : (obj.patronymic || obj.name)
  );

  // Функции получения подсказок
  const getFamHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return famList.filter(x => x.toLowerCase().startsWith(str));
  };
  const getNameHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return nameList.filter(x => x.toLowerCase().startsWith(str));
  };
  const getPatrHints = val => {
    const str = val.trim().toLowerCase();
    if (str.length < 2) return [];
    return patrList.filter(x => x.toLowerCase().startsWith(str));
  };

  // Настройки Autosuggest
  const famProps = {
    placeholder: 'Введите фамилию',
    value: famValue,
    onChange: (e, { newValue }) =>
      setFamValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const nameProps = {
    placeholder: 'Введите имя',
    value: nameValue,
    onChange: (e, { newValue }) =>
      setNameValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const patrProps = {
    placeholder: 'Введите отчество',
    value: patrValue,
    onChange: (e, { newValue }) =>
      setPatrValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };

  // Состояния опроса
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  // ======================
  // 4) Вопросы
  // ======================
  const questions = [
    {
      question: "1. Какой формат работы вам ближе?",
      answers: [
        "Когда можно детально разобраться в процессах и построить устойчивую систему",
        "Когда задачи достаточно разнообразны и требуют гибкого подхода",
        "Когда есть чёткий алгоритм действий и понятный конечный результат"
      ],
    },
    {
      question: "2. Если коллега допустил серьёзную ошибку, что для вас логичнее сделать?",
      answers: [
        "Понять, что пошло не так, и вместе найти пути исправления",
        "В первую очередь скорректировать свои планы с учётом возникших проблем",
        "Сделать выводы для себя и минимизировать влияние на мою работу"
      ],
    },
    // ... остальные вопросы ...
  ];

  // ======================
  // 5) Логика шагов вопросов
  // ======================
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [userAnswers, setUserAnswers] = useState([]);
  const [isFinished, setIsFinished] = useState(false);

  // Для анимации плавного появления/исчезновения вопроса
  const [fadeTransition, setFadeTransition] = useState(false);

  // Список ответов «вперемешку» (если нужно случайно перемешивать варианты)
  const [shuffledAnswers, setShuffledAnswers] = useState([]);

  // При загрузке каждого вопроса — перемешиваем ответы
  useEffect(() => {
    if (isStarted && currentQuestion < questions.length) {
      const newShuffled = [...questions[currentQuestion].answers].sort(() => Math.random() - 0.5);
      setShuffledAnswers(newShuffled);
      setSelectedAnswer('');
    }
  }, [currentQuestion, isStarted]);

  // «Назад»
  const handlePrevQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
      // Удалим предыдущий ответ из userAnswers
      setUserAnswers(prev => prev.slice(0, -1));
      setSelectedAnswer('');
    }
  };

  // «Далее»
  const handleNextQuestion = () => {
    if (!selectedAnswer) {
      setErrorMessage('Выберите один из вариантов');
      return;
    }
    setErrorMessage('');
    setUserAnswers(prev => [...prev, selectedAnswer]);
    setFadeTransition(true);
    setTimeout(() => {
      if (currentQuestion < questions.length - 1) {
        setCurrentQuestion(prev => prev + 1);
      } else {
        // Если это был последний вопрос
        handleFinish();
      }
      setFadeTransition(false);
    }, 400);
  };

  // «Завершить»
  const handleFinish = async () => {
    setIsFinished(true);
    const finalAnswers = [...userAnswers];
    if (selectedAnswer) {
      finalAnswers.push(selectedAnswer);
    }
    console.log('Ответы пользователя:', finalAnswers);

    // Здесь — отправка на сервер
    const dataToSave = {
      surname: surnameValue,
      firstName: firstNameValue,
      patronymic: patronymicValue,
      dateTime: new Date().toISOString(),
      answers: finalAnswers
    };
    try {
      const response = await fetch('/api/proxy/assessment/candidate/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dataToSave),
      });
      if (!response.ok) {
        console.error('Ошибка при сохранении данных в XLSX');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
    }
  };

  // ======================
  // 7) Рендер основного контента (по аналогии с FeedbackPage)
  // ======================
  const renderMainContent = () => {
    if (isFinished) {
      // Спасибо, опрос завершён
      return (
        <div>
          <h2>Спасибо за прохождение опроса, {firstNameValue}!</h2>
          <p>Мы свяжемся, если ваши взгляды совпадают с нашими.</p>
        </div>
      );
    } else if (isStarted && currentQuestion < questions.length) {
      // Показ вопроса
      return (
        <div
          className="ap-question-block"
          style={{ opacity: fadeTransition ? 0 : 1, transition: 'opacity 0.4s' }}
        >
          <h2 className="ap-question-text">
            {questions[currentQuestion].question}
          </h2>
          <div className="ap-answers-block">
            {shuffledAnswers.map((answer, idx) => (
              <button
                key={idx}
                className={`ap-answer-btn ${selectedAnswer === answer ? 'ap-answer-selected' : ''}`}
                onClick={() => setSelectedAnswer(answer)}
              >
                {answer}
              </button>
            ))}
          </div>
          {errorMessage && <p className="ap-error-message">{errorMessage}</p>}
        </div>
      );
    } else {
      // Форма ввода ФИО (до начала опроса)
      return (
        <div>
          <h2 className="ap-fio-title">Введите ваши ФИО</h2>
          {errorMessage && <p className="ap-error-message">{errorMessage}</p>}
          <form className="ap-name-form">
            <div className="ap-input-container autosuggest-container">
              <label>Фамилия</label>
              <Autosuggest
                suggestions={surnameSuggestions}
                onSuggestionsFetchRequested={({ value }) =>
                  setSurnameSuggestions(getSurnameSuggestions(value))
                }
                onSuggestionsClearRequested={() => setSurnameSuggestions([])}
                getSuggestionValue={(s) => s}
                renderSuggestion={(s) => <div>{s}</div>}
                inputProps={surnameInputProps}
              />
            </div>
            <div className="ap-input-container autosuggest-container">
              <label>Имя</label>
              <Autosuggest
                suggestions={firstNameSuggestions}
                onSuggestionsFetchRequested={({ value }) =>
                  setFirstNameSuggestions(getFirstNameSuggestions(value))
                }
                onSuggestionsClearRequested={() => setFirstNameSuggestions([])}
                getSuggestionValue={(s) => s}
                renderSuggestion={(s) => <div>{s}</div>}
                inputProps={firstNameInputProps}
              />
            </div>
            <div className="ap-input-container autosuggest-container">
              <label>Отчество</label>
              <Autosuggest
                suggestions={patronymicSuggestions}
                onSuggestionsFetchRequested={({ value }) =>
                  setPatronymicSuggestions(getPatronymicSuggestions(value))
                }
                onSuggestionsClearRequested={() => setPatronymicSuggestions([])}
                getSuggestionValue={(s) => s}
                renderSuggestion={(s) => <div>{s}</div>}
                inputProps={patronymicInputProps}
              />
            </div>
          </form>
        </div>
      );
    }
  };

  // ======================
  // Итоговый рендер «по логике FeedbackPage»
  // ======================
  return (
    <div className="ap-container">
      {/* Логотип — как в FeedbackPage: просто img */}
      <div className="ap-logo-container">
        <img src={logo} alt="Logo" className="ap-logo" />
      </div>

      {/* Контейнер для всего содержимого (вопросы / форма / кнопки) */}
      <div className="ap-content-wrapper">
        {/* Основная часть (вопрос / форма ФИО / «спасибо») */}
        {renderMainContent()}

        {/* Кнопки. Показываем их только если НЕ завершено */}
        {!isFinished && (
          <>
            {/* Кнопка "Назад" (слева сверху) */}
            {isStarted && currentQuestion > 0 && (
              <div className="ap-back-btn-container">
                <button className="ap-back-btn" onClick={handlePrevQuestion}>
                  {/* Стрелка «Назад» */}
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 20l-8-8 8-8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              </div>
            )}

            {/* Кнопка "Далее" / "Завершить" / "Начать" (справа сверху) */}
            <div className="ap-top-btn-container">
              {/* Если опрос ещё не начался, показываем кнопку "Начать" */}
              {!isStarted ? (
                <button
                  className="ap-top-btn"
                  onClick={handleStart}
                  disabled={!surnameValue || !firstNameValue || !patronymicValue}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 4l8 8-8 8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              ) : currentQuestion < questions.length - 1 ? (
                <button
                  className="ap-top-btn"
                  onClick={handleNextQuestion}
                  disabled={!selectedAnswer}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                    <path
                      d="M12 4l8 8-8 8"
                      fill="none"
                      stroke="#fff"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  </svg>
                </button>
              ) : (
                // Если это последний вопрос — «Завершить»
                <button
                  className="ap-top-btn finish-btn"
                  onClick={handleFinish}
                  disabled={!selectedAnswer}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24" fill="currentColor">
                    <path d="M9 16.17L4.83 12l-1.42 1.41L9 19l10-10-1.41-1.41z" />
                  </svg>
                </button>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default AssessmentPage;
