import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import backgroundImage from './components/background.png';
import logo from './components/logo.png';
import surnames from './components/autosuggest/surname.json';
import firstnames from './components/autosuggest/firstname.json';
import patronymics from './components/autosuggest/lastname.json';
import './AssessmentPage.css';

function AssessmentPage() {
  /*** Фамилия ***/
  const [surnameValue, setSurnameValue] = useState('');
  const [surnameSuggestions, setSurnameSuggestions] = useState([]);
  const surnameList = surnames.map(item => item.male || item.female);

  const getSurnameSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return surnameList.filter(s => s.toLowerCase().includes(trimmed));
  };

  const [offsetY, setOffsetY] = useState(0);

  const onSurnameChange = (event, { newValue }) => {
    setSurnameValue(newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase());
  };

  const onSurnameSuggestionsFetchRequested = ({ value }) => {
    setSurnameSuggestions(getSurnameSuggestions(value));
  };

  const onSurnameSuggestionsClearRequested = () => {
    setSurnameSuggestions([]);
  };

  const surnameInputProps = {
    placeholder: 'Введите фамилию',
    value: surnameValue,
    onChange: onSurnameChange,
    onFocus: () => setSurnameSuggestions([]),
    required: true,
    autoComplete: 'off'
  };

  /*** Имя ***/
  const [firstNameValue, setFirstNameValue] = useState('');
  const [firstNameSuggestions, setFirstNameSuggestions] = useState([]);
  const firstNameList = firstnames.map(item => typeof item === 'string' ? item : (item.firstName || item.name));

  const getFirstNameSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return firstNameList.filter(s => s.toLowerCase().includes(trimmed));
  };

  const onFirstNameChange = (event, { newValue }) => {
    setFirstNameValue(newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase());
  };

  const onFirstNameSuggestionsFetchRequested = ({ value }) => {
    setFirstNameSuggestions(getFirstNameSuggestions(value));
  };

  const onFirstNameSuggestionsClearRequested = () => {
    setFirstNameSuggestions([]);
  };

  const firstNameInputProps = {
    placeholder: 'Введите имя',
    value: firstNameValue,
    onChange: onFirstNameChange,
    onFocus: () => setFirstNameSuggestions([]),
    required: true,
    autoComplete: 'off'
  };

  /*** Отчество ***/
  const [patronymicValue, setPatronymicValue] = useState('');
  const [patronymicSuggestions, setPatronymicSuggestions] = useState([]);
  const patronymicList = patronymics.map(item => typeof item === 'string' ? item : (item.patronymic || item.name));

  const getPatronymicSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return patronymicList.filter(s => s.toLowerCase().includes(trimmed));
  };

  const onPatronymicChange = (event, { newValue }) => {
    setPatronymicValue(newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase());
  };

  const onPatronymicSuggestionsFetchRequested = ({ value }) => {
    setPatronymicSuggestions(getPatronymicSuggestions(value));
  };

  const onPatronymicSuggestionsClearRequested = () => {
    setPatronymicSuggestions([]);
  };

  const patronymicInputProps = {
    placeholder: 'Введите отчество',
    value: patronymicValue,
    onChange: onPatronymicChange,
    onFocus: () => setPatronymicSuggestions([]),
    required: true,
    autoComplete: 'off'
  };

  /*** Опрос ***/
  const [isStarted, setIsStarted] = useState(false);
  const [isFinishClicked, setIsFinishClicked] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [userAnswers, setUserAnswers] = useState([]);
  const [isFinished, setIsFinished] = useState(false);
  const [shuffledAnswers, setShuffledAnswers] = useState([]);
  const [formError, setFormError] = useState('');

  // Массив вопросов
  const questions = [
    // Ваши вопросы здесь...
  ];

  useEffect(() => {
    const handleScroll = () => {
      setOffsetY(window.pageYOffset);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (isStarted && questions.length > 0) {
      shuffleAnswers();
    }
  }, [currentQuestion, isStarted]);

  const shuffleAnswers = () => {
    if (questions.length > 0 && currentQuestion < questions.length) {
      const shuffled = [...questions[currentQuestion].answers].sort(() => Math.random() - 0.5);
      setShuffledAnswers(shuffled);
    }
  };

  const handleStart = () => {
    console.log('Кнопка "Начать опрос" нажата'); // Отладка
    if (!surnameValue || !firstNameValue || !patronymicValue) {
      setFormError('Пожалуйста, заполните все поля');
      return;
    }
    setFormError('');
    setIsStarted(true);
    console.log('isStarted:', true); // Отладка
  };

  const handleNext = () => {
    if (!selectedAnswer) {
      setErrorMessage('Выберите 1 из 3 вариантов');
      setTimeout(() => setErrorMessage(''), 1000);
      return;
    }

    setUserAnswers(prev => [...prev, selectedAnswer]);
    setSelectedAnswer('');

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
      shuffleAnswers();
    } else {
      handleFinish();
    }
  };

  const handleFinish = async () => {
    if (isFinishClicked) return;

    setIsFinishClicked(true);
    setIsFinished(true);

    const data = {
      fullName: `${surnameValue} ${firstNameValue} ${patronymicValue}`,
      dateTime: new Date().toISOString(),
      answers: userAnswers,
      avoidOverwrite: true,
    };

    try {
      const response = await fetch("https://rgszh-miniapp.org/api/proxy/assessment/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer token",
        },
        body: JSON.stringify(data),
        credentials: 'include',
      });

      if (!response.ok) {
        alert("Ошибка при сохранении данных");
      }
    } catch (error) {
      console.error("Ошибка при отправке данных:", error);
      alert("Ошибка соединения с сервером");
    }
  };

  if (isFinished) {
    return (
      <div className="thank-you-message" style={{ backgroundImage: `url(${backgroundImage})`, backgroundPositionY: `${offsetY * 0.5}px` }}>
        <div className="logo" style={{ backgroundImage: `url(${logo})` }} />
        <h2>Благодарим за прохождение опроса, {firstNameValue}!</h2>
        <p>Если Ваши взгляды соответствуют взглядам компании - мы обязательно свяжемся!</p>
      </div>
    );
  }

  if (isStarted && questions.length > 0 && currentQuestion < questions.length) {
    return (
      <div className="container" style={{ backgroundImage: `url(${backgroundImage})`, backgroundPositionY: `${offsetY * 0.5}px` }}>
        <div className="logo" style={{ backgroundImage: `url(${logo})` }} />
        <h2>{questions[currentQuestion].question}</h2>
        <div className="answers-container">
          {shuffledAnswers.map((answer, index) => (
            <button
              key={index}
              className={`answer-btn ${selectedAnswer === answer ? 'selected' : ''}`}
              onClick={() => setSelectedAnswer(answer)}
            >
              {answer}
            </button>
          ))}
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <button
          className="btn next-btn"
          onClick={currentQuestion < questions.length - 1 ? handleNext : handleFinish}
          disabled={!selectedAnswer}
        >
          {currentQuestion < questions.length - 1 ? 'Далее' : 'Завершить опрос'}
        </button>
      </div>
    );
  }

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})`, backgroundPositionY: `${offsetY * 0.5}px` }}>
      <div className="logo" style={{ backgroundImage: `url(${logo})` }} />
      <h2>Введите ваши ФИО</h2>
      <form className="name-form">
        <div className="input-container">
          <label>Фамилия</label>
          <Autosuggest
            suggestions={surnameSuggestions}
            onSuggestionsFetchRequested={onSurnameSuggestionsFetchRequested}
            onSuggestionsClearRequested={onSurnameSuggestionsClearRequested}
            getSuggestionValue={(suggestion) => suggestion}
            renderSuggestion={(suggestion) => <div>{suggestion}</div>}
            inputProps={surnameInputProps}
            theme={{
              container: 'react-autosuggest__container',
              input: 'react-autosuggest__input',
              suggestionsContainer: 'react-autosuggest__suggestions-container',
              suggestionsList: 'react-autosuggest__suggestions-list',
              suggestion: 'react-autosuggest__suggestion',
              suggestionHighlighted: 'react-autosuggest__suggestion--highlighted',
            }}
          />
        </div>

        <div className="input-container">
          <label>Имя</label>
          <Autosuggest
            suggestions={firstNameSuggestions}
            onSuggestionsFetchRequested={onFirstNameSuggestionsFetchRequested}
            onSuggestionsClearRequested={onFirstNameSuggestionsClearRequested}
            getSuggestionValue={(suggestion) => suggestion}
            renderSuggestion={(suggestion) => <div>{suggestion}</div>}
            inputProps={firstNameInputProps}
          />
        </div>

        <div className="input-container">
          <label>Отчество</label>
          <Autosuggest
            suggestions={patronymicSuggestions}
            onSuggestionsFetchRequested={onPatronymicSuggestionsFetchRequested}
            onSuggestionsClearRequested={onPatronymicSuggestionsClearRequested}
            getSuggestionValue={(suggestion) => suggestion}
            renderSuggestion={(suggestion) => <div>{suggestion}</div>}
            inputProps={patronymicInputProps}
          />
        </div>

        {formError && <p className="error-message">{formError}</p>}

        <button
          type="button"
          className={`btn ${!surnameValue || !firstNameValue || !patronymicValue ? 'disabled-btn' : 'red-btn'}`}
          onClick={handleStart}
          disabled={!surnameValue || !firstNameValue || !patronymicValue}
        >
          Начать опрос
        </button>
      </form>
    </div>
  );
}

export default AssessmentPage;
