import React, { useEffect, useState } from 'react';
import './fonts.css';  // Подключаем файл со шрифтами
import './App.css';
import { useNavigate } from 'react-router-dom';
import logo from './logo.png';  // Импортируем логотип

const App = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [userName, setUserName] = useState('Гость');
  const [isRegistered, setIsRegistered] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    console.log("Компонент App загружен!");

    const getUserData = () => {
      if (window.Telegram && window.Telegram.WebApp) {
        try {
          const user = window.Telegram.WebApp.initDataUnsafe;
          console.log("User Data:", user);

          if (user && user.first_name) {
            setUserName(user.first_name);
          }
        } catch (error) {
          console.warn("Ошибка WebApp.initDataUnsafe:", error);
        }
      }
    };

    setTimeout(() => {
      setIsLoaded(true);
    }, 1500);

    getUserData();
  }, []);

  useEffect(() => {
    if (!isRegistered) {
      setTimeout(() => {
        console.log("Переход на регистрацию...");
        navigate('/register');
      }, 4000);  // Увеличил задержку перед переходом на регистрацию
    }
  }, [isRegistered, navigate]);

  return (
    <div className="App">
      <img src={logo} alt="Логотип" className="logo" />  {/* Логотип */}
      <h1 className={`welcome-text ${isLoaded ? 'move-up' : ''}`}>
        Добро пожаловать, {userName}
      </h1>
    </div>
  );
};

export default App;
