import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import mainmenuIcon    from './components/icons/mainmenu.png';
import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // === —Å–æ—Å—Ç–æ—è–Ω–∏—è ===
  const [birthDateParts, setBirthDateParts] = useState({ day: null, month: null, year: null });
  const [birthDate,      setBirthDate]      = useState(null);
  const [gender,         setGender]         = useState(null);   // "–º—É–∂—Å–∫–æ–π" / "–∂–µ–Ω—Å–∫–∏–π"
  const [calcType,       setCalcType]       = useState(null);   // 'premium' –∏–ª–∏ 'sum'
  const [amount,         setAmount]         = useState('');     // —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–Ω–∞—è —Å—É–º–º–∞
  const [amountError,    setAmountError]    = useState('');     // —Ç–µ–∫—Å—Ç –æ—à–∏–±–∫–∏ –ø–æ —Å—É–º–º–µ
  const [isProcessing,   setIsProcessing]   = useState(false);  // –∏–¥—ë—Ç –∑–∞–ø—Ä–æ—Å
  const [resultData,     setResultData]     = useState(null);   // –æ—Ç–≤–µ—Ç —Å–µ—Ä–≤–µ—Ä–∞
  const [slideIndex,     setSlideIndex]     = useState(0);      // –∏–Ω–¥–µ–∫—Å —Å–ª–∞–π–¥–∞ –≤ –¥–µ—Ç–∞–ª–∏–∑–∞—Ü–∏–∏

  // === –ø–∞—Ä–∞–º–µ—Ç—Ä—ã ===
  const term        = 5;
  const periodicity = '–µ–∂–µ–≥–æ–¥–Ω–æ';
  const currency    = '—Ä—É–±–ª–µ–π';

  // === —ç—Ñ—Ñ–µ–∫—Ç: —Å–æ–±–∏—Ä–∞–µ–º Date –∏–∑ birthDateParts ===
  useEffect(() => {
    const { day, month, year } = birthDateParts;
    if (day && month && year) {
      const d = Number(day), m = Number(month), y = Number(year);
      const dt = new Date(y, m - 1, d);
      if (!isNaN(dt.getTime())
          && dt.getDate()     === d
          && dt.getMonth()+1  === m
          && dt.getFullYear() === y) {
        setBirthDate(dt);
      } else {
        setBirthDate(null);
      }
    } else {
      setBirthDate(null);
    }
  }, [birthDateParts]);

  // === —É—Ç–∏–ª–∏—Ç—ã ===

  // —Ñ–æ—Ä–º–∞—Ç —Å—É–º–º—ã "1234567" ‚Üí "1.234.567"
  const formatSum = raw => {
    let digits = raw.replace(/\D/g, '');
    if (!digits) return '';
    let out = '';
    while (digits.length > 3) {
      out = '.' + digits.slice(-3) + out;
      digits = digits.slice(0, -3);
    }
    return digits + out;
  };

  // –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤–≤–æ–¥–∞ —Å—É–º–º—ã –∏ –≤–∞–ª–∏–¥–∞—Ü–∏—è
  const handleAmountChange = e => {
    const fmt = formatSum(e.target.value);
    setAmount(fmt);
    const num = parseInt(fmt.replace(/\./g, ''), 10) || 0;
    if (calcType === 'premium') {
      setAmountError(num < 60000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 60 000 ‚ÇΩ' : '');
    } else if (calcType === 'sum') {
      setAmountError(num < 450000 ? '–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –æ—Ç 450 000 ‚ÇΩ' : '');
    }
  };

  // —Ä–∞—Å—á—ë—Ç –≤–æ–∑—Ä–∞—Å—Ç–∞
  const getAge = date => {
    if (!(date instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - date.getFullYear();
    const m = today.getMonth() - date.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < date.getDate())) age--;
    return age;
  };

  // –∞–∫—Ç–∏–≤–Ω–∞ –ª–∏ –∫–Ω–æ–ø–∫–∞ "–î–∞–ª–µ–µ"
  const canGoNext = () => (
    birthDate instanceof Date &&
    !isNaN(birthDate.getTime()) &&
    gender &&
    calcType &&
    amount &&
    !amountError &&
    getAge(birthDate) >= 18
  );

  // –∑–∞–ø—Ä–æ—Å –Ω–∞ —Å–µ—Ä–≤–µ—Ä /api/snp/save
  const handleNext = async () => {
    if (!canGoNext()) return;
    const dateRu = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit', month: '2-digit', year: 'numeric'
    });
    const age = getAge(birthDate);

    const payload = {
      birthdate: dateRu,
      age,
      gender,
      payment: calcType === 'premium' ? '–°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞' : '–°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã',
      sum: parseInt(amount.replace(/\./g, ''), 10)
    };

    setIsProcessing(true);
    try {
      const res = await fetch('/api/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      if (res.ok) {
        setResultData(data);
        setSlideIndex(0);
      } else {
        console.error('–°–µ—Ä–≤–µ—Ä –≤–µ—Ä–Ω—É–ª –æ—à–∏–±–∫—É:', data);
      }
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–ø—Ä–æ—Å–∞:', err);
    }
    setIsProcessing(false);
  };

  // —Å–µ–≥–æ–¥–Ω—è—à–Ω—è—è –¥–∞—Ç–∞
  const todayStr = new Date().toLocaleDateString('ru-RU', {
    day: '2-digit', month: '2-digit', year: 'numeric'
  });

  // –∫–æ–Ω—Ç–µ–Ω—Ç —Ç—Ä—ë—Ö —Å–ª–∞–π–¥–æ–≤
  const slides = resultData ? [
    <div key={0} className="detail-slide">
      <div className="slide-header">–í —Ç–µ—á–µ–Ω–∏–µ {term} –ª–µ—Ç</div>
      <div className="slide-title">–í—ã –≤–Ω–æ—Å–∏—Ç–µ —Å—Ä–µ–¥—Å—Ç–≤–∞ –≤ –ø—Ä–æ–≥—Ä–∞–º–º—É</div>
      <div className="slide-amount">{formatSum(String(resultData.contribution))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–°—É–º–º–∞ –≤–∞—à–∏—Ö –≤–∑–Ω–æ—Å–æ–≤</div>
      <div className="slide-amount">{formatSum(String(resultData.totalContribution))} ‚ÇΩ</div>
    </div>,
    <div key={1} className="detail-slide">
      <div className="slide-header">–ù–∞—á–∏—Å–ª—è–µ–º –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–æ</div>
      <div className="slide-title">{resultData.accrualPercent}% –æ—Ç –∫–∞–∂–¥–æ–≥–æ –≤–∑–Ω–æ—Å–∞</div>
      <div className="slide-amount">{formatSum(String(resultData.accrualAmount))} ‚ÇΩ</div>
      <div className="slide-period">{periodicity}</div>
      <div className="slide-subtitle">–û–±—â–∞—è —Å—É–º–º–∞ –Ω–∞—á–∏—Å–ª–µ–Ω–∏–π</div>
      <div className="slide-amount">{formatSum(String(resultData.totalAccrual))} ‚ÇΩ</div>
    </div>,
    <div key={2} className="detail-slide">
      <div className="slide-header">–í—ã –ø–æ–ª—É—á–∞–µ—Ç–µ:</div>
      <div className="slide-icon">üí∞</div>
      <div className="slide-amount-big">{formatSum(String(resultData.finalAnnual))} ‚ÇΩ</div>
      <div className="slide-period">–ï–∂–µ–≥–æ–¥–Ω—ã–µ –Ω–∞–∫–æ–ø–ª–µ–Ω–∏—è</div>
      <div className="slide-amount">{formatSum(String(resultData.finalTotal))} ‚ÇΩ</div>
      <div className="slide-subtitle">–í–∞—à –∫–∞–ø–∏—Ç–∞–ª —á–µ—Ä–µ–∑ {term} –ª–µ—Ç</div>
    </div>
  ] : [];

  return (
    <div className="snp-container" style={{ backgroundImage: `url(${backgroundImage})` }}>

      {/* –õ–æ–≥–æ—Ç–∏–ø */}
      <div className="logo-container">
        <img src={logoImage} alt="–õ–æ–≥–æ—Ç–∏–ø" className="logo-image" />
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <h1 className="snp-title">–†–∞—Å—á—ë—Ç –ø–æ –ø—Ä–æ–≥—Ä–∞–º–º–µ "–°—Ç—Ä–∞—Ç–µ–≥–∏—è –Ω–∞ –ø—è—Ç—å. –ì–∞—Ä–∞–Ω—Ç"</h1>

      {/* === –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ === */}
      {!isProcessing && !resultData && (
        <div className="snp-form">

          {/* –î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è */}
          <div className="form-group">
            <label className="form-label">–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è:</label>
            <DateWheelPicker
              value={birthDateParts}
              onChange={val => {
                setBirthDateParts(prev => {
                  if (val instanceof Date) {
                    return {
                      day:   val.getDate().toString().padStart(2,'0'),
                      month: (val.getMonth()+1).toString().padStart(2,'0'),
                      year:  val.getFullYear().toString()
                    };
                  }
                  if (Array.isArray(val) && val.length === 3) {
                    const [d,m,y] = val;
                    return { day: d, month: m, year: y };
                  }
                  if (typeof val === 'object') {
                    return {
                      ...prev,
                      ...(val.day   != null ? { day:   String(val.day).padStart(2,'0') } : {}),
                      ...(val.month != null ? { month: String(val.month).padStart(2,'0') } : {}),
                      ...(val.year  != null ? { year:  String(val.year)                } : {})
                    };
                  }
                  if (typeof val === 'string') {
                    if (val.includes('.')) {
                      const [d,m,y] = val.split('.').map(s => s.padStart(2,'0'));
                      return { day: d, month: m, year: y };
                    }
                    if (val.includes('-')) {
                      const [y,m,d] = val.split('-').map(s => s.padStart(2,'0'));
                      return { day: d, month: m, year: y };
                    }
                  }
                  return prev;
                });
              }}
            />
          </div>

          {/* –ü–æ–ª */}
          <div className="form-group">
            <label className="form-label">–ü–æ–ª:</label>
            <div className="gender-selector">
              {['–º—É–∂—Å–∫–æ–π','–∂–µ–Ω—Å–∫–∏–π'].map(value => (
                <div
                  key={value}
                  className={`gender-option ${gender===value?'selected':''}`}
                  onClick={() => setGender(value)}
                >
                  <div className="gender-label">
                    {value==='–º—É–∂—Å–∫–æ–π'?'–ú—É–∂—Å–∫–æ–π':'–ñ–µ–Ω—Å–∫–∏–π'}
                  </div>
                  <div className="gender-icon">
                    {value==='–º—É–∂—Å–∫–æ–π'?'üë®':'üë©'}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* –†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶ */}
          <div className="form-group">
            <label className="form-label">–†–∞—Å—á—ë—Ç –æ—Ç‚Ä¶</label>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType==='premium'?'selected':''}`}
                onClick={() => { setCalcType('premium'); setAmount(''); setAmountError(''); }}
              >
                –°—Ç—Ä–∞—Ö–æ–≤–æ–≥–æ –≤–∑–Ω–æ—Å–∞
              </button>
              <button
                className={`insurance-btn ${calcType==='sum'?'selected':''}`}
                onClick={() => { setCalcType('sum'); setAmount(''); setAmountError(''); }}
              >
                –°—Ç—Ä–∞—Ö–æ–≤–æ–π —Å—É–º–º—ã
              </button>
            </div>
          </div>

          {/* –í–≤–æ–¥ —Å—É–º–º—ã */}
          {calcType && (
            <div className="form-group">
              <label className="form-label">–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –≤ —Ä—É–±–ª—è—Ö</label>
              <input
                type="text"
                className="insurance-sum-input"
                value={amount}
                onChange={handleAmountChange}
                placeholder={calcType==='premium'?'–ú–∏–Ω–∏–º—É–º 60 000':'–ú–∏–Ω–∏–º—É–º 450 000'}
              />
              {amountError && <div className="input-error-message">{amountError}</div>}
            </div>
          )}

        </div>
      )}

      {/* === –ü—Ä–æ—Ü–µ—Å—Å–∏–Ω–≥ === */}
      {isProcessing && (
        <div className="processing-container">
          <p>–í—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è —Ä–∞—Å—á—ë—Ç...</p>
          <div className="arrow-animation">‚Üí</div>
        </div>
      )}

      {/* === –û—Å–Ω–æ–≤–Ω—ã–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã === */}
      {!isProcessing && resultData && (
        <>
          <div className="result-container">
            <h2>–†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Ä–∞—Å—á—ë—Ç–∞</h2>
            <p><strong>–î–∞—Ç–∞ —Ä–∞—Å—á—ë—Ç–∞:</strong> {todayStr}</p>
            <p><strong>–ü–æ–ª:</strong> {resultData.gender}</p>
            <p><strong>–í–æ–∑—Ä–∞—Å—Ç –∑–∞—Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–Ω–æ–≥–æ:</strong> {resultData.age} –ª–µ—Ç</p>
            <p><strong>–°—Ä–æ–∫ —Å—Ç—Ä–∞—Ö–æ–≤–∞–Ω–∏—è:</strong> {term} –ª–µ—Ç</p>
            <p><strong>–ü–µ—Ä–∏–æ–¥–∏—á–Ω–æ—Å—Ç—å –≤–∑–Ω–æ—Å–æ–≤:</strong> {periodicity}</p>
            <p><strong>–í–∞–ª—é—Ç–∞:</strong> {currency}</p>
          </div>

          {/* === –°–ª–∞–π–¥–µ—Ä –ø–æ–¥—Ä–æ–±–Ω—ã—Ö —Ä–∞—Å—á—ë—Ç–æ–≤ === */}
          <div className="detail-slider-container">
            {slides[slideIndex]}
            <div className="detail-nav">
              {slideIndex>0 && (
                <button onClick={()=>setSlideIndex(sl=>sl-1)} className="nav-btn left">‚Üê</button>
              )}
              {slideIndex<slides.length-1 && (
                <button onClick={()=>setSlideIndex(sl=>sl+1)} className="nav-btn right">‚Üí</button>
              )}
            </div>
