import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSwipeable } from 'react-swipeable';
import './ProfilePage.css';
import backgroundImage from './components/background.png';

const ProfilePage = () => {
  const navigate = useNavigate();
  const userName = localStorage.getItem('userName') || 'Гость';
  const [swipeClass, setSwipeClass] = useState('');

  const handleLogout = () => {
    localStorage.removeItem('userToken');
    localStorage.removeItem('userUuid');
    navigate('/');
  };

  const handleSwipeLeft = () => {
  console.log("Свайп влево сработал!"); // Логируем свайп в консоли
  setSwipeClass('swipe-left');
  setTimeout(() => {
    navigate('/main-menu');
  }, 500);
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: (eventData) => {
      console.log("Детали свайпа:", eventData); // Логируем детали свайпа
      handleSwipeLeft();
    },
    preventDefault: true
  });

  useEffect(() => {
    Telegram.WebApp.expand(); // Разворачивает WebView
    console.log("Telegram WebView активирован");
  }, []);


  return (
    <div className={`profile-container ${swipeClass}`}
         style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center' }}
         {...swipeHandlers}>
      <h1 className="profile-text">{userName}, нажми чтобы выйти</h1>
      <button className="logout-btn" onClick={handleLogout}>
        Выйти
      </button>
    </div>
  );
};

export default ProfilePage;
