import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import surnames from './components/autosuggest/surname.json';
import firstnames from './components/autosuggest/firstname.json';
import patronymics from './components/autosuggest/lastname.json';

// Если нужен градиент — можно убрать import backgroundImage
import backgroundImage from './components/background.png';
import logo from './components/logo.png';

import './AssessmentPage.css';

function AssessmentPage() {
  // ------ 1) Состояния для ФИО ------
  const [surnameValue, setSurnameValue] = useState('');
  const [surnameSuggestions, setSurnameSuggestions] = useState([]);
  const surnameList = surnames.map(item => item.male || item.female);

  const [firstNameValue, setFirstNameValue] = useState('');
  const [firstNameSuggestions, setFirstNameSuggestions] = useState([]);
  const firstNameList = firstnames.map(item =>
    typeof item === 'string' ? item : (item.firstName || item.name)
  );

  const [patronymicValue, setPatronymicValue] = useState('');
  const [patronymicSuggestions, setPatronymicSuggestions] = useState([]);
  const patronymicList = patronymics.map(item =>
    typeof item === 'string' ? item : (item.patronymic || item.name)
  );

  // Функции для Autosuggest
  const getSurnameSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return surnameList.filter(s => s.toLowerCase().startsWith(trimmed));
  };
  const getFirstNameSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return firstNameList.filter(s => s.toLowerCase().startsWith(trimmed));
  };
  const getPatronymicSuggestions = (value) => {
    const trimmed = value.trim().toLowerCase();
    if (trimmed.length < 2) return [];
    return patronymicList.filter(s => s.toLowerCase().startsWith(trimmed));
  };

  // inputProps для Autosuggest
  const surnameInputProps = {
    placeholder: 'Введите фамилию',
    value: surnameValue,
    onChange: (e, { newValue }) =>
      setSurnameValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const firstNameInputProps = {
    placeholder: 'Введите имя',
    value: firstNameValue,
    onChange: (e, { newValue }) =>
      setFirstNameValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };
  const patronymicInputProps = {
    placeholder: 'Введите отчество',
    value: patronymicValue,
    onChange: (e, { newValue }) =>
      setPatronymicValue(
        newValue.charAt(0).toUpperCase() + newValue.slice(1).toLowerCase()
      ),
    autoComplete: 'off'
  };

  // ------ 2) Логика начала опроса ------
  const [isStarted, setIsStarted] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleStart = () => {
    if (!surnameValue.trim() || !firstNameValue.trim() || !patronymicValue.trim()) {
      setErrorMessage('Пожалуйста, заполните все поля.');
      return;
    }
    setErrorMessage('');
    setIsStarted(true);
  };

  // ------ 3) Массив вопросов (25 шт.) ------
  const questions = [
    // ... Все 25 вопросов, как раньше
    {
      question: '1. Какой формат работы вам ближе?',
      answers: [
        'Когда можно детально разобраться в процессах и построить устойчивую систему.',
        'Когда задачи достаточно разнообразны и требуют гибкого подхода.',
        'Когда есть чёткий алгоритм действий и понятный конечный результат.'
      ],
    },
    {
      question: '2. Если коллега допустил серьёзную ошибку...',
      answers: [
        'Понять, что пошло не так...',
        'В первую очередь скорректировать...',
        'Сделать выводы для себя...'
      ],
    },
    // ... и т.д. (до 25-го вопроса)
  ];

  // ------ 4) Процесс опроса ------
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [userAnswers, setUserAnswers] = useState([]);
  const [isFinished, setIsFinished] = useState(false);

  // Для плавной анимации исчезновения
  const [fadeTransition, setFadeTransition] = useState(false);

  // Параллакс (если нужно)
  const [offsetY, setOffsetY] = useState(0);
  useEffect(() => {
    const handleScroll = () => setOffsetY(window.pageYOffset);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Перемешиваем варианты ответов при каждом новом вопросе
  const [shuffledAnswers, setShuffledAnswers] = useState([]);
  useEffect(() => {
    if (isStarted && currentQuestion < questions.length) {
      const shuffled = [...questions[currentQuestion].answers].sort(() => Math.random() - 0.5);
      setShuffledAnswers(shuffled);
      setSelectedAnswer('');
    }
  }, [currentQuestion, isStarted]);

  const handleNextQuestion = () => {
    if (!selectedAnswer) {
      setErrorMessage('Выберите один из вариантов');
      return;
    }
    setErrorMessage('');
    setUserAnswers(prev => [...prev, selectedAnswer]);

    setFadeTransition(true);
    setTimeout(() => {
      if (currentQuestion < questions.length - 1) {
        setCurrentQuestion(prev => prev + 1);
      } else {
        setIsFinished(true);
        console.log('Ответы пользователя:', [...userAnswers, selectedAnswer]);
      }
      setFadeTransition(false);
    }, 400);
  };

  // Когда опрос завершён
  if (isFinished) {
    return (
      <div
        className="assessment-container"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundPositionY: `${offsetY * 0.5}px`
        }}
      >
        <h2>Спасибо за прохождение опроса, {firstNameValue}!</h2>
        <p>Мы свяжемся, если ваши взгляды совпадают с нашими.</p>
      </div>
    );
  }

  // Если опрос начат и есть вопросы
  if (isStarted && currentQuestion < questions.length) {
    const questionObj = questions[currentQuestion];
    return (
      <div
        className="assessment-container"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundPositionY: `${offsetY * 0.5}px`
        }}
      >
        {/* Кнопка-стрелка в правом верхнем углу */}
        <div className="top-btn-container">
          <button
            className="top-btn"
            onClick={handleNextQuestion}
            disabled={!selectedAnswer}
          >
            {/* SVG-стрелка вправо */}
            <svg xmlns="http://www.w3.org/2000/svg"
                 viewBox="0 0 24 24"
                 width="24"
                 height="24">
              <path d="M12 4l8 8-8 8"
                    fill="none"
                    stroke="#fff"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"/>
            </svg>
          </button>
        </div>

        <div
          className="question-block"
          style={{ opacity: fadeTransition ? 0 : 1, transition: 'opacity 0.4s' }}
        >
          <h2 className="question-text">{questionObj.question}</h2>

          <div className="answers-block">
            {shuffledAnswers.map((answer, idx) => (
              <button
                key={idx}
                className={`answer-btn ${selectedAnswer === answer ? 'answer-selected' : ''}`}
                onClick={() => setSelectedAnswer(answer)}
              >
                {answer}
              </button>
            ))}
          </div>

          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
      </div>
    );
  }

  // Иначе — форма ввода ФИО
  return (
    <div className="assessment-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      {/* Кнопка-стрелка "Начать опрос" (вверху справа) */}
      <div className="top-btn-container">
        <button
          className="top-btn"
          onClick={handleStart}
          disabled={!surnameValue || !firstNameValue || !patronymicValue}
        >
          <svg xmlns="http://www.w3.org/2000/svg"
               viewBox="0 0 24 24"
               width="24"
               height="24">
            <path d="M12 4l8 8-8 8"
                  fill="none"
                  stroke="#fff"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"/>
          </svg>
        </button>
      </div>

      {/* Логотип, смещаем чуть ниже, чтобы не перекрывался */}
      <div className="logo-assessment" style={{ backgroundImage: `url(${logo})` }} />

      <h2 className="fio-title">Введите ваши ФИО</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <form className="name-form">
        <div className="input-container">
          <label>Фамилия</label>
          <Autosuggest
            suggestions={surnameSuggestions}
            onSuggestionsFetchRequested={({ value }) =>
              setSurnameSuggestions(getSurnameSuggestions(value))
            }
            onSuggestionsClearRequested={() => setSurnameSuggestions([])}
            getSuggestionValue={s => s}
            renderSuggestion={s => <div>{s}</div>}
            inputProps={surnameInputProps}
          />
        </div>
        <div className="input-container">
          <label>Имя</label>
          <Autosuggest
            suggestions={firstNameSuggestions}
            onSuggestionsFetchRequested={({ value }) =>
              setFirstNameSuggestions(getFirstNameSuggestions(value))
            }
            onSuggestionsClearRequested={() => setFirstNameSuggestions([])}
            getSuggestionValue={s => s}
            renderSuggestion={s => <div>{s}</div>}
            inputProps={firstNameInputProps}
          />
        </div>
        <div className="input-container">
          <label>Отчество</label>
          <Autosuggest
            suggestions={patronymicSuggestions}
            onSuggestionsFetchRequested={({ value }) =>
              setPatronymicSuggestions(getPatronymicSuggestions(value))
            }
            onSuggestionsClearRequested={() => setPatronymicSuggestions([])}
            getSuggestionValue={s => s}
            renderSuggestion={s => <div>{s}</div>}
            inputProps={patronymicInputProps}
          />
        </div>
      </form>
    </div>
  );
}

export default AssessmentPage;
