import React, { useState, useEffect } from 'react';
import Autosuggest from 'react-autosuggest';
import backgroundImage from './components/background.png'; // Фоновое изображение
import happyGif from './components/animations/happy.gif';
import loyalGif from './components/animations/loyal.gif';
import sadGif from './components/animations/sad.gif';
import { useSwipeable } from 'react-swipeable';

import './FeedbackPage.css';

// Импорт данных партнёров и спикеров
import partners from './components/autosuggest/partners.json';
import speakersData from './components/autosuggest/speakers.json';

const FeedbackPage = () => {
  /********** Функции обработки фокуса для iOS **********/
  const handleFocus = () => {
    document.body.style.overflow = 'hidden'; // Отключаем прокрутку
  };

  const handleBlur = () => {
    document.body.style.overflow = ''; // Включаем прокрутку обратно
  };

  /********** Шаг 1: Ввод партнёра **********/
  const [partner, setPartner] = useState('');
  const [partnerSuggestions, setPartnerSuggestions] = useState([]);

  const getPartnerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return partners.filter(
      (p) => typeof p === 'string' && p.toLowerCase().includes(inputValue)
    );
  };

  const handlePartnerChange = (e, { newValue }) => {
    setPartner(newValue);
  };

  const partnerInputProps = {
    placeholder: 'Название партнёра',
    value: partner,
    onChange: handlePartnerChange,
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  };

  /********** Шаг 2: Ввод ФИО спикера(ов) **********/
  const [speakersList, setSpeakersList] = useState([{ fullName: '', id: 1 }]);
  const [speakersSuggestions, setSpeakersSuggestions] = useState([]);

  const getSpeakerSuggestions = (value) => {
    const inputValue = (value || '').trim().toLowerCase();
    if (inputValue.length < 2) return [];
    return speakersData.filter(
      (s) => typeof s === 'string' && s.toLowerCase().includes(inputValue)
    );
  };

  const handleSpeakerChange = (index, e, { newValue }) => {
    const updated = [...speakersList];
    updated[index].fullName = newValue;
    setSpeakersList(updated);
  };

  const handleAddSpeaker = () => {
    setSpeakersList([...speakersList, { fullName: '', id: speakersList.length + 1 }]);
  };

  const speakerInputProps = (index) => ({
    placeholder: 'Введите ФИО спикера (например, Иванов Иван)',
    value: speakersList[index].fullName,
    onChange: (e, { newValue }) => handleSpeakerChange(index, e, { newValue }),
    autoComplete: 'off',
    onFocus: handleFocus,
    onBlur: handleBlur,
  });

  /********** Шаг 3: Оценка качеств спикера **********/
  const qualityRows = [
    { positive: 'Экспертный', negative: 'Компетентный' },
    { positive: 'Энергичный', negative: 'Пассивный' },
    { positive: 'Мотивирующий', negative: 'Невдохновляющий' },
    { positive: 'Харизматичный', negative: 'Душный' },
    { positive: 'Доступно доносит информацию', negative: 'Говорит сложными терминами' },
  ];

  // Для каждого спикера создаём массив из 5 значений: 'positive' | 'negative' | null
  const [speakerQualities, setSpeakerQualities] = useState([]);
  // currentSpeakerIndex определяет, какого спикера сейчас оценивают
  const [currentSpeakerIndex, setCurrentSpeakerIndex] = useState(0);

  useEffect(() => {
    setSpeakerQualities(speakersList.map(() => Array(5).fill(null)));
  }, [speakersList]);

  const handleQualitySelectForSpeaker = (rowIndex, type) => {
    const updated = [...speakerQualities];
    updated[currentSpeakerIndex][rowIndex] =
      updated[currentSpeakerIndex][rowIndex] === type ? null : type;
    setSpeakerQualities(updated);
  };

  const countSelectedQualities = () => {
    if (!speakerQualities[currentSpeakerIndex]) return 0;
    return speakerQualities[currentSpeakerIndex].filter(q => q !== null).length;
  };

  /********** Шаг 4: Фактическая ценность информации **********/
  // Здесь вместо текстового поля – 2 кнопки: "Полезно" и "Бесполезно"
  const [usefulness, setUsefulness] = useState('');

  /********** Шаг 5: Яркие мысли **********/
  const [brightThoughts, setBrightThoughts] = useState('');

  /********** Шаг 6: Дополнительные предложения **********/
  const additionalOptions = [
    'Практики',
    'Клиентских историй',
    'Статистических данных',
    'Фишек продаж',
    "Перерывов / 'Кофе'-пауз",
    'Разбор продуктовой линейки',
    'Детальный разбор параметров продукта',
  ];
  const [additionalSelections, setAdditionalSelections] = useState([]);
  const toggleAdditionalSelection = (option) => {
    if (additionalSelections.includes(option)) {
      setAdditionalSelections(additionalSelections.filter(item => item !== option));
    } else {
      setAdditionalSelections([...additionalSelections, option]);
    }
  };

  /********** Шаг 7: Общее впечатление от тренинга **********/
  const [impression, setImpression] = useState('');

  /********** Логика шагов **********/
  // currentStep: 1 – партнёр, 2 – спикеры, 3 – оценка качеств, 4 – полезность, 5 – яркие мысли, 6 – доп. предложения, 7 – общее впечатление
  const [currentStep, setCurrentStep] = useState(1);
  const [isFinished, setIsFinished] = useState(false);

  // Функция формирования массива обратной связи по спикерам
  const getSpeakersFeedback = () => {
    return speakersList.map((speaker, i) => ({
      fullName: speaker.fullName,
      qualities: speakerQualities[i]
    }));
  };

  // Функция проверки возможности перехода на следующий шаг
  const canGoNext = () => {
    switch (currentStep) {
      case 1:
        return partner.trim() !== '' && partners.includes(partner);
      case 2:
        return speakersList.every(s => s.fullName.trim() !== '' && speakersData.includes(s.fullName));
      case 3:
        // Для текущего спикера должны быть выбраны ровно 3 качества
        return countSelectedQualities() === 3;
      case 4:
        return usefulness === 'Полезно в работе (буду применять на практике)' ||
               usefulness === 'Бесполезно в работе (с моими клиентами это не работает)';
      case 5:
        return brightThoughts.trim().length >= 5;
      case 6:
        return additionalSelections.length > 0;
      case 7:
        return impression.trim() !== '';
      default:
        return true;
    }
  };

  // Обновлённая функция handleNext: на шаге 3, если несколько спикеров, сначала переключаем currentSpeakerIndex
  const handleNext = () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для перехода к следующему шагу');
      return;
    }
    if (currentStep === 3) {
      // Если оценка для текущего спикера не завершена (не выбрано ровно 3 качества) – не переключаем
      if (countSelectedQualities() !== 3) {
        alert(`Для спикера "${speakersList[currentSpeakerIndex].fullName}" выберите ровно 3 качества`);
        return;
      }
      // Если есть ещё спикеры, переключаем currentSpeakerIndex, оставаясь на шаге 3
      if (currentSpeakerIndex < speakersList.length - 1) {
        setCurrentSpeakerIndex(currentSpeakerIndex + 1);
        return;
      }
    }
    setCurrentStep(prev => prev + 1);
  };

  const handlePrev = () => {
    // Если мы на шаге 3 и не первый спикер, можем переключить currentSpeakerIndex назад
    if (currentStep === 3 && currentSpeakerIndex > 0) {
      setCurrentSpeakerIndex(currentSpeakerIndex - 1);
      return;
    }
    if (currentStep > 1) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const handleFinish = async () => {
    if (!canGoNext()) {
      alert('Не выполнены условия для завершения опроса');
      return;
    }
    setIsFinished(true);
    const data = {
      partner,
      dateTime: new Date().toISOString(),
      commonAnswers: {
        usefulness,
        brightThoughts,
        additionalSuggestions: additionalSelections.join(', '),
        impression,
      },
      speakersFeedback: getSpeakersFeedback()
    };
    try {
      const response = await fetch('https://rgszh-miniapp.org/api/proxy/feedback/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
        credentials: 'include',
      });
      if (!response.ok) {
        alert('Ошибка при сохранении данных');
      }
    } catch (error) {
      console.error('Ошибка при отправке данных:', error);
      alert('Ошибка соединения с сервером');
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => handlePrev(),
    onSwipedRight: () => { if (canGoNext()) handleNext(); },
    preventDefault: true,
  });

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="autosuggest-container">
            <h2>Введите название партнёра</h2>
            <Autosuggest
              suggestions={partnerSuggestions}
              onSuggestionsFetchRequested={({ value }) =>
                setPartnerSuggestions(getPartnerSuggestions(value))
              }
              onSuggestionsClearRequested={() => setPartnerSuggestions([])}
              getSuggestionValue={suggestion => suggestion}
              renderSuggestion={suggestion => <div>{suggestion}</div>}
              inputProps={partnerInputProps}
            />
          </div>
        );
      case 2:
        return (
          <div>
            <h2>Введите ФИО спикера</h2>
            {speakersList.map((speaker, index) => (
              <div key={speaker.id} className="speaker-row">
                <Autosuggest
                  suggestions={speakersSuggestions}
                  onSuggestionsFetchRequested={({ value }) =>
                    setSpeakersSuggestions(getSpeakerSuggestions(value))
                  }
                  onSuggestionsClearRequested={() => setSpeakersSuggestions([])}
                  getSuggestionValue={suggestion => suggestion}
                  renderSuggestion={suggestion => <div>{suggestion}</div>}
                  inputProps={speakerInputProps(index)}
                />
              </div>
            ))}
            <button type="button" className="add-speaker-btn" onClick={handleAddSpeaker}>
              <span className="add-speaker-symbol">+</span>
            </button>
          </div>
        );
      case 3:
        return (
          <div>
            <h2>Оцените качества спикера</h2>
            <h3>{speakersList[currentSpeakerIndex].fullName}</h3>
            {qualityRows.map((row, index) => {
              const currentValue = speakerQualities[currentSpeakerIndex]?.[index];
              return (
                <div key={index} className="quality-row">
                  <button
                    type="button"
                    className={`quality-option-btn ${currentValue === 'positive' ? 'positive-selected' : ''}`}
                    onClick={() => handleQualitySelectForSpeaker(index, 'positive')}
                  >
                    {row.positive}
                  </button>
                  <button
                    type="button"
                    className={`quality-option-btn ${currentValue === 'negative' ? 'negative-selected' : ''}`}
                    onClick={() => handleQualitySelectForSpeaker(index, 'negative')}
                  >
                    {row.negative}
                  </button>
                </div>
              );
            })}
            {countSelectedQualities() < 3 && (
              <p className="quality-warning">Выберите минимум 3 качества</p>
            )}
          </div>
        );
      case 4:
        return (
          <div>
            <h2>Оцените полезность информации</h2>
            <div className="usefulness-container">
              <button
                type="button"
                className={`usefulness-btn ${usefulness === 'Полезно в работе (буду применять на практике)' ? 'positive-selected' : ''}`}
                onClick={() => setUsefulness('Полезно в работе (буду применять на практике)')}
              >
                Полезно в работе (буду применять на практике)
              </button>
              <button
                type="button"
                className={`usefulness-btn ${usefulness === 'Бесполезно в работе (с моими клиентами это не работает)' ? 'negative-selected' : ''}`}
                onClick={() => setUsefulness('Бесполезно в работе (с моими клиентами это не работает)')}
              >
                Бесполезно в работе (с моими клиентами это не работает)
              </button>
            </div>
          </div>
        );
      case 5:
        return (
          <div>
            <h2>Яркие мысли</h2>
            <textarea
              className="bright-thoughts-textarea"
              value={brightThoughts}
              onChange={(e) => setBrightThoughts(e.target.value)}
            />
          </div>
        );
      case 6:
        return (
          <div>
            <h2>Дополнительные предложения</h2>
            {additionalOptions.map(option => (
              <button
                key={option}
                type="button"
                className={`add-option-btn ${additionalSelections.includes(option) ? 'selected' : ''}`}
                onClick={() => toggleAdditionalSelection(option)}
              >
                {option}
              </button>
            ))}
          </div>
        );
      case 7:
        return (
          <div>
            <h2>Общее впечатление от тренинга</h2>
            <div className="impression-container">
              <button className="impression-btn" onClick={() => setImpression('happy')}>
                <img src={happyGif} alt="Happy" />
              </button>
              <button className="impression-btn" onClick={() => setImpression('neutral')}>
                <img src={loyalGif} alt="Neutral" />
              </button>
              <button className="impression-btn" onClick={() => setImpression('sad')}>
                <img src={sadGif} alt="Sad" />
              </button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  if (isFinished) {
    return (
      <div className="thank-you-message" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <h2>Спасибо за прохождение опроса!</h2>
      </div>
    );
  }

  return (
    <div className="feedback-container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="content-wrapper" {...swipeHandlers}>
        {renderStep()}
        <div className="top-btn-container">
          {currentStep < 7 ? (
            <button className="top-btn" onClick={handleNext} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M12 4l8 8-8 8" />
              </svg>
            </button>
          ) : (
            <button className="top-btn" onClick={handleFinish} disabled={!canGoNext()}>
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none">
                <path d="M5 13l4 4L19 7" />
              </svg>
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default FeedbackPage;
