// src/SNPPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import logoImage       from './components/logo.png';
import backgroundImage from './components/background.png';
import DateWheelPicker from './DateWheelPicker';

import './PollsPage.css';
import './SNPPage.css';

export default function SNPPage() {
  const navigate = useNavigate();

  // --- Общие состояния ---
  const [step, setStep]                   = useState(1);
  const [carouselIndex, setCarouselIndex] = useState(0);
  const [showContact, setShowContact]     = useState(false);
  const [isProcessing, setIsProcessing]   = useState(false);
  const [resultData, setResultData]       = useState(null);

  // --- Шаг 1: почта ---
  const [email, setEmail]           = useState('');
  const [emailError, setEmailError] = useState('');
  const emailValid = email.toLowerCase().endsWith('@vtb.ru');

  // --- Шаг 2: личные данные ---
  const [birthDate, setBirthDate]   = useState(new Date());
  const [gender, setGender]         = useState(null);    // 'мужской' | 'женский' | null
  const [calcType, setCalcType]     = useState(null);    // 'premium' | 'sum' | null
  const [amountRaw, setAmountRaw]   = useState('');
  const [amountDisplay, setAmountDisplay] = useState('');
  const [amountError, setAmountError]     = useState('');

  // --- Шаг 3: контактная форма ---
  const [surname, setSurname]       = useState('');
  const [firstName, setFirstName]   = useState('');
  const [patronymic, setPatronymic] = useState('');
  const [city, setCity]             = useState('');

  // --- Утилиты ---
  function getAge(d) {
    if (!(d instanceof Date)) return 0;
    const today = new Date();
    let age = today.getFullYear() - d.getFullYear();
    if (
      today.getMonth() < d.getMonth() ||
      (today.getMonth() === d.getMonth() && today.getDate() < d.getDate())
    ) {
      age--;
    }
    return age;
  }

  function isAmountValid() {
    const n = parseInt(amountRaw, 10) || 0;
    return (calcType === 'premium' && n >= 60000) || (calcType === 'sum' && n >= 450000);
  }

  function canGoNext() {
    return (
      step === 2 &&
      getAge(birthDate) >= 18 &&
      gender &&
      calcType &&
      amountRaw &&
      isAmountValid()
    );
  }

  // --- Шаг 1 → Шаг 2 ---
  function handleEmailNext() {
    if (!emailValid) {
      setEmailError('Используйте корпоративную почту домена @vtb.ru');
      return;
    }
    setEmailError('');
    // Сброс полей шага 2 перед тем, как показывать его заново
    setGender(null);
    setCalcType(null);
    setAmountRaw('');
    setAmountDisplay('');
    setAmountError('');
    setStep(2);
  }

  // --- Форматирование суммы при вводе ---
  function onAmountChange(e) {
    const raw = e.target.value.replace(/\D/g, '');
    setAmountRaw(raw);
    let d = raw,
      o = '';
    while (d.length > 3) {
      o = '.' + d.slice(-3) + o;
      d = d.slice(0, -3);
    }
    setAmountDisplay(d + o);

    const n = parseInt(raw, 10) || 0;
    if (calcType === 'premium' && n < 60000) setAmountError('Мин. 60 000 ₽');
    else if (calcType === 'sum' && n < 450000) setAmountError('Мин. 450 000 ₽');
    else setAmountError('');
  }

  // --- Шаг 2 → Отправка на сервер ---
  async function handleNext() {
    if (!canGoNext()) return;
    setIsProcessing(true);

    const dateStr = birthDate.toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });

    const payload = {
      email,
      birthdate: dateStr,
      age: getAge(birthDate),
      gender,
      payment: calcType === 'premium' ? 'Страхового взноса' : 'Страховой суммы',
      sum: parseInt(amountRaw, 10),
    };

    try {
      const proxyRes = await fetch('/api/proxy/snp/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (!proxyRes.ok) {
        console.error('Ошибка прокси', proxyRes.status, await proxyRes.text());
        alert(`Ошибка ${proxyRes.status}`);
      } else {
        const data = await proxyRes.json();
        setResultData(data);
        setCarouselIndex(0);
      }
    } catch (err) {
      console.error(err);
      alert('Не удалось выполнить запрос');
    } finally {
      setIsProcessing(false);
    }
  }

  function goHome() {
    navigate(-1);
  }

  // --- Экран «Выполняется расчёт…» ---
  if (isProcessing) {
    return (
      <div className="processing-screen">
        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>
        <h2 className="processing-title">
          Выполняется расчёт по программе
          <br />
          «Стратегия на пять. Гарант»
        </h2>
        <div className="arrow-animation">→</div>
      </div>
    );
  }

  // --- Итоговый экран + Карусель или Форма контакта ---
  if (resultData) {
    const todayStr = new Date().toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });

    // Форматер для денежных значений
    const fmtMoney = new Intl.NumberFormat('ru-RU', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    });

    // Извлекаем нужные поля (названия ключей должны совпадать с тем, что вы отдаёте с сервера)
    const c13 = Number(resultData.H13) || 0; // ячейка C13
    const c16 = Number(resultData.H16) || 0; // ячейка C16
    const ratePercent = Number(resultData.H12) || 0; // ячейка H12
    const gainPerYear = Number(resultData.H13) || 0; // пример: H13 → 52800 (ежегодно)
    const totalGain   = Number(resultData.H16) || 0; // пример: H16 → 264000 (общая сумма)

    return (
      <div
        className="snp-container"
        style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        <div className="polls-header">
          <button className="back-btn" onClick={goHome}>
            <svg viewBox="0 0 24 24">
              <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
            </svg>
          </button>

          <div className="logo-wrapper animate-logo">
            <img className="poll-logo" src={logoImage} alt="Логотип" />
          </div>

          <button
            className="repeat-btn"
            onClick={() => {
              setResultData(null);
              setStep(2);
              setShowContact(false);
              setCarouselIndex(0);
            }}
          >
            <svg viewBox="0 0 24 24">
              <path
                d="M12 4V1L8 5l4 4V6
                   c4.41 0 8 3.59 8 8
                   s-3.59 8-8 8-8-3.59-8-8h2
                   c0 3.31 2.69 6 6 6s6-2.69 6-6
                   -2.69-6-6-6v3l-4-4 4-4z"
                fill="white"
              />
            </svg>
          </button>
        </div>

        <div className="content-wrapper">
          {!showContact ? (
            <>
              <h1 className="polls-title">
                Расчёт по программе «Стратегия на пять. Гарант»
              </h1>

              <div className="params-container">
                <h2>Параметры</h2>
                <p>Дата расчёта: {todayStr}</p>
                <p>Возраст: {getAge(birthDate)} лет</p>
                <p>Пол: {gender}</p>
                <p>
                  Расчёт от:
                  {calcType === 'premium'
                    ? '  Страхового взноса'
                    : '  Страховой суммы'}
                </p>
              </div>

              <div className="carousel-container">
                {/* Индикаторы */}
                <div className="carousel-indicators">
                  {[0, 1, 2].map((i) => (
                    <span key={i} className={i === carouselIndex ? 'active' : ''} />
                  ))}
                </div>

                {/* «Обрезающее» окно */}
                <div className="carousel-clipper">
                  <div
                    className="carousel-wrapper"
                    style={{ transform: `translateX(-${carouselIndex * 100}%)` }}
                  >
                    {/* === Первая страница === */}
                    <div className="carousel-page">
                      <p className="first-line">В течение 5 лет</p>

                      <p className="unit">Вы вносите средства в программу</p>
                      <p className="value">
                        {fmtMoney.format(c13)} ₽
                      </p>

                      <p className="unit">Ежегодно</p>

                      <p className="value">
                        {fmtMoney.format(c16)} ₽
                      </p>
                      <p className="unit">Сумма ваших взносов</p>
                    </div>

                    {/* === Вторая страница === */}
                    <div className="carousel-page">
                      <p className="first-line">НАЧИСЛЯЕМ ДОПОЛНИТЕЛЬНО</p>

                      <p className="unit">
                        {ratePercent}% от каждого взноса
                      </p>

                      <p className="value">
                        {fmtMoney.format(gainPerYear)} ₽
                      </p>

                      <p className="unit">Ежегодно</p>

                      <p className="value">
                        {fmtMoney.format(totalGain)} ₽
                      </p>
                      <p className="unit">Общая сумма начислений</p>
                    </div>

                    {/* === Третья страница (пустая, зарезервирована) === */}
                    <div className="carousel-page"></div>
                  </div>
                </div>

                {/* Кнопки «‹» / «›» */}
                {carouselIndex > 0 && (
                  <button
                    className="carousel-control prev"
                    onClick={() => setCarouselIndex((i) => i - 1)}
                  >
                    ‹
                  </button>
                )}
                {carouselIndex < 2 && (
                  <button
                    className="carousel-control next"
                    onClick={() => setCarouselIndex((i) => i + 1)}
                  >
                    ›
                  </button>
                )}
              </div>

              <button className="polls-btn" onClick={() => setShowContact(true)}>
                Связаться с менеджером
              </button>
            </>
          ) : (
            <>
              <h1 className="polls-title">Ваши контактные данные</h1>

              <div className="email-step">
                <label className="email-label">Фамилия</label>
                <input
                  type="text"
                  value={surname}
                  onChange={(e) => setSurname(e.target.value)}
                />
              </div>
              <div className="email-step">
                <label className="email-label">Имя</label>
                <input
                  type="text"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                />
              </div>
              <div className="email-step">
                <label className="email-label">Отчество</label>
                <input
                  type="text"
                  value={patronymic}
                  onChange={(e) => setPatronymic(e.target.value)}
                />
              </div>
              <div className="email-step">
                <label className="email-label">Город работы</label>
                <input
                  type="text"
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                />
              </div>
              <button
                className="polls-btn"
                disabled={!(surname && firstName && patronymic && city)}
                onClick={() => alert('Данные отправлены менеджеру')}
              >
                Отправить менеджеру
              </button>
            </>
          )}
        </div>
      </div>
    );
  }

  // --- Шаги 1 и 2 (ввод) ---
  return (
    <div
      className="snp-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="polls-header">
        <button className="back-btn" onClick={goHome}>
          <svg viewBox="0 0 24 24">
            <path d="M3 11l9-8 9 8v10a1 1 0 0 1-1 1h-5v-7h-6v7H4a1 1 0 0 1-1-1V11z" />
          </svg>
        </button>

        <div className="logo-wrapper animate-logo">
          <img className="poll-logo" src={logoImage} alt="Логотип" />
        </div>

        <button
          className="forward-btn"
          onClick={step === 1 ? handleEmailNext : handleNext}
          disabled={step === 1 ? !emailValid : !canGoNext()}
        >
          <span className="arrow-icon">→</span>
        </button>
      </div>

      <div className="content-wrapper">
        {step === 1 && (
          <>
            <div className="email-step">
              <label className="email-label">Корпоративная почта ВТБ:</label>
              <input
                type="email"
                placeholder="example@vtb.ru"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            {emailError && <div className="input-error">{emailError}</div>}
            <button
              className="polls-btn"
              onClick={handleEmailNext}
              disabled={!emailValid}
            >
              Продолжить
            </button>
          </>
        )}

        {step === 2 && (
          <>
            <h1 className="polls-title">
              Расчёт по программе «Стратегия на пять. Гарант»
            </h1>

            <div className="date-section">
              <label className="section-label">Дата рождения:</label>
              <DateWheelPicker
                value={birthDate}
                onChange={(val) => {
                  if (val instanceof Date) {
                    setBirthDate(val);
                  } else if (val?.day) {
                    setBirthDate(new Date(val.year, val.month - 1, val.day));
                  }
                }}
              />
            </div>

            <div className="section-label">Выберите пол:</div>
            <div className="gender-section">
              <button
                className={`insurance-btn ${gender === 'мужской' ? 'selected' : ''}`}
                onClick={() => setGender('мужской')}
              >
                <div>Мужской</div>
                <div className="emoji">👨</div>
              </button>
              <button
                className={`insurance-btn ${gender === 'женский' ? 'selected' : ''}`}
                onClick={() => setGender('женский')}
              >
                <div>Женский</div>
                <div className="emoji">👩</div>
              </button>
            </div>

            <div className="section-label">Расчёт от…</div>
            <div className="insurance-options">
              <button
                className={`insurance-btn ${calcType === 'premium' ? 'selected' : ''}`}
                onClick={() => setCalcType('premium')}
              >
                <div>Страхового</div>
                <div>взноса</div>
              </button>
              <button
                className={`insurance-btn ${calcType === 'sum' ? 'selected' : ''}`}
                onClick={() => setCalcType('sum')}
              >
                <div>Страховой</div>
                <div>суммы</div>
              </button>
            </div>

            {calcType && (
              <>
                <div className="section-label">Сумма (₽):</div>
                <div className="sum-section">
                  <input
                    type="text"
                    placeholder={calcType === 'premium' ? 'Мин. 60 000' : 'Мин. 450 000'}
                    value={amountDisplay}
                    onChange={onAmountChange}
                  />
                </div>
                {amountError && <div className="input-error">{amountError}</div>}
              </>
            )}
          </>
        )}
      </div>
    </div>
  );
}
